css:
@-moz-document domain("twitter.com") {

.stream-item:not(.no-header-background-module), .permalink, .stream-end-inner {
background: transparent;
border: none;
}

.account, .tweet, .app, .RtbAd-container {
background: #15202b;
border-radius: 16px;
padding: 16px;
margin-bottom: 4px;
margin-top: 4px;
border: none;
}

.ActivityItem {
background: #15202b;
border: none;
border-radius: 16px;
margin-bottom: 4px;
margin-top: 4px;
padding: 16px 16px 16px 36px;
}

.tweet:hover {
background: #15202b;
box-shadow: 0px 0px 0px 3px #15202b;
}

.ThreadedConversation-tweet::after {
top: 80px;
}

.DashboardProfileCard {
border: none;
border-radius: 16px;
text-align: center;
}

.DashboardProfileCard-avatarLink {
margin: -60px 0 0 0;
background-color: transparent;
}

.DashboardProfileCard-userFields {
position: relative;
top: 0;
left: 0;
width: 100%;
}

.ProfileHeading-content {
text-align: center;
border-radius: 16px;
border: none;
margin-bottom: 8px;
margin-top: 8px;
}

.top-timeline-tweetbox .tweet-user, .top-timeline-tweetbox .timeline-tweet-box {
border: none;
border-radius: 16px;
margin-bottom: 4px;
}

.top-timeline-tweetbox .timeline-tweet-box .t1-form {
margin-left: 0;
}

.top-timeline-tweet-box-user-image {
display: none;
}

.permalink .inline-reply-tweetbox, .permalink .inline-reply-tweetbox-container, .Trends, .module ul:first-child li:first-child .list-link, .module .flex-module:first-child {
border: none;
border-radius: 16px;
}

.NotificationsHeadingContent, .content-header, .content-no-header {
margin-bottom: 4px;
border: none;
border-radius: 16px;
}

.wtf-module .UserSmallListItem {
border-bottom: none;
border-radius: 16px;
}

.stream-end-item, .stream-end, .stream-loading, .stream-placeholder {
border: none;
}

.wtf-module .import-prompt {
display: none;
}

.Footer {
display: none;
}

}
