css:
@-moz-document regexp("https?://(www\\.)?coursera\\.org/.*") {
body {
  background-color: #333;
  color: #ddd;
  border-color: white !important;
}

body * {
  border-color: #444 !important;
}

#rendered-content div.rc-PageHeader,
#rendered-content div.rc-PageHeader nav,
.mobile-header-menu,
.theme-light,
.rc-PageList .bt3-panel-heading,
.rc-MobileHeader > div,
.c-phoenix-template-page,
.resource-link,
.mega-menu,
.rc-Home .course-content,
.c-open-single-page-attempt,
.rc-WeekSingleItemDisplay:hover,
.rc-CourseNavigation,
.rc-NavigationLink,
.theme-white {
  background-color: #343434 !important;
  color: #ddd !important;
}
.rc-Course [class*="card-"].selected,
.c-assess-theme-phoenix.coursera-assess-question-optionBase label:hover,
.rc-QuizAttemptHeader,
.rc-NavigationDrawerLink.selected,
.mega-menu .selected {
    background: #555 !important;
}

div.rc-InContextNotification div[class*="success"] {
    background: #030;
}
div.rc-CollapsibleMessage .message-content .white {
    background-image: linear-gradient(rgba(255, 255, 255, 0), #444);
}

.c-assess-theme-phoenix .c-points-box.attempt,
.color-hint-text {
    color: #ccc;
}


.explore-Button-bottom-border-mask {
    background: transparent;
}

.rc-MobileHeader svg {
  fill: rgb(255, 255, 255) !important;
}

.hover-color:hover,
.current-item {
  background-color: #555 !important;
}

span,
i,
.color-primary-text {
  color: #fff !important;
}

.cif-stack-2x {
  color: #2ab573 !important;
}
.rc-Phrase,
.c-membership-tabs-container li.active a {
  color: #ddd !important;
}

.c-membership-tabs-container li a:hover {
  color: #ddd !important;
}
.rc-UIModal,
.card-rich-interaction {
  background-color: #555 !important;
  color: #ddd !important;
}

.c-dashboard-header {
  color: #ddd !important;
}

div.video-container * {
   color: #ddd !important;
}

div.c-video-title * {
  color: #ddd !important;
}

.c-video-title {
  margin: 10px auto 0 auto;
  padding: 10 !important;
  font-weight: bold;
}

.video-container {
  padding: 5px 0 !important;
}

.rc-NavigationDrawerLink:hover,
.bgcolor-success-light,
.rc-Help .help-widget,
.video-container .vjs-overlay,
.video-container .coursera-assess-assessment-in-video-bar, 
.video-container .rc-VideoConceptsReviewOverlay,
.video-container .coursera-assess-assessment-in-video {
  background-color: #222 !important;
}

.vjs-overlay .coursera-assess-assessment-in-video {
  color: #000 !important;
  background-color: #ddd !important;
}

.vjs-overlay .coursera-assess-assessment-in-video * {
  color: #000 !important;
}

.vjs-overlay .coursera-assess-assessment-in-video  .coursera-assess-assessment-in-video-bar,
.vjs-overlay .coursera-assess-assessment-in-video  .coursera-assess-assessment-in-video-bar * {
  color: #fff !important;
}

.video-container .c-overlay-autonext-container {
	position: absolute;
  top: 100px;
  right: 0;
  height: 200px;
}

.video-container .c-overlay-autonext-content {
  position: absolute;
  top: 100px;
  right: 0;
  background-color: rgba(255, 255, 255, 0.5) !important;
  color: #fff !important;
}

a.dim,
a.dim:hover {
  color: rgba(82, 155, 251, 0.7);
  text-decoration: none;
}

a.dim:hover,
a.dim:hover:hover {
  color: rgba(82, 155, 251, 1);
  text-decoration: underline;
}
/* Video */
.c-video {
  max-width: inherit !important;
  max-height: inherit !important;
  width: calc(100% - 350px) !important;
  height: calc(100% - 85px) !important;
}

.c-video .vjs-control-bar,
.c-video .rc-VideoConceptsGoalMarkers,
.c-video .rc-VideoConceptsStudyCard {
  visibility: hidden;
  color: #fff !important;
/*   background: rgba(43, 51, 63, 0.3) !important; */
  transition: all 0.5s;
  opacity: 0;
}

.c-video:hover .vjs-control-bar,
.c-video:hover .rc-VideoConceptsGoalMarkers,
.c-video .rc-VideoConceptsStudyCard {
  visibility: visible;
  opacity: 1;
}
.rc-PageHeader .c-ph-nav,
.card-one-clicker,
.rc-TrackChooser select:not([multiple]),
.rc-NavSingleItemDisplay.highlighted,
.card-one-clicker:hover,
.rc-ItemBox .item-box-content,
.rc-NavSingleItemDisplay.highlighted, .rc-NavSingleItemDisplay:hover,
.c-video .rc-VideoConceptsStudyCard,
.c-video .rc-VideoConceptsStudyCard * {
  background-color: rgba(0, 0, 0, 0.5) !important;
}
.react-autosuggest__container .react-autosuggest__suggestions-container--open,
.rc-AuthenticatedAccountDropdown .c-authenticated-dropdown-menu-container .c-authenticated-dropdown-menu {
    background-color: #222;
}

video {
  position: relative !important;
/*   height: calc(100% - 150px) !important; */
/*   width: calc(100% - 50px) !important; */
  padding-bottom: 100px !important;
  line-height: 10px !important;
}

video::-webkit-media-text-track-container {
  position: relative !important;
  bottom: -90px !important;
}

video:-webkit-full-screen,
video:full-screen {
  padding-bottom: 50px !important;
  line-height: 15px !important;
}


video::-webkit-media-text-track-display {
/*   position: absolute !important; */
/*   height: 100px !important; */
/*   background: rgba(255, 255, 255, 0.2); */
/*   color: rgba(0, 0, 0, 0.5); */
  color: rgba(255, 255, 255, 1);
  text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.2);
  font-size: 24px !important;
  line-height: 30px !important;
}

}
