css:
@-moz-document domain('mongabay.com') {
	
 :root {
    --darker-color: #222222;
    --dark-color: #333333;
    /*--dark-color: rgba(51,51,51,.75);*/
    --light-color: #454545;
    --light-color-mid: #555;
    --light-color-code: #777;
    --lighter-color: #86a6ce;
    --alert-color: #435f82;
    --text-color: #bbb;
    --text-color-light: #ccc;
    --shade-color: rgba(30, 30, 30, 0.8);
 }
* {
    border-color: var(--darker-color) !important
}
img {
    opacity: .7 !important;
}
input, button {
    color: black;
    background: var( --light-color-code);
}
button, [class*="button"] {
    background: var(--light-color-code);
}
h1, h2, h3, h4, h5, a, button, [class*="button"] {
    color: var(--text-color) !important;
}
body {
    background: var(--dark-color);
    color: var(--text-color-light)
}

/* weird side slideout */
.spnf_newsfeed-module,
.spnf_guest-actions
{
    background: var(--dark-color);
    color: var(--text-color-light)
}
.spnf_title {
    color: var(--text-color-light) !important;
}
.spnf_newsfeed-item ,
.spnf_article
{
    background: var(--light-color);
    color: var(--text-color-light)
    
}

.sidebar,
#special-series,
footer,
.social,
/* bottom extra links */
.spotim-recirculation,
.sprc1YjLM,
/*comment stuff */
.spot-im-conversation-module,
.sppre_conversation-module
{
    display:none;
}

.col-lg-8
{
    /*
    -webkit-flex: 0 0 100%;
    -ms-flex: 0 0 100%;
    flex: 0 0 100%;
    max-width: 100%;
    */
}
}
