css:
@-moz-document url(https://www.omegle.com/) {
  body {
    background-color: #1d1d1d !important;
    color: #ffffff;
}
a:link {
    color: #efefef !important;
}
#header {
    background: none !important;
    border: 0px !important;
    box-shadow: 0 0em 0em #CCC;
    -moz-box-shadow: 0 0em 0em #CCC;
    -webkit-box-shadow: 0 0em 0em #CCC;
}
div#footer {
    display: none;
}
div#introtext a img {
    display: none;
}
#onlinecount {
    color: #cccccc !important;
    top: 25px !important;
}
.logwrapper {
    background: #2d2d2d !important;
    border: none !important;
}
div.logwrapper[style],
div.controlwrapper[style] {
    margin-right: 0px!important;
}
.statuslog {
    color: #FFF!important;
}
.questionHeading {
    background: #4D4D4D;
    color: #D4D4D4;
}
.questionText {
    background: #222;
}
.topictag {
    background: #000;
}
.topictageditor {
    background: #3d3d3d!important;
    border: 1px solid #505050!important;
}
img[src*="exclamationog.png"],
img[src*="exclamationog@2x.png"] {
	height: 0 !important;
    width: 0 !important;
    padding-left: 25px !important;
    padding-top: 25px !important;
    background: url(http://i.imgur.com/3pnbg5e.png) no-repeat !important;
	background-size: 100% !important;
}
img[src*="textbtn.png"], 
img[src*="textbtn@2x.png"] {
	height: 0 !important;
    width: 0 !important;
    padding-left: 124px !important;
    padding-top: 50px !important;
    background: url(http://i.imgur.com/7FV5emK.png) no-repeat !important;
	background-size: 100% !important;
}
img[src*="videobtn-enabled.png"],
img[src*="videobtn-enabled@2x.png"] {
	height: 0 !important;
    width: 0 !important;
    padding-left: 124px !important;
    padding-top: 50px !important;
    background: url(http://i.imgur.com/IObxrIp.png) no-repeat !important;
	background-size: 100% !important;
}
img[src*="videobtn-disabled.png"],
img[src*="videobtn-disabled@2x.png"] {
	height: 0 !important;
    width: 0 !important;
    padding-left: 124px !important;
    padding-top: 50px !important;
    background: url(http://i.imgur.com/2KY9hPB.png) no-repeat !important;
	background-size: 100% !important;
}
img[src*="newchatbtn.png"],
img[src*="newchatbtn@2x.png"] {
	height: 0 !important;
    width: 0 !important;
    padding-left: 170px !important;
    padding-top: 50px !important;
    background: url(http://i.imgur.com/jFTIxxw.png) no-repeat !important;
	background-size: 100% !important;
}
#tagline img {
    display: none !important;
}
#intro {
    border: 1px solid #000;
    background: rgb(20, 20, 20);
    left: 0px;
    -webkit-box-shadow: inset 0 0 .0em #000;
	-moz-box-shadow: inset 0 0 .0em #000;
	box-shadow: inset 0 0 .0em #000;
}
#startachat,
#intoheader {
    color: rgb(200, 200, 200);
}
#mobilesitenote {
    display: none;
}
#monitoringnotice {
    border: 1px solid #505050;
    box-shadow: 0 0 0em #000000;
    -webkit-box-shadow: 0 0 0em #000000;
    background: #3D3D3D!important;
}
#monitoringnotice h2 {
    background: #3D3D3D;
}
.youmsg .msgsource,
.strangermsg .msgsource {
    display: none;
}
.youmsg:before {
    content: "Y:";
    font-weight: bold;
}
.strangermsg:before {
    content: "S:";
    font-weight: bold;
}
.youmsg {
    color: #aaccff;
}
.disconnectbtn,
.sendbtn {
    background: #2d2d2d !important;
    color: #ccc !important;
}
.chatmsgwrapper,
textarea {
    background: #2d2d2d !important;
    color: #aaccff !important;
}
.disconnectbtnwrapper,
.sendbtnwrapper {
    border: none !important;
    background: #000000!important;
}
.chatmsgwrapper {
    border: none !important;
}
#onlinecount {
    font-size: 1em !important;
    color: #ccc !important;
}
#onlinecount strong {
    font-size: 1em !important;
    color: #ccc !important;
}
.topictagtext,
.topictagdelete {
    color: #fff !important;
}
[target="_blank"] span {
    color: #777 !important;
    background: #333 !important;
}
a#spymodebtn[style],
a#videobtnunmoderated[style],
a[style="display: block; margin-top: 0.5em; border: 1px solid rgb(204, 204, 204); color: black; text-align: center; text-decoration: none; padding: 0.25em; border-radius: 0.5em; position: relative; background: rgb(238, 238, 238);"] {
	background: #3d3d3d !important;
    border-color: #505050 !important;
}
#topterms {
	display: none;
}
.newtopicinput {
	color: #ffffff;
}
.conversationgreat {
    background: #00459C;
}
#onlinecount:before {
	font-size: 10px;
	color: #555;
}

a[style*="display: block; margin-top: 0.5em; border: 1px solid rgb(204, 204, 204); background: rgb(238, 238, 238); color: black; text-align: center; text-decoration: none; padding: 0.25em; border-radius: 0.5em; position: relative;"],
a[style*="display: block; margin-top: 0.5em; border: 1px solid rgb(204, 204, 204); background: rgb(238, 238, 238); color: black; text-align: left; text-decoration: none; padding: 0.25em 0.25em 0.25em 2em; border-radius: 0.5em; position: relative;"],
a[style^="display: block; margin-top: 0.5em; border: 1px solid rgb(204, 204, 204); background: rgb(238, 238, 238) none repeat scroll 0% 0%; color: black; text-align: center; text-decoration: none; padding: 0.25em; border-radius: 0.5em; position: relative;"],
a[style^="display: block; margin-top: 0.5em; border: 1px solid rgb(204, 204, 204); background: rgb(238, 238, 238) none repeat scroll 0% 0%; color: black; text-align: left; text-decoration: none; padding: 0.25em 0.25em 0.25em 2em; border-radius: 0.5em; position: relative;"] {
	display: none!important;
}
.chattypeorcell,
#chattypevideocell,
#videobtnstatus,
#monitoringnotice,
div .newchatbtnwrapper a,
#flashwrapper
{
	display: none;
}
div .newchatbtnwrapper {
	color: #2d2d2d;
}
}
