css:
@-moz-document domain("calendar.google.com") {
/* custom edits */

.gb_Hd.gb_de,
.gb_Ed.gb_be /* top bar color */
	{background-color: #2a2a2a !important;}


/* Colors */

:root {
    --GR-BLACK: #000;
    --GR-BLUE1: #515B62;
    --GR-BLUE2: #3B424A;
    --GR-BLUE3: #40474D;
    --GR-BLUE4: #30373D;
    --GR-BLUE5: #20272D;
    --GR-GRAY1: #F7F8F9;
	--GR-WHITE: #ddd;
}

/* General */

    body {
        background: var(--GR-BLUE3) !important;
    }

    /* Icons */
    .gb_yc svg,
    .gb_kc svg,
    .rF3YF,
    .DPvwYc {
        color: var(--GR-WHITE) !important;
	}

	/* Conferencing Icon in "Create Event page" needs overrides */
	.DPvwYc svg { fill: var(--GR-WHITE) !important}

		/* Black Icons */
		.kydeve .z80M1 .DPvwYc, /* Calendar Context Menu */
		.i5a7ie .rF3YF:not(.wmyjXb) .DPvwYc.NhjcAb, /* Event Popover Header Icons */
		.krHqHb .DPvwYc  /* Event Popover Edit Icon */
		{
            color: var(--GR-BLACK) !important
		}

/* Header */

.gb_8d,
.gb_9d {
    background: linear-gradient(to bottom, var(--GR-BLUE1), var(--GR-BLUE3)) !important;
}

	/* Overall Header */
	.gb_8d,
	.gb_9d,
    /* Calendar Range */
	.KaL5Wc,
	/* Calendar Type */
	.Cd9hpd .NlWrkb {
        color: var(--GR-WHITE) !important;
	}

/* Main Content */

body[data-viewfamily="EVENT"] .TOTjfb, body[data-viewfamily="SEARCH"] .TOTjfb {
    border-top: 0 !important;
}

/* Left Sidebar */
.hEtGGf { border-color: var(--GR-BLUE1) !important }

	/* Section Dividers */
	.T8CF3c, .qZvm2d-clz4Ic { border-color: var(--GR-BLUE1) !important }

	/* Mini-Calendar Month */
	.MSZkRb,
	/* Mini-Calendar Day of the Week */
	.zRt28b,
	/* Mini-Calendar Day Circle Text (Out of Range) */
	.uGtG7d,
	/* Mini-Calendar Day Circle Text (In range) */
	.iMprOe {
    	color: var(--GR-WHITE) !important;    
	}

	/* Mini-Calendar Rows */
	.W0m3G::before { background: transparent !important }

	/* Mini-Calendar Day Circle */
	.W0m3G .r4nke { background: var(--GR-BLUE5) !important }

	/* Mini-Calendar Day Circle (Selected) */
	.i8dSE .r4nke,
	.i8dSE + .i8dSE::before {
        background: var(--GR-BLUE1) !important;
	}

	/* Mini-Calendar Day Circle Text (Out of Range) */
	.uGtG7d { opacity: 0.5 !important }

	/* "Add a friend's calendar" Wrapper */
	.IIB0tc .ULpymb { background: transparent !important }

		/* "Add a friend's calendar" placeholder text */
		.ULpymb .snByac {
            color: var(--GR-WHITE) !important;
            opacity: 0.6 !important;
		}

		/* "Add a friend's calendar" Input */
		.whsOnd { color: var(--GR-WHITE) !important }

	/* Calendars Group Title */
	.uQ1ixe .snByac { color: var(--GR-WHITE) !important }
		
		.I2LGc:hover { background: var(--GR-BLUE4) !important }
	
	/* Calendar Name */
	.DYTqTd .NI2kfb { color: var(--GR-WHITE) !important }

	/* Calendar Checkbox (unchecked) */
	.uHMk6b { border-color: var(--GR-BLUE4) !important }

/* Calendar Body */
.tNDBE { background: var(--GR-BLUE5) !important }

	/* Schedule */

		/* Today's Date */
		.OmwDud.F262Ye { color: var(--GR-WHITE) !important }

		/* Section */
		.L1Ysrb {
            border-color: var(--GR-BLUE1) !important;
        	color: var(--GR-WHITE) !important;    
		}

		/* Current Event */
		.dtaVuc.UflSff { color: var(--GR-WHITE) !important }

	/* Day */

	.OCQPo { background: transparent !important }
	
		/* Time Column */
		.Gk2izd, .GENA3c {
            background: var(--GR-BLUE3) !important;
            border-color: var(--GR-BLUE1) !important;
		}

			/* Text */
			.pCoqfc, .MANBAf { color: var(--GR-WHITE) !important }

		/* Grid Borders */
		.H7IzGb,
		.SU7tYb,
		.yEkOpe,
		.G5v83e,
		.YvjgZe,
		.mmsF1c::after {
            border-color: var(--GR-BLUE1) !important
		}

		/* Calendar Name */
		.mdzkGf { color: var(--GR-WHITE) !important }

	/* Week */

	.v83gc { background: var(--GR-BLUE3) !important }

		/* Week Number Column */
		.akJTWe, .XnnJrc.Wyo4Qe { border-color: var(--GR-BLUE1) !important }
	
		/* Current Day Row */
		.SU7tYb.F262Ye,
		.YvjgZe.F262Ye::after,
		.yEkOpe.F262Ye {
            background: var(--GR-BLUE4) !important;
		}

		/* Day Title */
		.MmhHI.N4XV7d { color: var(--GR-WHITE) !important }

	/* Monthly */

		/* Grid Lines */
		.t8qpF, .PhLhOd, .QIadxc { border-color: var(--GR-BLUE1) !important }

		/* Multi-day event that wraps has a little white angle */
		.zWcBU { color: var(--GR-BLUE5) !important }

		/* Week Day Header */
		.wy3aMe { color: var(--GR-WHITE) !important }

		/* Day Number Header */
		.YK7obe {
            color: var(--GR-WHITE) !important;
            opacity: 0.8 !important;
		}

		/* Current Day */
		.ecKr0, .ef2wWc { background: var(--GR-BLUE3) !important }

		/* Time Event */
		.g3dbUc.smECzc, .g3dbUc.smECzc.UflSff { color: var(--GR-WHITE) !important }

		/* Time & Day Event (Past) */
		.g3dbUc.UflSff { opacity: 0.5 !important }

			/* Day Event (Past) Black Text */
			.g3dbUc.qqMC3e { color: var(--GR-BLACK) !important }
			/* Day Event (Past) White Text */
			.UflSff .g3dbUc { color: var(--GR-BLACK) !important }

		/* Selected Event */
		.g3dbUc.smECzc.KKjvXb,
		.GrxScd .g3dbUc.smECzc:focus {
            background: var(--GR-BLUE2) !important;
		}

/* Tasks Right Sidebar */
.lzc0vb { border-color: var(--GR-BLUE1) !important }

	/* Collapse Button */
	.BWsMTe {
        background: var(--GR-BLUE2) !important;
        border-color: var(--GR-BLUE1) !important;
	}

/* Event Popover */

.JtukPc { background: var(--GR-BLUE3) !important }

	/* Body & Footer Text */
	.NI2kfb, /* Exclude time dropdowns */
	.YWILgc {
        color: var(--GR-WHITE) !important;
	}

	/* Secondary Text */
	.JtukPc .DN1TJ {
        color: var(--GR-WHITE) !important;
        opacity: 0.75 !important
	}
	
	
/* Create Event Popover */

	/* Title Input */
	.ndJi5d, .shdZ7e .zHQkBf { color: var(--GR-WHITE) !important }
	
		/* Placeholder */
		.ndJi5d { opacity: 0.5 !important}

	/* Event/Task Button */
	.qmv2fc.kaAt2 {
    	color: var(--GR-WHITE) !important;
        border-color: var(--GR-BLUE1) !important;
	}

	/* Date Input */
	.BvQsje, .Kb2sf { background: transparent !important }

		.WpDZC, .DSC0Mc, .YKfFKe { color: var(--GR-WHITE) !important; }

	/* Calendar Date Dropdown Menus */
	.yqvK0e .gHQcAb { background: var(--GR-BLUE4) !important }

	/* All Day Checkbox */
	.ETkhPe { color: var(--GR-WHITE) !important; }

	/* Calendar Selector */
	.jZ0DTb { background: var(--GR-BLUE4) !important }

		.jgvuAb:not(.iWO5td) .jT5e9 { color: var(--GR-WHITE) !important }

	/* More Options */
	.nYqxP { color: var(--GR-WHITE) !important }

	/* Add Time Button */
	.JtukPc .CCRNT { background: var(--GR-BLUE1) !important }

	/* Time Dropdown */
	.JtukPc .gHQcAb {
        background: var(--GR-BLUE4) !important;
        color: var(--GR-WHITE) !important;
	}

	/* Appointment Slots > Minutes */
	.FXZz0b {
    	background: transparent !important;
        color: var(--GR-WHITE) !important;
	}
	

/* Create Event Page */

.p9lUpf { background: transparent !important }

	/* Header */
	#gb, .NkK3Fc.PTLZrb .gb_8d { background: transparent !important }

	/* Location & Time Dropdown */
	.VOEIyf .jBmls,
	.kMp0We .gHQcAb {
        background: var(--GR-BLUE4) !important;
	}

		/* Selected Row */
		.oKubKe[aria-selected="true"],
		.VKy0Ic[aria-selected="true"] {
            background: var(--GR-BLUE5) !important
		}

	/* "Add Title */
	.bojM1c { color: var(--GR-WHITE) !important; opacity: 0.5 !important; }

	/* [Read-only] Text */
	.j3nyw { color: var(--GR-WHITE) !important }

	/* Checkbox Labels */
	.ymbhmd .dRjtd {
        color: var(--GR-WHITE) !important;
	}

	/* Unselected Tab */
	.ThdJC { color: var(--GR-WHITE) !important }

	/* Calendar Selector */
	.FpmPAb { background: var(--GR-BLUE4) !important }

	/* Description Box */
	.lR3h6d { background: var(--GR-BLUE4) !important }

	/* Select Text */
	.GfgKp:not(.iWO5td) .mZytBb,
	.GfgKp:not(.iWO5td) .MocG8c { color: var(--GR-WHITE) !important }

	/* "Guests Can" */
	.hGcgEd { color: var(--GR-WHITE) !important }
	.VgMzz .DX3x9d { color: var(--GR-WHITE) !important }

	/* Guest List */
	.cHB8o.AL18ce { color: var(--GR-WHITE) !important }
	.DN1TJ { color: var(--GR-WHITE) !important; opacity: 0.5 !important; }

	/* "Add Video Call", "Add Notification" */
	.BTV3qc .snByac, .rhtYVe .snByac {
        color: var(--GR-WHITE) !important;
        opacity: 0.5 !important;
	}

	/* "Add Description" */
	.lR3h6d .snByac {
        color: var(--GR-WHITE) !important;
        opacity: 0.5 !important;
	}

		#CghhOe0 { color: var(--GR-WHITE) !important }

	/* Selected description Textarea in Read-only event */
	.lR3h6d.W1ZWq .w5Bz0c { background: transparent !important }

	/* "Today" button in "Find a time" */
	.RveJvd.snByac { color: var(--GR-WHITE) !important }

	/* Current date range in "Find a time" */
	.oPF3Ce { color: var(--GR-WHITE) !important }

	/* Rooms Search Field */
	.qTH1m { background: var(--GR-BLUE4) !important }

/* Custom Recurrence Dialog */
.g3VIld {
    background: var(--GR-BLUE1) !important;
    color: var(--GR-WHITE) !important;
}

.gb_ce, .gb_Dd.gb_ce  /* top bar */
    {background: var(--GR-BLUE4) !important;}

.NlL62b.EfQccc.elYzab-cXXICe-Hjleke.EiZ8Dd.jKgTF, .U26fgb.XHsn7e.XiVK6d
    {opacity: .77}

}
