@-moz-document domain("colorzilla.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

#main-nav li a
{
	text-transform: capitalize!important;
}

.css-output div.css-output-text,
.menu.desktop a,
.pane,
.stop-details .opacity-adjust-open-button,

:focus,
body #hd
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

.gradient-name .name-input,
.preview .output-options .dimension,
.stop-details .opacity-value,
.stop-details .position {
    text-align: center;
}

/* Fonts 
_____________________________________________________________________________________________________*/

.css-output div.css-output-text,
.gradient-name .name-input,

body 
{
	font-size: 1.5rem!important;
}

.browser-compatibility td .more-info,

button,
input,
select
{
	font-size: 1.2rem!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

.gradient-ad-panel,
.share-links
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Left/Right/Top/Bottom 
_____________________________________________________________________________________________________*/

.gradient-name {
    margin-top: 8rem;
}

.stop-details .color {
    margin-left: 6px;
}

.stop-details .opacity-value {
    margin-left: 23px;
}

#main-nav {
    right: 29px;
}

/* Margins 
_____________________________________________________________________________________________________*/

.css-output div.css-output-text
{
	margin: 1.2rem!important;
}

#editor-heading,
.gradient-control,
.info-panel
{
	margin: 12px auto!important;
}

.css-output div.css-output-text div {
    margin: 12px 0!important;
}

/* Paddings 
_____________________________________________________________________________________________________*/

#doc2,

.column.left,
.column.right,
.presets .presets-container
{
	padding: 1.2rem!important;
}

.browser-compatibility td .more-info .syntax,
.browser-compatibility td .more-info,
.stop-details .delete-button button,

button,
input,
select
{
	padding: 3px 6px!important;
}

/* Heights 
_____________________________________________________________________________________________________*/

.css-output div.css-output-text {
	max-height: none!important;
}

.browser-compatibility td .more-info,
.presets .presets-container {
	height: auto!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

body
{
	max-width: none!important;
}

.dialog-panel,
.permalink-panel
{
    width: 100%;
}

#editor-heading,

.gradient-control,
.info-panel
{
	width: 98%!important;
}

#doc2 {
	width: 96%!important;
}

.column.left,
.column.right
{
	width: 46%!important;
}

.preview .output-options .dimension {
    width: 3em;
}

}

@-moz-document domain("colorzilla.com") {
/* Font Settings */

xa[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

.gradient-adjustments-panel .adjustment-ops-panel a:hover,
.gradient-name .save-new-preset:hover,

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

.browser-compatibility td.not-supported div,
.browser-compatibility td.prefixed:not(.not-supported) .syntax,
.colorpicker .dialog-button,
.css-output div.css-output-text,
.gradient-adjustments-panel .adjustment-ops-panel a,
.gradient-name .save-new-preset,
.navigation-link,
.permalink-panel span,
.product h3 a, .product h3 a:active, .product h3 a:hover, .product h3 a:link, .product h3 a:visited,

li,
p,
tr td
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

.product h2 a, .product h2 a:active, .product h2 a:hover, .product h2 a:link, .product h2 a:visited,

h1,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

.dialog-panel .panel-label,

h3,
h4,
li strong,
p strong
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

.css-output .css-options,
.label,
.preview .output-options,
.stop-details .disabled,
.stop-details .position-details,
.stop-details .stop-type-label,

h5,
h6,
html,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

time
 {
	color: #07beff!important;
}

/* Green 
_____________________________________________________________________________________________________*/

selector
 {
	color: #07ff24!important;
}

/* Red 
_____________________________________________________________________________________________________*/

selector
 {
	color: #ff0707!important;
}

/*
_____________________________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

/* Misc 
_____________________________________________________________________________________________________*/

selector
{
	color: #fff!important;
}


}

@-moz-document domain("colorzilla.com") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

/* Background 
_____________________________________________________________________________________________________*/

.dialog-panel .panel-label
{
	background: #060505!important;
	background-color: #060505!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

.dialog_content,
.menu.desktop a.enabled:hover,
.pane,
.stop-details .opacity-adjust-open-button.disabled,

body #hd
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

.sidemenu .button
{	
	background: radial-gradient(circle, #0f79af 0%, #031017 100%);
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem!important;
	position: relative!important;
	z-index: 1!important;
}

.sidemenu .button::before
{
	background: radial-gradient(circle, #031017 0%, #0f79af 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.sidemenu .button:hover::before
 {
  opacity: 1!important;
}

/* Red 
_____________________________________________________________________________________________________*/

.dialog-button.cancel-button,
.stop-details .delete-button button
{	
	background: radial-gradient(circle, #380002 0%, #870101 100%);
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem!important;
	position: relative!important;
	z-index: 1!important;
}

.dialog-button.cancel-button::before,
.stop-details .delete-button button::before
{
	background: radial-gradient(circle, #870101 0%, #380002 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.dialog-button.cancel-button:hover::before,
.stop-details .delete-button button:hover::before {
  opacity: 1!important;
}

/* Green 
_____________________________________________________________________________________________________*/

.dialog-button.ok-button,
.gradient-name button
{	
	background: radial-gradient(circle, #066503 0%, #3de10b 100%);
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem!important;
	position: relative!important;
	z-index: 1!important;
}

.dialog-button.ok-button::before,
.gradient-name button::before
{
	background: radial-gradient(circle, #3de10b 0%, #066503 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.dialog-button.ok-button:hover::before,
.gradient-name button:hover::before {
  opacity: 1!important;
}

/* Plain Black 
_____________________________________________________________________________________________________*/

#doc2,
#editor-heading,

.gradient-control,
.info-panel,
.inner-sub-panel,
.menu.desktop,
.presets .presets-container
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

/* Plain 
_____________________________________________________________________________________________________*/

.browser-compatibility td div,
.column.right,
.column.left,
.dialog,

body .product
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

#doc2:hover,
#editor-heading:hover,

.browser-compatibility td div:hover,
.column.left:hover,
.column.right:hover,
.dialog:hover,
.gradient-control:hover,
.info-panel:hover,
.inner-sub-panel:hover,
.menu.desktop:hover,
.presets .presets-container:hover,

body .product:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* Red Gradient 
_____________________________________________________________________________________________________*/

.browser-compatibility td.empty,
.browser-compatibility td.not-supported
{
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
}

/* Blue Gradient 
_____________________________________________________________________________________________________*/

.browser-compatibility td.partial
{
	background: linear-gradient(to bottom, #7980f8, #000 100%)!important;
}

/* Green Gradient 
_____________________________________________________________________________________________________*/

.browser-compatibility td.prefixed
{
	background: linear-gradient(to bottom, #1b9f31  0%, #000 100%)!important;
}

/* Yellow Gradient 
_____________________________________________________________________________________________________*/

.browser-compatibility td
{
	background: linear-gradient(to bottom, #a6edb2 0%, #000 100%)!important;
}

/* Orange Gradient 
_____________________________________________________________________________________________________*/

.browser-compatibility td .more-info .syntax
{
	background: linear-gradient(to bottom, #da3a04 0%, #000 100%)!important;
}

/* Gray Gradient 
_____________________________________________________________________________________________________*/

.dialog_e,
.dialog_n,
.dialog_ne,
.dialog_nw,
.dialog_s,
.dialog_se,
.dialog_sw,
.dialog_w
{
	background: linear-gradient(to bottom, #424040 0%, #000 100%)!important;
}

/* Gradient Border 
_____________________________________________________________________________________________________*/

.css-output-text-container
{
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

/* Code 
_____________________________________________________________________________________________________*/

.css-output-text-container
{
	background: url(https://image.ibb.co/e8fWZk/red_carbon_pattern_by_mystica_264_d4ceu9m.png) #111!important;
	background-size: 100%!important;
}

}

@-moz-document domain("colorzilla.com") {
/* Image Settings */

.css-output div.css-output-text div,
.presets .preset-item-background,

body .product
 {
	filter: brightness(75%)!important;
}

/*
_____________________________________________________________________________________________________*/

.css-output div.css-output-text div:hover,
.presets .preset-item-background:hover,

body .product:hover
 {
	filter: brightness(100%)!important;
}

/*
_____________________________________________________________________________________________________*/

.gradient-background,
.presets .preset-item,
.presets .preset-item-background,

img
 {
	border-radius: 22px!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

/*
_____________________________________________________________________________________________________*/

.info-panel li,
p {
	filter: brightness(68%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}

.info-panel li:hover,
p:hover {
	filter: brightness(100%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}
}

@-moz-document domain("colorzilla.com") {
/* New Icons */

input[type="checkbox"],
input[type="radio"] {
    appearance: none!important;
}

/* Arrows _______________________________________________________________________*/

/* Up _________________*/

selector
{
	background: url(https://i.ibb.co/bvZmSFj/red-up.png) no-repeat!important;
	box-sizing: border-box;
}

/* Down _________________*/

selector
{
	background: url(https://i.ibb.co/z7MRv74/direction-arrow-down.png) no-repeat!important;
	box-sizing: border-box;
}

/* Left _________________*/

selector
{
	background: url(https://i.ibb.co/mRKFvH8/direction-arrow-left.png) no-repeat!important;
	box-sizing: border-box;
}

/* Right _________________*/

.stop-details .opacity-adjust-open-button
{
	background: url(https://i.ibb.co/LQDKzQT/direction-arrow-right.png) no-repeat!important;
	box-sizing: border-box;
}

.stop-details .opacity-adjust-open-button
{
    background-size: 35px!important;
    height: 36px!important;
    padding-left: 36px!important;
    width: 10px!important;
}

}

@-moz-document domain("colorzilla.com") {
/* Scrollbar Settings */

/* Width 
_____________________________________________________________________________________________________*/


 ::-webkit-scrollbar {

 	width: 1.2em!important;
 }

 /* Color of handle 
__________________________________________________________________________________________________________________________________*/


 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track 
_____________________________________________________________________________________________________*/



 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom 
_____________________________________________________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of the bottom corner of the scrollable element, where two scrollbar meet. 
_____________________________________________________________________________________________________*/

::-webkit-scrollbar-corner
 {
 	background: #530205!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

::-webkit-scrollbar-corner:hover
 {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

}
