css:
@-moz-document domain("youtube.com") {
ytd-searchbox[mode=legacy] #container.ytd-searchbox,
ytd-searchbox[mode=legacy-centered] #container.ytd-searchbox {
    border-radius: 10px 0 0 10px;
}
#search-icon-legacy.ytd-searchbox {
    border-radius: 0 10px 10px 0;
}
ytd-guide-entry-renderer {
    width: 96.6%;
    border-radius: 0 20px 20px 0;
}
#endpoint.yt-simple-endpoint.ytd-guide-entry-renderer:hover,
#endpoint.yt-simple-endpoint.ytd-guide-entry-renderer:focus,
#endpoint.yt-simple-endpoint.ytd-guide-entry-renderer:active {
    border-radius: 0 20px 20px 0;
}

#guide-inner-content.ytd-app {
    overflow-y: visible;
}
#guide-inner-content.ytd-app::-webkit-scrollbar-thumb {
    background-color: #202020;
    box-shadow: inset 0 0 5px #1c1c1c;
    border-radius: 5px !important;
}
}
