css:
@-moz-document url-prefix("https://app.zenhub.com") {
/* Basic Color Inversion */
.zhc-issue-cards,
.zhc-issue-card,
.zhc-issue-card__header,
.zhc-issue-card__issue-title,
.zhc-issue-card__repo-name,
.zhc-issue-card__issue-number,
.zhc-issue-card__epics__title,
.zhc-pipeline,
.zhc-topbar,
.zhc-board,
.zhc-topbar__menu-toggle,
.zhc-pipeline-header,
.zhc-search-bar__input,
.zhc-btn,
.zhc-btn--default.zhc-btn--is-active,
.zhc-selection-list__body,
.zhc-selection-list__header__text,
.zhc-search__input,
.zhc-selection-filter,
.zhc-sidebar__navigation,
.zhc-sidebar-profile,
.zhc-sidebar__footer,
.zhc-modal__header,
.zhc-modal__body,
.zhc-add-comment,
.zhc-edit-comment__preview, .zhc-edit-comment__textarea,
.zhc-issue,
.zh-repo__workspace,
.zh-repo__content,
.zhu-panel,
.zhc-chart-container__header-title,
.zhc-chart-container,
.zhc-chart-container__header,
.zhc-dropdown .zhc-selection-button,
.zhc-chart-legend__labels,
.zhc-chart-legend .zhc-dropdown .zhc-selection-button,
.zhc-item-table,
.zhc-item-table__column-title,
.zhc-edit-issue__title-input,
.zhc-repo-notifications__header,
.zhc-app-notification,
.zhc-repo-notifications__load-more,
.zhc-issues-list-item,
.zhc-report-tabs__nav,
.zhc-report-tabs__filters,
.zhc-report-tabs,
.zhc-invite-your-team__header,
.zhc-invitees-list__add-invitee input,
.zhc-invite-your-team__scrollable-section,
.zhc-invite-your-team {
  background-color: #212121;
  color: #c0c0c0;
}

.zhc-invite-your-team {
  border: 1px solid #c0c0c0;
}

.zhc-item-table__row .zhc-item-table__row--closed {
  background-color: #212121;
  color: #c0c0c0;
}

.zhc-btn--action {
  color: black;
}

.zhc-issue-description .zhc-markdown pre {
  background-color: black;
  color: white;
}

.zhc-discussion-events__icon {
  border: 1px solid #c0c0c0;
}

.zhc-dependency,
.zhc-comment,
.zhc-edit-comment__file-upload,
.zhc-issue-card__connected-pull-request,
.zhc-issues-list__header {
  background-color: #101010;
}

.hljs-number,
.hljs-literal {
  color: #09bebe;
}

.zhc-btn--action {
  background-color: #00a572;
  color: #fefcfc;
}

.zhc-btn-group .zhc-btn--action:hover,
.zhc-page__actions .zhc-btn:hover,
.zhc-edit-issue__actions__buttons .zhc-btn--action:hover {
  background-color: #0b6e4c;
}

/* For places we didn't want to change the background color */
.zhc-topbar__repo-name,
.zhc-sidebar-nav-item__label,
.zhc-sidebar-profile__name__text,
.zhc-sidebar-profile__company,
.zhc-issue-description__title,
.zhc-markdown-block--html,
.zhu-text-important,
.zhu-font-bold,
.zhc-comment__username,
.zhc-discussion-events__body a,
.zhc-sidebar-selector-button__heading,
.zhc-sidebar-epic-selector .zhc-sidebar-active-epics a,
.zhc-sidebar-active-pipeline__name,
.zhc-add-comment__username,
.zhc-tabs__nav-item,
.zhc-edit-comment__file-upload,
.zhc-milestone-panel__title,
.zhc-milestone-panel__start-date,
.zhc-milestone-panel__end-date,
.zhc-milestone-panel__duration,
.zhc-issue-meta__username,
.zhc-tooltip-wrapper,
.zhc-app-notification__title,
.zhc-issues-list-item a,
.zhc-issues-list-item__issue_number,
.zhc-report-tabs__nav__item {
  color: #c0c0c0;
}

/* inverting text shaddow to not look ugly */
.zhc-selection-list__header__text {
  text-shadow: #212121 0 1px 0;
}

/* back tick blocks */
.zhc-markdown-block code {
  background-color: #2f2f2f;
  border: 1px solid #656565;
  color: #e2e2e2;
}

/* Lightening blue links to be easier to see on dark background */
.zhc-remove-filter-item__text,
.zhc-click-text-item,
.zhc-selection-button__title,
.zhc-collapsible-control,
.zhc-issue-modal__header__issue-title a,
.zhc-markdown-block--html a,
.zhc-repo-notifications__load-more a,
.zhc-badge--estimate-closed,
.zhc-badge__value,
.zhc-invite-your-team-item--toggle-text {
  color: #8e90e5;
}

/* selected blue link */
.zhc-selection-button--active-filter .zhc-selection-button__title {
  color: #b8b9df;
}

/* selected items */
.zhc-btn:hover,
.zhc-selection-item--is-active.zhc-selection-item,
.zhc-selection-item:hover,
.zhc-selection-item.zhc-selection-item--is-active,
.zhc-selection-list__list-container--items-underlined .zhc-selection-item--is-active, .zhc-selection-list__list-container--items-underlined .zhc-selection-item:hover,
.zhc-filter-expand-button,
.zhc-topbar__menu:hover,
.zhc-sidebar-nav-item--is-active,
.zhc-sidebar-nav-item:hover,
.zhc-sidebar-profile__toggle:hover,
.zhc-sidebar-nav-item__action:hover,
.zhc-tabs--lined-bottom .zhc-tabs__nav-item--active, .zhc-tabs--lined-bottom .zhc-tabs__nav-item:hover,
.zhc-app-notification:hover,
.zhc-item-table__row--closed {
  background-color: #454545;
  color: #eaeaea;
}

/* selected items with bottom blue bar */
.zhc-report-tabs__nav .zhc-report-tabs__nav__item--active {
  color: #c0c0c0;
  border-bottom: 5px solid #5e60ba;
}

.zhc-report-tabs__nav .zhc-report-tabs__nav__item:hover {
  color: #eaeaea;
}

.zhc-comment__timestamp,
.zhc-comment__timestamp a,
.zhc-release-report__date {
  color: #a1a1a1;
}

/* scrollbar inversion */
::-webkit-scrollbar {
  /* i really want this to be global */
  max-width: 10px !important;
  max-height: 10px !important;
  background: #1d1d1d !important;
}
::-webkit-scrollbar-track, ::-webkit-scrollbar-corner {
  /* i really want this to be global */
  background: #1d1d1d !important;
}
::-webkit-scrollbar-thumb {
  /* i really want this to be global */
  background: rgba(175, 175, 175, .5) !important;
}
::-webkit-scrollbar-thumb:hover {
  /* i really want this to be global */
  background: rgba(65, 131, 196, .8) !important;
}

