css:
@-moz-document domain(www.kicktipp.com), domain(www.kicktipp.de), domain(www.kicktipp.co.uk), domain(www.kicktipp.es), domain(www.kicktipp.fr), domain(www.kicktipp.it), domain(www.kicktipp.pl), domain(www.kicktipp.pt), domain(www.kicktipp.nl), domain(www.kicktipp.ch), domain(www.kicktipp.at), domain(www.kicktipp.ru) {
	

	



#adtag_banner {

    position: relative;
    height: 0px;

}

#wrapper {

    background-color: #222;
    height: auto;

}


body {
background: #222;	
color: #fff;
	padding: 0;
	margin: 0;
	font-size: 100%;
	font-family: Arial, sans-serif;
	font-weight: normal;
	font-style: normal;
	line-height: 1.3;
	position: relative;
	cursor: auto;
	width: auto;
	min-width: 320px;
	max-width: 1024px;
	font-size-adjust: none;
	-webkit-text-size-adjust: none;
	-webkit-background-size: 0.438em 100%;
    margin-left: auto;
margin-right: auto;
}


nav {

    display: none;
    position: absolute;
    width: 0;
    background-color: #333;
    border-right: 1px solid #ddd;
    z-index: 1000002;
    overflow: auto;
    margin-bottom: 100px;
    height: auto;

}

div.messagebox.dismiss {

    padding: 10px 50px 10px 10px;
    background-color: #c8aa00;
    cursor: pointer;
    position: relative;
    font-size: 90%;

}

nav a {

    display: block;
    width: auto;
    text-decoration: none;
    padding: 0.8em 0.2em 0.7em 0.4em;
    text-overflow: ellipsis;
    overflow: hidden;
    white-space: nowrap;
    word-wrap: break-word;
    color: #fff;

}


nav .level0 {

    border-bottom: 1px solid #7d7d7d;
    font-size: 100%;
    margin: 0;
    padding: 0;

}

nav#navigation .level0 a {

    border-left: 5px solid #fff;
background: #595959;
}



body.notouch #kicktipp-content a:hover, body.notouch #footer a:hover, body.notouch .hover:hover, body.notouch nav a:hover, body.notouch tr.clickable:hover td, body.notouch th.link:hover span {

    cursor: pointer;
    background-color: #1f1f1f;

}


.pagetitle {

    margin: 0 0 15px 0;
    background-color: #353535;
    font-weight: bold;
    border-bottom: 1px solid #ddd;
    padding: 0.8em 0.6em 0.7em 0.6em;

}


nav .level1 {

    border-bottom: 1px solid #949494;
    margin: 0;

}


.ktable > tbody > tr.odd > td {

    background-color: #8d8d8d;

}

a {

    color: #fff;

}

.ktable td {

    background-color: #373737;
    border-top: 1px solid #ddd;

}


th {

    text-align: left;
    vertical-align: bottom;
    font-weight: normal;
    padding: 6px 5px 6px 5px;
    background-color: #5c5c5c;
    border-bottom: 1px solid #ddd;

}


.startbox {

    width: 300px;
    margin: 0;
    float: right;
    padding-left: 2em;
    padding-right: 0px;
    background-color: #222;

}


h2 {

    margin: 0;
        margin-top: 0px;
    background-color: #777;
    font-weight: bold;
    padding: 0.7em 0.6em 0.6em 0.6em;
    text-align: center;
    clear: both;

}


.tab a {

    display: block;
    text-decoration: none;
    padding: 0.6em;
    overflow: hidden;
    text-overflow: ellipsis;
    outline: 0;
    height: 40px;
    font-size: 12pt;
    background: #565656;

}


.ktable > tbody > tr.footer.odd > td {

    background-color: #575757;

}


tr.footer td {

    background-color: #1c1c1c;
    font-weight: bold;

}


nav .level0 a {

    background-color: #232323;
    font-weight: bold;

}


tr.treffer td, .ktable > tbody > tr.treffer > td, .ktable > tbody > tr.odd.treffer > td {

    background-color: #9a8e46;
    color: #fff;

}

}
