@-moz-document domain("github.com") {
 ::-webkit-scrollbar {
 	/* Width */
 	width: 1.2em!important;
 }

 /* Color of handle ___________________________________________________________________________*/

 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track ______________________________________________________________________________*/

 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom ___________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of the bottom corner of the scrollable element, where two scrollbar meet. ______________*/

::-webkit-scrollbar-corner
 {
 	background: #530205!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

::-webkit-scrollbar-corner:hover
 {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

}

@-moz-document domain("github.com") {
/*_____________________________________________________________________________________*/
*
{
	text-decoration: none!important;
}

.Box-header,
.bg-white,
.blankslate,
.border,
.border-gray-light,
.code-list .file-box,
.commit-tease,
.details-truncate-file-list.Details--on .file-wrap,
.file,
.file-wrap,
.link-mktg,
.pagination a,
.pagination em,
.pagination span,
.reponav-item,
.subnav-item,
.summarylink-btn,
.summarylink:active .summarylink-btn,
.summarylink:hover .summarylink-btn,
.table-list-header,
.timeline-comment,
.timeline-comment--caret:before,
.timeline-comment-header,
table.files td
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
}

.code-list em
{
	border-radius: 6px!important;
}

.flex-nowrap
{
	flex-wrap: wrap!important;
}

table {
    border-spacing: 0;
    border-collapse: separate;
}

/*___ Fonts __________________________________________________________________________________*/

.blob-code-inner,
.f5,
.markdown-body
{
	font-size: 19px!important;
}

body
{
	font-size: 17px!important;
}

.f6
{
	font-size: 15px!important;
}

.blob-code {
    line-height: 28px;
}

/*___ Remove Items ___________________________________________________________________________*/

.menu-item.selected:before
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Margins _______________________________________________________________________________*/

.Story,
.container-xl,
.p-responsive,
.team-left-column
{
	margin: 12px!important;
}

.py-4,
.repo-list-item
{
	margin: 12px 0!important;
}

/*___ Paddings ______________________________________________________________________________*/

.f6,
.Box,
.diff-table td,
.p-responsive,
.py-4,
.repo-list-item,
.team-left-column
{
	padding: 12px!important;
}

.btn-link,
.code-list em,
code
{
	padding: 5px 6px!important;
}

/*___ Widths ________________________________________________________________________________*/

.container-xl,
.container-lg,
.Story--large
{
	max-width: none!important;
}

.container,
.width-full
{
	width: 98%!important;
}

.col-lg-6
{
	width: 59%!important;
}

.col-sm-4
{
	width: 31.33333%!important;
}

.col-sm-3
{
	width: 32%!important;
}

.col-md-3
{
	width: 22%!important;
}
}

@-moz-document domain("github.com") {
a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/*________________________________________________________________________________________________________*/

.text-emphasized,
a[href]:hover span,
a[href]:hover,
h1,
input[type="checkbox"]:checked + label,
input[type="radio"]:checked + label,
span strong
{
	color: #ff0!important;
	text-shadow: 1px 1px #000!important;
}

/*________________________________________________________________________________________________________*/

.Link--primary,
.link-gray-dark,
.blob-code-inner,
.blob-num,
.btn,
.btn-link,
.discussion-sidebar-item,
.file-info,
.gh-header-meta,
.link-gray,
.reponav-item .Counter,
.team-mention,
.text-gray,
.text-gray-dark,
.text-gray-light,
.user-mention,
[style="background-color: rgb(255, 255, 255); padding: 10px 16px; border: 1px solid rgb(211, 211, 211); box-shadow: rgba(0, 0, 0, 0.2) 0px 2px 4px 0px; border-radius: 8px; position: fixed; top: 20px; right: 20px; z-index: 999; text-align: center; max-width: 300px; color: rgb(23, 23, 23); line-height: 1.4;"],
body,
li,
p,
pre
{
	color: #fff!important;
	text-shadow: 1px 1px #000!important;
}

/*__ Khaki _______________________________________________________________________________________________*/

.discussion-sidebar-heading,
.h0-mktg,
.h00-mktg,
.h000-mktg,
.h1-mktg,
.h2-mktg,
.h3-mktg,
.h4-mktg,
.h5-mktg,
.h6-mktg,
h1,
h2
{
	color: #F0E890!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*__ Burlywood ___________________________________________________________________________________________*/
.breadcrumb strong.final-path,
.lead-mktg,
.text-mono,
em,
h3,
h4
{
	color: #e0b880!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*__ Sandy Brown _________________________________________________________________________________________*/
.Label--outline,
h5,
h6,
label
{
	color: #F0A060!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Blue ______________________________________________________________________________________*/

relative-time,
time-ago
{
	color: #07beff!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Red _______________________________________________________________________________________*/
.link-gray:hover
{
	color: #f00!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*_______________________________________________________________________________________________*/
input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #fff!important;
}

/*________________________________________________________________________________________________________*/
::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}


::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

code,
.pl-k
{
	color: #ffcb9d!important;
}

.pl-e,
.pl-en
{
	color: #b58dff!important;
}

.pl-c1, .pl-s .pl-v {
    color: #7cb9ff;
}

.pl-c {
    color: #9aa1a7;
}

.pl-pds, .pl-s, .pl-s .pl-pse .pl-s1, .pl-sr, .pl-sr .pl-cce, .pl-sr .pl-sra, .pl-sr .pl-sre {
    color: #eff680;
}

table.files td.icon {
    color: rgba(208, 224, 242, 0.55);
}

table.files td.icon .octicon-file-directory {
    color: rgba(246, 235, 152, 0.99);
}

.pl-s .pl-s1, .pl-smi {
    color: #b5f27e!important;
}















}

@-moz-document domain("github.com") {

.code-list em
{
	background-color: rgba(255, 255, 231, 0.07)!important;
}

/*___ Background Gray __________________________________________________________________________*/

.Header-old,
.bg-gray-dark,
.gh-header .gh-header-sticky.is-stuck+.gh-header-shadow,
.repohead.experiment-repo-nav,
.site-subheader-sticky.is-stuck
{
	background-color: #060505!important;
}

/*___ Background 0000 __________________________________________________________________________*/

.full-commit .commit-meta,
.full-commit,
.jump-to-suggestions-results-container .navigation-focus .jump-to-suggestions-path,
.jump-to-suggestions-results-container [aria-selected=true] .jump-to-suggestions-path,
html,
table.files tr.navigation-focus td,
table.files tr[aria-selected=true] td
{
	background: #000!important;
}

/*___ Background-color 0000 ______________________________________________________________________*/

.Box-header,
.Header .header-search-wrapper,.Header,
.MarketplaceBackground-buffer,
.TimelineItem:before,
.bg-blue-7,.bg-gray,.bg-white,
.blankslate,
.blob-code-addition .x,.blob-code-addition,.blob-code-deletion,.blob-code-hunk,.blob-num-addition,.blob-num-deletion,.blob-num-expandable, .blob-num-hunk,
.border-bottom,
.commit-tease,.commit-tease-contributors,
.discussion-timeline-actions,
.file-header,
.gh-header,
.header-search-wrapper,
.menu,
.pagination .disabled,.pagination .disabled:hover,
.pagination .gap,.pagination .gap:hover,
.pagination a,.pagination a:focus,.pagination a:hover,
.pagination em,.pagination em:focus,.pagination em:hover,
.pagination span,.pagination span:focus,.pagination span:hover,
.readme .markdown-body,.readme .plain,
.social-count,
.subnav-item:focus,.subnav-item:hover,
.table-list-header,
.timeline-comment,.timeline-comment-header,
.topic-tag,
body,
table.files
{
	background-color: #0000!important;
}

/*___ Highlight Background ________________________________________________________________________*/

.Header:hover,
.signup-prompt-bg,
.site-subheader-sticky.is-stuck
{
	background-image: none!important;
}

/*______________________________________________________________________________________________*/

.highlight
{
	background: #111!important;
	background: url(https://image.ibb.co/msdy5U/Red_Fabric.jpg) #111!important;
}

/*__ Blue ________________________________________________________________________________________*/

.btn-mktg,
.btn,
button
{
	background-image: linear-gradient(to bottom right, #043dda, #000)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

.btn-mktg:hover,
.btn:hover,
button:hover
{
	background-image: linear-gradient(to bottom right, #154eeb, #000)!important;
	color: #ff0!important;
}

.btn-mktg:active,
.btn:active,
button:active
{
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*__ Green _________________________________________________________________________________________*/

.btn-primary
{
	color: #fff!important;
	border-radius: 22px!important;
	border: none!important;
	background-image: linear-gradient(to bottom right, #81BC2E, #000)!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

.btn-primary:hover
{
	background-image: linear-gradient(to bottom right, #92cd3f, #000)!important;
	color: #ff0!important;
}

.btn-primary:active
{
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*__ Plain Black ______________________________________________________________________________________*/

.Box,
.bg-gray-light,
.menu,
.py-4,
.repo-list-item
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

.Box:hover,
.bg-gray-light:hover,
.menu:hover,
.py-4:hover,
.repo-list-item:hover
{
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*__ Plain ___________________________________________________________________________________________*/

.Header-old .dropdown-menu,
.Story,
.auth-form-body,
.container-xl.d-lg-flex.flex-items-center.p-responsive,
.f6,
.float-left,
.team-left-column,
[style="background-color: rgb(255, 255, 255); padding: 10px 16px; border: 1px solid rgb(211, 211, 211); box-shadow: rgba(0, 0, 0, 0.2) 0px 2px 4px 0px; border-radius: 8px; position: fixed; top: 20px; right: 20px; z-index: 999; text-align: center; max-width: 300px; color: rgb(23, 23, 23); line-height: 1.4;"]
{
	background-color: #1c1c1c!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

.Header-old .dropdown-menu:hover,
.Story:hover,
.auth-form-body:hover,
.container-xl.d-lg-flex.flex-items-center.p-responsive:hover,
.f6:hover,
.float-left:hover,
.team-left-column:hover,
[style="background-color: rgb(255, 255, 255); padding: 10px 16px; border: 1px solid rgb(211, 211, 211); box-shadow: rgba(0, 0, 0, 0.2) 0px 2px 4px 0px; border-radius: 8px; position: fixed; top: 20px; right: 20px; z-index: 999; text-align: center; max-width: 300px; color: rgb(23, 23, 23); line-height: 1.4;"]:hover
{
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*__ Noise ___________________________________________________________________________________________*/

.Header,
.bg-gray-light,
.site-subheader-sticky.is-stuck
{
	background-image: linear-gradient(to bottom, rgba(0, 0, 0, .1), rgba(0, 0, 0, 0)), url(https://image.ibb.co/e39YEk/noise2.jpg)!important;
	background-repeat: repeat-x, repeat!important;
	background-size: 100px 15px, 140px 140px!important;
}

.Header:hover,
.bg-gray-light:hover,
.site-subheader-sticky.is-stuck
{
	background-image: linear-gradient(to bottom, rgba(255, 255, 255, .1), rgba(0, 0, 0, 0)), url(https://image.ibb.co/e39YEk/noise2.jpg)!important;
}

/*__ Red Gradient ______________________________________________________________________________________*/

.code-list .divider .blob-code,.code-list .divider .blob-num,
.filter-item:hover,
.menu-item:hover,
pre
{
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

/*__ Blue Gradient ______________________________________________________________________________________*/

.flash-warn,
.intro-shelf,
.signup-prompt
{
	background: linear-gradient(to bottom, #03193c, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

/*__ Green Gradient _____________________________________________________________________________________*/

.menu-item.selected,
.reponav-item.selected,
.subnav-item.selected,.subnav-item.selected:focus,.subnav-item.selected:hover
{
	background: linear-gradient(to bottom, #15534d 0%, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	text-shadow: 1px 1px #555!important;
}


/*__________________________________________________________________________________*/

p code
{
	background: linear-gradient(to bottom, #ba0101 0%, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	text-shadow: 1px 1px #555!important;
}


/*___ Highlight Background _________________________________________________________*/

.Box-row--focus-gray.navigation-focus,
.is-hovered,
table.files tr.navigation-focus td,
table.files tr[aria-selected="true"]
{
	background: linear-gradient(to right, #383838, #0000)!important;
	border-left-color: #434343!important;
}










}

@-moz-document domain("github.com") {
/*___________________________________________________________________________________________*/

.Box-row--focus-gray,
.Story,
.apps-cluster .CircleBadge,
.col-md-6,
.py-4,
.py-md-3,
.repo-list-item,
.summarylink-btn
{
	filter: brightness(75%)!important;
}

/*___________________________________________________________________________________________*/

.Box-row--focus-gray.navigation-focus,
.Story:hover,
.apps-cluster .CircleBadge:hover,
.col-md-6:hover,
.py-4:hover,
.py-md-3:hover,
.repo-list-item:hover,
.summarylink-btn:hover
{
	filter: brightness(100%)!important;
}

/*___________________________________________________________________________________________*/
img
{
	border-radius: 22px!important;
}

/*___________________________________________________________________________________________*/

img
{
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

/*___________________________________________________________________________________________*/

li,
p,
tr
{
	filter: brightness(68%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}

li:hover,
p:hover,
tr:hover
{
	filter: brightness(100%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}







}

@-moz-document domain("github.com") {

}
