css:
@-moz-document url("https://www.w3schools.com/"), url-prefix("https://www.w3schools.com/angular/"), regexp(".+w3schools.+\\.asp") {
html, #main, .w3-sidebar, .w3-light-grey,
.w3-container, .top, .w3-white
{
background-color: #222222 !important;
}
.w3-example, th,
.nextprev a:link, .nextprev a:visited
{
background-color: #444444 !important;
}

.w3-code
{
border-left:4px #5D707F solid !important;
}
.nextprev a
{
border:1px #444444 solid !important;
}
hr, table, tr, td, th
{
border-color:#B5C7DD !important;
}
h1, h2, h3, h4, h5, .w3-example span, a:hover, 
.dotcom, .w3-code, .w3-code span, th
{
color: #B5C7DD  !important;
}
a, p, li, td,
.w3-xlarge, .w3-text-dark-grey
{
color: #DDDDDD !important;
}

.w3-code, #leftmenuinnerinner a:hover, td {
    background-color: #1F2428 !important;
}
.w3-info 
    {background-color: #1F2428;
    border-left: 6px solid #5D707F;}

.w3-panel.w3-note 
	{background-color: #282320;
     border-left: 6px solid #E5A16E;}

.w3-codespan {
    color: #E5A16E;
    background-color: #333;
    padding-left: 4px;
    padding-right: 4px;
    font-size: 110%;
}


.w3-btn, .w3-btn:link, .w3-btn:visited, #sidenav a.active, .topnav .w3-bar a.active
	
    {color: #B5C7DD;
    background-color: #5D707F;}

mark	{opacity: .88}


/* scrollbar */

/* width */
::-webkit-scrollbar
    {width: 10px;}

/* Track */
::-webkit-scrollbar-track
    {background: #2e2e2e;}
 
/* Handle/bar */
::-webkit-scrollbar-thumb 
	{background: #555;}

/* Handle on hover */
::-webkit-scrollbar-thumb:hover 
	{background: #888;}
}

@-moz-document url-prefix("chrome-extension://"), regexp(".+w3schools.+\\.asp\\?filename=.*") {
html, #textareaCode
	{background-color: #efefef;
     color: #222;}

body #tryitLeaderboard
	{background-color: #222 !important;
 	 margin:0 !important;}

.w3-bar.w3-light-grey .CodeMirror.cm-s-default.CodeMirror-wrap

.w3-bar.w3-light-grey, .CodeMirror.cm-s-default.CodeMirror-wrap, div.w3bar.w3-light-grey, #textareacontainer#textarea, #textareaCode
	{-webkit-filter:invert(100%) hue-rotate(180deg) contrast(70%) !important;
     font-size:1.18em !important;} 

.line-content
{background-color: #fefefe;}

.w3-light-grey, .w3-hover-light-grey:hover, .w3-light-gray, .w3-hover-light-gray:hover, #container 
    {color: #ddd;
     background-color: #444!important;
     border-top: 1px solid #444 !important;overflow:auto;}

    @media screen and (min-width: 727px)
{                                            
#tryitLeaderboard {
    height: 110px;}
}

.w3-green, .w3-hover-green:hover 
	{ background-color: #5D707F!important;}

span#framesize
	{color: #d7d7d7;}

.w3-hover-white:hover, .w3-green:hover
    {color: #111 !important;
     background-color: #E5A16E !important;}

.w3-bar.w3-bar-item:hover
    {color: inherit !important;}
}

@-moz-document regexp(".+w3schools.+showphp\\.asp\\?filename=.*") {
iframe#iframeResult
	{background-color: #efefef;}

#framesize
	{color: #ccc;}
}
