css:
@-moz-document regexp("(.*wikipedia.org/wiki/(?!.*:)(?!Main_Page).*)|(.*secure.wikimedia.org/wikipedia/../wiki/(?!.*:)(?!Main_Page).*)") {
body {
    background-color: #f9f9f9 !important;
    font-family: -apple-system, ".SFNSText-Regular", "San Francisco", "Roboto", "Segoe UI", "Helvetica Neue", "Lucida Grande", sans-serif !important;
    font-size: 18px !important;
    line-height: 1.5rem;
}



/* Headings */
#firstHeading {
    text-align: center;
    width: 600px !important;
    hyphens: auto;
    border: 0;
    margin-top: 1.5rem !important;
}

h1,
h2,
h3,
h4 {
    font-family: inherit !important;
    border: 0;
    font-weight: normal !important;
    margin-top: 0 !important;
    margin-bottom: 1.5rem !important;
    font-style: normal !important;
}
h2 {
    margin-top: 3rem !important;
}

h2 + h3 {
    margin-top: 0;
}



/* Main Content */
.mw-body {
    background-color: inherit !important;
    margin: 0 auto !important;
    width: 600px !important;
    border: 0 !important;
}
.mw-body-content {
    width: 600px !important;
    text-align: justify;
    hyphens: auto;
    position: static;
    font-size: inherit !important;
}
.mw-body-content p {
    margin-bottom: 1.5rem;
}

#contentSub {
    margin-left: 0;
}
#siteSub {
    display: none;
}
.hatnote {
    padding-left: 0 !important;
}



/* Boxes */
.toc {
    margin-bottom: 1.5rem;
}
.infobox,
.vertical-navbox {
    width: 22rem !important;
    margin: 0 -24rem 1rem 0 !important;
}




/* Figures */
.tright,.tleft {
    margin: 0 0 1rem 0 !important;
}
.thumb.tleft {
    margin-left: -16rem !important;
}
.thumb.tright {
    margin-right: -16rem !important;
}
.thumbinner {
    border: 0 !important;
    margin: 0;
    padding: 0 !important;
    width: 14rem !important;
}



/* Lists */
.mw-content-ltr ol,
.mw-content-rtl .mw-content-ltr ol,
ol,
.mw-content-rtl .mw-content-ltr ul,
ul {
    margin: inherit !important;
}



/* hide things */
#p-personal,
#left-navigation,
#mw-head-base,
#mw-page-base,
#p-views,
#footer {
    display: none !important;
}



/* Header and Searchbar */
#mw-head {
    height: 40px;
    border-top: 5px solid #0645ad;
}
#simpleSearch {
    margin-top: 0 !important;
}
.imeselector {
    display: none !important;
}
#p-search {
    margin-right: 1rem;
}



/* Main Navigation */
#mw-panel .portal {
    opacity: 0;
}
#mw-panel:hover .portal {
    opacity: 1;
}
#mw-panel .mw-wiki-logo {
    opacity: 1;
    width: 9rem !important;
}
#right-navigation {
    margin: 0 auto !important;
}
#mw-panel h3 {
    margin-bottom: 0 !important;
    margin-left: 0 !important;
}
#mw-panel .body {
    margin-left: 0 !important;
}

}
