css:
@-moz-document domain("newstudio.tv") {

body {
    color: #eee !important;
    background: #333 url('https://i.imgur.com/Mph2Kgz.png') repeat top left fixed !important;
}

.navbar-inverse .navbar-inner {
    background-color: #444 !important;
    background-image: linear-gradient(to bottom, #444, #333) !important;
    background-repeat: repeat-x !important;
    border-top: 1px solid #555 !important;
    border-left: 1px solid #484848 !important;
    border-right: 1px solid #484848 !important;
    border-bottom: 0px solid #555 !important;
    border-top-left-radius: 6px !important;
    border-top-right-radius: 6px !important;
    box-shadow: 0px 0px 3px 0px rgba(0,0,0,0.75) !important;
}

.navbar-inverse .navbar-search .search-query:focus,
.navbar-inverse .navbar-search .search-query.focused {
  color: #eee !important;
  text-shadow: 0 1px 0 rgba(0, 0, 0, .4) !important;
  background-color: #303030 !important;
  border: 1px solid #505050 !important;
  box-shadow: inset 0 0 5px 0px rgba(0, 0, 0, 0.45);
}

div.container-fluid.bg-img {
    box-shadow: 0px 0px 3px 0px rgba(0,0,0,0.75) !important;  
    border-left: 1px solid  #444 !important;
    border-right: 1px solid  #444 !important;
    border-top: 1px solid  #444 !important;
    padding-right: 5px !important;
    
}


.bg-img:before {
    position: absolute  !important;
    display: block !important;
    content: "" !important;
    line-height: 0 !important;
    background: #222 url('https://i.imgur.com/JAxD6SB.png') repeat top left fixed !important;
    border-radius: 6px !important;
    margin-top: -43px !important;
    width: 100% !important;
    height: 100% !important;
    overflow: hidden !important;
    z-index: -1 !important;
    
}


.bg-img {
    background: rgba(30,30,30,0.1) url('https://imgur.com/7hiUIoo.png') !important;
}

#sideRightW {
    margin-top: 5px  !important;
}

#sideRight .accordion-heading a:hover {
  background: rgba(10,10,10,0.1) url('https://imgur.com/7hiUIoo.png') !important; 
    border-left: 1px solid #292929 !important; 
}

#sideRight .accordion-heading {
  background: rgba(30,30,30,0.1) url('https://imgur.com/7hiUIoo.png') !important;
  /*background: transparent !important; */
  border-bottom: 0px solid #202020 !important; 
  border-top: 0px solid #444 !important; 
  box-shadow: inset 0px 0px 1px rgba(0, 0, 0, 0.5) !important; 
}

#sideRight .accordion-body {
  background: rgba(60,60,60,0.1) url('https://imgur.com/7hiUIoo.png') !important; 
}
#sideRight .accordion-body a {
  color: #ccc !important;
}

.accordion-inner {
  padding: 9px 15px !important; 
  border-top: 1px solid #444 !important; 
    border-left: 0px solid #292929 !important; 
}









#sideLeftW {
    /*background: rgba(30,30,30,0.1) url('https://imgur.com/7hiUIoo.png') !important;  */
    background: rgba(30,30,30,0) !important;
    box-shadow: inset 0px 0px 10px rgba(0, 0, 0, 0.0) !important;  
    padding-right: 6px !important;  
}








.accordion-group {
    /*border: 1px solid #222 !important;*/
    border-top: 1px solid #555 !important;
    border-left: 1px solid #484848 !important;
    border-right: 1px solid #484848 !important;
    border-bottom: 1px solid #484848 !important;
    border-radius: 0px !important;
    box-shadow: 0px 0px 3px 0px rgba(0,0,0,0.75) !important;
}

.accordion-heading {
    background-color: #444 !important;
    border-radius: 0px !important;
    background-image: linear-gradient(to bottom, #444, #333) !important;
    background-repeat: repeat-x !important;
}

.accordion-heading a {
    color: #ccc !important;
}

.accordion-body {
    background: rgba(30,30,30,0.1) url('https://imgur.com/7hiUIoo.png') !important; 
}

.well {
    background-color: rgba(30,30,30,0.25) !important;
    border: 1px solid #484848 !important;
    border-radius: 4px !important;
    /*box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.05) !important;*/
    box-shadow: 0px 0px 3px 0px rgba(0,0,0,0.75) !important;
}

#sideLeft .torrent {
    background-color: rgba(30,30,30,0.25) !important;
    border: 1px solid #484848 !important;
    border-radius: 4px !important;
    box-shadow: 0px 0px 3px 0px rgba(0,0,0,0.75) !important;
}

#sideLeft .torrent .label-title {
    color: #ddd !important;
    text-shadow: 0 -1px 0 rgba(0, 0, 0, 0.25) !important;
    background-color: rgba(180,180,180,0.15) !important;
    border-radius: 4px !important;
    border: 1px solid #555 !important;
    box-shadow: 0px 0px 3px 0px rgba(0,0,0,0.75) !important;
}
.label-important, .badge-important {
    background-color: #36363e !important;
    border: 2px solid #65bbd6 !important;
    box-shadow: 0px 0px 3px 0px rgba(0,0,0,0.75) !important;
}
.label {
    border-radius: 4px !important;
}

.label, .badge {
    color: #ffffff;
    text-shadow: 0 -1px 0 rgba(0, 0, 0, 0.25);
    background-color: rgba(180,180,180,0.15) !important;
    border-radius: 4px !important;
    border: 1px solid #555 !important;
    box-shadow: 0px 0px 3px 0px rgba(0,0,0,0.75) !important;
}


#sideLeft .torrent .img-tracker {
    height: 80px;
    width: 80px;
    margin-left: -10px;
    margin-top: -9px;
    margin-right: 9px;
    border-radius: 4px 0 0 4px !important;
}


.accordion-heading h5.accordion-toggle {
    box-shadow: 0px 2px 0px 0px rgba(0,0,0,0.5) !important;
}



/*button*/
.btn-inverse {
    color: #ddd !important;
    text-shadow: 0 -1px 0 rgba(0, 0, 0, 0.25) !important;
    background-color: #505050 !important;
    background-image: linear-gradient(to bottom, #353535, #252525) !important;
    background-repeat: repeat-x !important;
    /*border-color: #222222 #222222 #000000 !important;*/
    /*border-color: rgba(0, 0, 0, 0.1) rgba(0, 0, 0, 0.1) rgba(0, 0, 0, 0.25) !important;*/
    border-color: #555 #444 #383838 !important;
    box-shadow: 0px 0px 3px 0px rgba(0,0,0,0.75) !important;

}
.btn-inverse:hover,
.btn-inverse:active,
.btn-inverse.active,
.btn-inverse.disabled,
.btn-inverse[disabled] {
    color: #ffffff !important;
    background-color: #222222 !important;
    *background-color: #151515 !important;
}

#sideLeft .label-link {
    color: #eee !important;
    text-shadow: 0 -1px 0 rgba(255, 255, 255, 0.02) !important;
    border-radius: 4px !important;
    background-color: #4a5254!important;
    border: 1px solid #616b6e !important;
    box-shadow: 0px 0px 3px 0px rgba(0,0,0,0.75) !important;
}
#sideLeft .label-link:hover {
    color: #ffffff !important;
    background-color: #417787 !important;
    border: 1px solid #4d8ea1 !important;
}



/*List*/
.breadcrumb {
    background-color: #f5f5f5 !important;
    background-image: linear-gradient(to bottom, #444, #333) !important;
    background-repeat: repeat-x !important;
    border-top: 1px solid #555 !important;
    border-left: 1px solid #484848 !important;
    border-right: 1px solid #484848 !important;
    border-bottom: 1px solid #444 !important;
    border-radius: 4px !important;
    box-shadow: 0px 0px 3px 0px rgba(0,0,0,0.75) !important;
}
.breadcrumb li {
    text-shadow: 0 1px 0 rgba(0,0,0,0.1) !important;
}
a {
    color: #65bbd6 !important;
}
.lastpostt {
    color: #bbb !important;
}

/*WRAP*/
/*.c-body, .q, .sp-wrap, .sp-head, .sp-body {
    /*border: solid #444 !important;*/
    /*border-width: 1px 1px 1px 1px !important;*/
    /*box-shadow: 0px 0px 3px 0px rgba(0,0,0,0.75) !important;*/
/*}*/
.sp-body {
    /*border-width: 1px 0 0 0 !important;*/
    background: rgba(0,0,0,0.45) url('https://imgur.com/7hiUIoo.png') !important; 
    /*background: #383838 !important;*/
    border-bottom: 0px solid #444 !important; 
    border: 1px solid #444 !important;
    
    border-radius: 0px 0px 4px 4px !important;
}
.sp-wrap {
    background-color: #424242 !important;
    border: 1px solid #555 !important;
    box-shadow: 0px 0px 3px 0px rgba(0,0,0,0.75) !important;
    border-radius: 4px !important;
}
.sp-head {
    color: #fff !important;
    border: 0px !important;
    border-bottom: 0px solid #555 !important;
    margin-left: -1px !important;
    box-shadow: 0px 0px 3px 0px rgba(0,0,0,0.0) !important;
    border-radius: 4px 4px 0px 0px !important;
}









.input-prepend.input-append .add-on:first-child, .input-prepend.input-append .btn:first-child {
    border-radius: 4px 0 0 4px !important;
}
.input-append .add-on, .input-prepend .add-on {
    text-shadow: 0 1px 0 rgba(0,0,0,0.1) !important;
    background-color: #333 !important;
    background-image: linear-gradient(to bottom, #444, #333) !important;
    background-repeat: repeat-x !important;
    border-top: 1px solid #555 !important;
    border-left: 1px solid #484848 !important;
    border-right: 1px solid #484848 !important;
    border-bottom: 1px solid #444 !important;
    box-shadow: 0px 0px 3px 0px rgba(0,0,0,0.75) !important;
}
select {
    background-color: #333 !important;
    background-image: linear-gradient(to bottom, #444, #333) !important;
    background-repeat: repeat-x !important;
    border-top: 1px solid #555 !important;
    border-left: 1px solid #484848 !important;
    border-right: 1px solid #484848 !important;
    border-bottom: 1px solid #444 !important;
    box-shadow: 0px 0px 3px 0px rgba(0,0,0,0.75) !important;
}


/*text*/
select, textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"], .uneditable-input {
    color: #ccc !important;
}






.btn {
    text-shadow: 0 1px 0 rgba(0,0,0,0.1) !important;
    background-color: #333 !important;
    background-image: linear-gradient(to bottom, #444, #333) !important;
    background-repeat: repeat-x !important;
    border-top: 1px solid #555 !important;
    border-left: 1px solid #484848 !important;
    border-right: 1px solid #484848 !important;
    border-bottom: 1px solid #444 !important;
    box-shadow: 0px 0px 3px 0px rgba(0,0,0,0.75) !important;
    color: #fff !important;
}



/*Separators*/
.table th, .table td {
    border-top: 1px solid #444 !important;
}

/*Paginator*/
.pagination ul {
    box-shadow: 0px 0px 3px 0px rgba(0,0,0,0.75) !important;
}
.pagination ul > .active > a, .pagination ul > .active > span {
    color: #999999 !important;
}
.pagination ul > li > a:hover, .pagination ul > .active > a, .pagination ul > .active > span {
    background-color: #555555 !important;
}

.pagination ul > li > a, .pagination ul > li > span {
    background-color: #484848 !important;
    border: 1px solid rgba(180, 180, 180, .2) !important;
}

/*DropDownList*/
.autocomplete {
    background: rgba(55, 55, 55, .95) !important;
    color: #ccc !important;
}
strong {
    color: #65bbd6 !important;
}
.autocomplete .selected {
    background:  rgba(180, 180, 180, .25) !important;
    border-radius: 4px !important;
}

/*Login*/
.modal {
    background-color: #333 !important;
    border: 1px solid #444 !important;
    border-radius: 6px !important;
    box-shadow: 0px 0px 3px 0px rgba(0,0,0,0.75) !important;
    background-clip: padding-box;
}
.modal-header {
    border-bottom: 1px solid #444 !important;
}
textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"], .uneditable-input {
    background-color: #282828 !important;
    border: 1px solid #444 !important;
    box-shadow: inset 0 0 5px 0px rgba(0, 0, 0, 0.45);
}
.modal-backdrop {
    background:  rgba(10, 10, 10, .99) url('https://imgur.com/7hiUIoo.png') !important;
}




.footer {
    background: rgba(30,30,30,0.45) url('https://imgur.com/7hiUIoo.png') !important;  
    border-top: 1px solid #444 !important;
    box-shadow: 0px -1px 0px 0px rgba(0,0,0,0.35) !important;
    
}
.navbar .brand {
    color: #65bbd6 !important;  
    text-shadow: 0 1px 0 rgba(0, 0, 0, .1) !important;  
}



.q-wrap {
    border: 1px solid #555 !important;
    border-radius: 6px !important;
    box-shadow: 0px 0px 3px 0px rgba(0,0,0,0.75) !important;
    background: #444 !important;
}
.c-body, .q, .sp-wrap, .sp-head, .sp-body {
    background: #333 !important;
    border: solid #555 !important;
        border-top-width: medium;
        border-right-width: medium;
        border-bottom-width: medium;
        border-left-width: medium;
    border-width: 1px 1px 1px 1px !important;
    border-radius: 8px !important;
    box-shadow: inset 0px 0px 3px 0px rgba(0,0,0,0.75) !important;
}

.c-body, .q, .sp-body {

    padding: 2px 6px !important;
    padding-right: 12px !important;

}




}
