css:
@-moz-document url-prefix("https://www.bitmex.com/app"), url-prefix("https://static.bitmex.com/assets"), url-prefix("https://testnet.bitmex.com/app"), url-prefix("https://testnet.static.bitmex.com/assets"), url-prefix("https://www.bitmex.com/popout") {
::root {
	--bg-light-color: #1f5881;
	--text-color: #687b93;
	--text-light-color: #7695bb;
	--bg-color: #10182f;
	--red: #a52c35;
	--green: #fcd315;
	--sidebarBackground: #170900;
	--headerBackground: #170900;
	--background: #10182f;
}
body {
	font-family: 'DINPro', 'IBMPlexSans-Medium', "Open Sans", "Helvetica Neue", "Helvetica", "Arial", sans-serif !important;
	background-image: radial-gradient(farthest-side ellipse at 30% 30%, #172344, #10182f, rgb(0, 0, 0));
}
.sideWrap {
	background: transparent;
}
.sidebar {
	background: #170900;
}
.tradeSidebar .help-block {
	border-bottom: 2px solid #fff;
}
.tradeSidebar .orderControls .ordTypes,
.tradeSidebar .orderControls .fullPositionStatus,
.tradeSidebar .orderControls .well,
.tradeSidebar .orderControls pre,
.tradeSidebar .orderControls .preferencesPage fieldset > .help-block,
.preferencesPage .tradeSidebar .orderControls fieldset > .help-block {
	background: transparent;
	color: #ddd;
}
#header {
	margin-bottom: 0.5rem;
}
#header .link,
#header .dropdown-toggle {
	color: #888;
}
.instrumentSelectorPanel.widget-tabs .btn.round {
	border-radius: 0;
}
.instrumentSelectorPanel.widget-tabs .inactiveExpiry .btn {
	color: #ddd !important;
}
.tradeSidebar .sidebar {
	color: #ddd;
}
.instrumentSelectorPanel.widget-tabs .activeExpiry .btn {
	background-color: #001d1f;
	color: #DBCC00;
}
.orderBook .asks td:nth-of-type(1),
.orderBook .asks td:nth-of-type(1) .myOrder {
	color: #ddd;
}
.instrumentSelectorPanel.widget-tabs .inactiveExpiry .btn {
	color: #ddd;
}
.btn-primary {
	color: #a7a7a7;
}
#header .link:active,
#header .link.active,
#header .dropdown-toggle:active,
#header .dropdown-toggle.active {
	color: #999 !important;
}
.btn-primary {
	background-color: #000000 !important;
}
.btn-success {
	background-color: #026221;
	border-color: #47b268;
	color: #fff;
}
.btn-danger {
	background-color: #9C2400;
	border-color: #e24e24;
	color: #fff;
}

/*chart*/
.darkTheme .chart-controls-bar {
	background: #111;
	border-top: 1px solid #555;
	height: 21px;
}
.darkTheme .chart-container {
	border-color: #555;
}
.darkTheme .favored-list-container span {
	background: #111;
	color: #eee;
	border: 1px solid #666;
}
.darkTheme .header-chart-panel .button,
.darkTheme .header-chart-panel .button.disabled,
.darkTheme .hotlist-controls .button,
.darkTheme .hotlist-controls .button.disabled,
.darkTheme .symbol-edit-widget .button,
.darkTheme .symbol-edit-widget .button.disabled {
	border: 1px solid #666;
	background: #111 !important;
	color: #eee !important;
}
.darkTheme .chart-container {
	border-color: #666;
	background-color: #111;
}


/* --- from blue theme --- */

/* ChatWidget */
.popUpChat .chatWidget {
	background: #22283d !important;
}
.chatSection {
	border-top: 1px solid rgb(56, 56, 67) !important;
}
.chatWidget .connected {
	border-bottom: 1px solid rgb(56, 56, 67) !important;
}
.chatWidget .chats {
	background: var(--background) !important;
}
.chatMessage {
	line-height: 1.2rem;
}
.chatMessage .message {
	color: #f5fbff !important;
	font-size: 1.1rem;
}
.widget .body .nav-tabs {
	border: 1px solid #2e3447 !important;
}
.chatWidget .nav-tabs {
	background: #22283d !important;
}
.chatSection:nth-of-type(odd) {
	background: rgba(29, 28, 28, 0.25) !important;
}
.popUpWidget header {
	border-top: 1px solid #4f576e !important;
}

/* ----- Tables -----*/
.openOrderTable .summary,
.executionTable .summary {
	border-left-color: #1a1f30 !important;
	border-right-color: #1a1f30 !important;
}
.table-container-inner table {
	border: 1px transparent !important;
}
.tradeHistory table tr.short.terminated td:first-child,
.openOrderTable tr.short.terminated td:first-child,
.executionTable tr.short.terminated td:first-child,
.positionsList tr.short.terminated td:first-child {
	border-left: 6px solid #1a1f30 !important;
}
.table-container-inner td {
	border-right: 1px transparent;
}
.orderControls {
	padding: 0.1rem;
}
.orderControlsAdvanced.well,
pre.orderControlsAdvanced,
.preferencesPage fieldset > .orderControlsAdvanced.help-block {
	padding: 0.8rem;
}
.tradeSidebar .orderControls .ordTypes .tabs-dropdown {
	background: #666 !important;
}
.tradeSidebar .orderControls {
	margin-left: 5px;
}
.tradeSidebar .orderControls .fullPositionStatus {
	--margin-left: 1px;
	--margin-right: 1px;
	padding: 0.4rem;
}
h1.underlined,
.staticContent h1,
.announcements h1,
form h1,
body.static-page .static-page .body h1,
h2.underlined,
.staticContent h2,
.announcements h2,
form h2,
body.static-page .static-page .body h2,
h3.underlined,
.staticContent h3,
.announcements h3,
form h3,
body.static-page .static-page .body h3,
h4.underlined,
h5.underlined,
h6.underlined {
	padding-bottom: .2857142858rem !important;
	border-bottom: 1px transparent;
	--border-bottom: 1px solid #22283d !important;
}
.logoWrap {
	display: block;
	text-align: center;
}
.tickerBar .tickerBarSection {
	border-left: 1px solid #22283d !important;
}
.orderBook td.price,
.orderBook td.price .myOrder {
	font-weight: 600;
}
.recentTradeList {
	font-size: 1.1rem;
	border: 0;
}
.btn.btn-primary.seriesGuideBtn.tooltipWrapper {
	border: 1px solid #000 !important;
	opacity: 0.75;
}
.tooltipWrapper div.cost {
	font-size: 1rem;
}
a .tooltipWrapper .value {
	font-size: 1rem;
	color: #9cd358;
}
a .tooltipWrapper {
	color: #FFF;
	text-decoration: none;
}
.positionDeleverageIndicator .leverageLight {
	border: 0;
	width: 10px;
	margin-right: 3px;
}
.orderBook.singleColumn .priceWidget.lastTick {
	display: block;
	padding: 0.5rem 0 1rem;
}
.priceWidget.PlusTick {
	color: #70a800 !important;
}
.priceWidget.MinusTick {
	color: #ea0070 !important;
}
.priceWidget.ZeroMinusTick {
	color: #d6efde;
}
.tradeSidebar .pricePos {
	color: #70a800 !important;
}
.highMarkLiqDif, .pricePos {
	color: #70a800 !important;
}
.tradeSidebar .priceNeg {
	color: #ea0070 !important;
}
.lowMarkLiqDif, .priceNeg {
	color: #ea0070 !important;
}
.table-container-inner .neg {
	color: #ea0070;
	text-shadow: -3px 0 6px #000, -3px 0 6px black, 3px 0 6px #000;
}
.table-container-inner .pos {
	color: #70a800;
	text-shadow: -3px 0 6px #222, 0 0 4px #222;	
}

.marginDisplay .fieldLabel {
	color: #ddd;
}
.marginDisplay .barPositive .dataBar {
	background-color: #70a800ee;
}
.marginDisplay .barNeutral .dataBar {
	background-color: #ea0070ee;
}

.dropdown-menu {
	background: #111 !important;
	color: #aaa !important;
	border: 1px solid #000 !important;
	border-top: 3px solid #666 !important;
}
.tradeSidebar .orderControls .ordTypes .dropdown-menu .tabTitle {
	background: transparent;
}
.plusMinusButtons .btn {
	color: #fefefe !important;
	font-weight: bold;
	border-color: transparent !important;
}
.plusMinusButtons .btn:nth-of-type(1) {
	background-color: #881e00;
}
.plusMinusButtons .btn:nth-of-type(2) {
	background-color: #7a4900;
}
.plusMinusButtons .btn:nth-of-type(3) {
	background-color: #36580d;
}
.plusMinusButtons .btn:nth-of-type(4) {
	background-color: #174607;
}

.tradeSidebar .orderControls .ordTypes .tabTitle {
	/*color: #c6e5fb !important;*/
	margin-top: 0 !important;
}

body.trade .static-footer {
	background: #000;
}
.static-footer .section {
	color: #999;
}
.static-footer .sections a {
	color: #777;
}
.static-footer h4 {
	color: #777;
	font-size: 1rem;
	font-weight: 800;
}
#footer {
	background-color: #000;
	/*display:none;*/
}
td.cumSize span.output {
	font-size: 0.9rem;
	float: right;
}
div.nowrap {
	margin-top: 0.5rem;
}
div.recentTradeItem.Buy {
	color: #ddd;
}
div.recentTradeItem.Sell {
	color: #ff7676;
}
div.orderBook.singleColumn > div.table-container.table.orderBookTable.asks td.cumSize div.depthBar {
	background-color: #ff7676;
}
.instrumentSelectorPanel.widget-tabs .expiries {
	border: 1px solid #000;
	margin: -2px 0;
	padding: 0 5px 0 0;
}
.widget {
	background: #170900;
}
div.resizesensor-wrapper .widget {
	border: 1px solid #171500;
	color: #BEBEBE;
}
div.resizesensor-wrapper .widget-tabs-outlined .nav-tabs .innerTitle {
	border: none;
}
.nav-tabs {
	padding-left: 0;
}
.nav-tabs span.innerTitle:first {
	margin: 0 10px;
}
div.resizesensor-wrapper .widget .nav-tabs li.active .tabTitle {
	border-color: #fff;
	background: #171500;
	color: #fff;
}
#header div:first span {
	color: #171500;
}
.nav-tabs li.active {
	background: transparent;
}
div.resizesensor-wrapper .widget .nav-tabs .tabTitle {
	border-left: none;
	border-right: none;
}
div.resizesensor-wrapper .widget .nav-tabs li {
	margin-right: 0;
}
div.resizesensor-wrapper .widget .nav-tabs li:last {
	border-right: none;
}
.nav-tabs {
	border-bottom: none;
}
div.resizesensor-wrapper .widget > header {
	/* background: #222; */
	color: #eee;
	opacity: 0.9;
}
.instrumentSelectorPanel.widget-tabs > header .nav-tabs {
	padding: 6px 0px;
}
.widget-tabs-outlined .nav-tabs .tabTitle {
	margin-left: 10px;
}
.nav-tabs li.active .tabTitle {
	border-right: 2px solid #222;
	border-left: 2px solid #222;
}
.lastChangePcnt {
	font-weight: 600;
}
.instrumentSelectorPanel.widget-tabs .instrumentPanelExpiry {
	background-image: linear-gradient(to bottom, rgba(51,51,51,0.4) 0%, rgba(51,51,51,0.4) 100%);
}
.instrumentSelectorPanel.widget-tabs .instrumentPanelExpiry .priceWidget {
	background: #000;
	padding: 0 2px 0 8px;
}
#content div.recentTradeList.reactList span {
	border: 1px transparent;
}
.darkTheme input/*.colorpicker-widget */{
	background: unset;
	background-color: unset;
}
.orderBook .table-container.no-hacks th {
	text-align: center;
}
a.helpCircle.titleTooltip.tooltipWrapper {
	margin-left: 2px;
	/*visibility: visible;*/
}
a.helpCircle.titleTooltip.tooltipWrapper.hovered {
	color: #fff;
}
.table-container.scrollable .table-container-inner {
	border-top: 0;
}
#content div.resizesensor-wrapper > div.react-grid-layout.gridLayout > div.react-grid-item.widget-container.orderBookWrapper.react-draggable.cssTransforms.react-resizable > section > header {
	opacity: 1;
}
.orderBook.singleColumn .limits {
	margin-bottom: 1rem;
}
.marginDisplay {
	border: none;
}
.marginDisplay .row {
	border-bottom: none;
}
.dialog {
	background: #222;
}

section.widget.fullscreened > header {
	background: none;
	border-bottom: none;
}
.widget.fullscreened .body {
	background: none;
}
.widget.fullscreened .nav-tabs li.active .tabTitle {
	border-left: none;
	border-right: none;
}
.depthChart {
	background: transparent;
}
span.date {
	font-size: 0.9rem;
}
.widget-container {
	background: #171500;
}
.widget-container.instrumentSelectorPanelWrapper {
	background: transparent;
}
section.instrumentSelectorPanel span.innerTitle {
	background: #000;
	padding: 0 9px;
}
.darkTheme .favored-list-container, .darkTheme .favored-list-container span, .darkTheme .favored-list-container a, .darkTheme .favored-list-container .item, .darkTheme .favored-list, .darkTheme .favored-list span, .darkTheme .favored-list a, .darkTheme .favored-list .item, .darkTheme .header-chart-panel .button, .darkTheme .header-chart-panel .button.disabled, .darkTheme .hotlist-controls .button, .darkTheme .hotlist-controls .button.disabled, .darkTheme .symbol-edit-widget .button, .darkTheme .symbol-edit-widget .button.disabled, .darkTheme .tv-dialog, .darkTheme ._tv-dialog, .darkTheme .tv-dialog .js-study-item, .darkTheme ._tv-dialog .js-study-item, .darkTheme input, .darkTheme input.inset, .darkTheme .context-menu-wrapper, .darkTheme .context-menu .action-separator td, .darkTheme .context-menu, .darkTheme .properties-tabs a.properties-tabs-label, .darkTheme .property-page, .darkTheme .charts-popup-list .item span, .darkTheme tr.symbol-edit-popup:hover, .darkTheme .tv-objects-tree-item {
	background-color: unset;
}
.table-striped tbody>tr:nth-child(odd)>td {
	background-color: rgba(51,51,51,0.35);
}
.table-container-inner td {
	padding: 0 2px;
}
.input-group-addon {
	border: 1px solid #222;
	background: #111;
}
#orderQty {
	border: 1px solid #222;
}

div.sideWrap.tradeSidebar.hidden-xs.responsiveOpen > div > div > li.accordion-group > h4 {
    background-color: #33333380;
	margin: 0 -0.5rem 0.5rem -0.8rem;
	padding: 0.4rem 1rem 0.4rem 0.4rem;
}
div.sideWrap.tradeSidebar.hidden-xs.responsiveOpen > div > div > li.accordion-group > h4 {
	font-size: 1.1rem;
	font-weight: 600;
}
.tradeSidebar .orderControls .ordTypes, .tradeSidebar .orderControls .fullPositionStatus {
	border: none;
}
.positionStatusIndicator>div .posValue {
	text-shadow: -3px 0 6px #111, 0 0 6px #111;
}
}
