css:
@-moz-document url-prefix("https://pikabu.ru") {
body {
    background-image: url(http://cs8.pikabu.ru/images/big_size_comm/2016-03_6/145908606116459402.png)!important;
    background-repeat: repeat-x;
    background-attachment: fixed;
}
.story__tags.tags .tags__tag, .stories-feed__message .tags > a {
    margin: 5px 4px 0 0;
    padding: 0 4px;
    background: rgba(132, 195, 80, .04);
    border: 1px solid rgba(132, 195, 80, .2);
    border-radius: 2px;
    line-height: 18px;
    color: #539022;
    text-shadow: 0 1px 0 rgba(0, 0, 0, .05);
    transition: all .2s ease-in-out;
}
.story__header {
    background: #8ac858;
    transition: color 200ms ease-out;
    font-size: 14px;
    line-height: 40px;
}
.story__title {
    font-weight: 500;
}
.story__title-link {
    color:#fff;
}
.collapse-button:hover{  background-color: #8ac858;}
.collapse-button{
    color: #ffffff;
    background-color: #8ac858;
    border:1px solid grey;
}
}
