.wcfImageViewer > div.loading:before {content: "\f110";margin: -24px 0 0 -24px;left: 50%;position: absolute;top: 50%;color: #151516;display: inline-block;font-family: FontAwesome;font-weight: normal !important;font-style: normal !important;line-height: 1em;text-align: center;-webkit-user-select: none;-moz-user-select: none;-ms-user-select: none;text-shadow: 0 1px 0 transparent;font-size: 42px;width: 48px;height: auto;-moz-animation: spin 2s infinite linear;-o-animation: spin 2s infinite linear;-webkit-animation: spin 2s infinite linear;animation: spin 2s infinite linear;}
