css:
@-moz-document domain(studier.nti.se) {
	a {
    color: #91c6f3;
}
body {
    background-color: #111111;
    color: #ccc;
}
.panel {
    background-color: #222;
}
.panel-default {
    border-color: #000;
}
.course-edu-instruction + .course-edu-instruction {
    background-color: #111111;
}
.navbar-default {
    background: #333;
    border-color: #000;
    border-top: 15px solid #444;
}
.alert-success {
    background-color: #2e532f;
    color: #00ff04;
}
blockquote {
    background-color: #111111;
}
.panel-success {
    border-color: #3c763d;
}
.panel-success > .panel-heading {
    color: #00ff04;
    background-color: #3c763d;
    border-color: #3c763d;
}
.well {
    background-color: #111;
    border: 1px solid #000000;
}
.exam-question-matrix-table td {
    border: 1px dotted #fff !important;
    background-color: #222;
}
.exam-question-matrix-cell-selected {
    background-color: #222 !important;
    background-image: linear-gradient(45deg, #111 25%, transparent 25%, transparent 50%, #111 50%, #111 75%, transparent 75%, transparent)!important;
}
.eq-score-wrapper {
    background-color: #111;
    border: 1px solid #000;
}
.nav-tabs > li.active > a, .nav-tabs > li.active > a:hover, .nav-tabs > li.active > a:focus {
    color: #b5b5b5;
    background-color: #333;
    border: 1px solid #000;
}
.bg-success {
    background-color: #235624;
}
h1 small, h1 .small, h2 small, h2 .small, h3 small, h3 .small, h4 small, h4 .small, h5 small, h5 .small, h6 small, h6 .small, .h1 small, .h1 .small, .h2 small, .h2 .small, .h3 small, .h3 .small, .h4 small, .h4 .small, .h5 small, .h5 .small, .h6 small, .h6 .small {
    color: #ccc;
}
.bg-info {
    background-color: #25313c;
}
.eq-score-wrapper {
    background-color: #222!important;
    border: 1px solid #000!important;
}
.panel-info > .panel-heading {
    color: #00abff;
    background-color: #25313c;
    border-color: #25313c;
}
.panel-info {
    border-color: #25313b;
}
.exam-question-matrix-table td {
    background-color: #222 !important;
}
.form-control {
    background-color: #111;
    border: 1px solid #000;
}
.form-control, output {
    color: #bbb;
}
a.list-group-item.message-thread {
    background-color: #333;
    color: #ddd;
}
.list-group-item {
    border: 1px solid #000;
}
.breadcrumb {
    background-color: #222;
}
.panel-default>.panel-heading {
    color: #999;
    background-color: #333;
    border-color: #000;
}
.discussion-post .panel-default:not(.panel-info)>.discussion-heading {
    background: #333;
}
a.list-group-item.message-thread.unread {
    color: #ccc;
    background-color: #555;
}
.message-thread:hover {
    background-color: #444!important;
}
.user-link.user-teacher {
    color: #d9534f;
}
.user-link.user-combox {
    color: #2e6da4;
}
.user-card {
    background-color: #333;
    border: 2px solid #000;
}
.user-card-image {
    border: 2px solid #000;
}
.modal-content {
    background-color: #333;
}
.alert-default {
    background-color: #333;
    border: 1px solid #000 !important;
}
.table-striped>tbody>tr:nth-of-type(odd) {
    background-color: #333;
}
.navbar-brand {
    background-color: #222!important;
}
.course-edu-instruction {
    background-color: #222222!important;
}
.literature-partial {
    background-color: #222222;
}
.timeline-green {
    border-left-color: #222!important;
}
.timeline-gray {
    border-left-color: #222!important;
}
h3:hover {
    color: rgb(204, 204, 204)!important;
}
.course-edu-week h3:hover {
    background-color: #141414;
}
.bg-success:hover {
    background-color: #2d7b2f!important;
}
.fa-file::before {
    color: rgba(255, 255, 255, 0.5)!important;
}
.user-link {
    background-color: #222222!important;
    color: rgb(204, 204, 204)!important;
}
.list-group-item {
    color: rgb(204, 204, 204)!important;
}
.user-teacher {
    background-color: inherit!important;
}
}
