css:
@-moz-document domain("googleprojectzero.blogspot.com") {
body {
    color: #2f2f2f;
    background-color: #2f2f2f;
}
.date-header span {
    background-color: #5997b5;
    color: #000;
}
.post-footer {
    color: #bebebe;
    background-color: #2f2f2f;
    border-bottom: 1px solid #2f2f2f;
}
body .navbar .Navbar, .content-inner {
    color: #bebebe;
    background-color: #2f2f2f;
}
.post-body * {
    color: #bebebe !important;
    background-color: transparent !important;
}
.comment-content, div.widget > h2, div.widget h2.title, div.widget h2.title, .Header .description, .Header h1 a, .Header h1, .post-title {
    color: #bebebe !important;
}

}
