css:
@-moz-document domain("forums.warframe.com") {
body.ipsApp {
    background: url('https://content.invisioncic.com/Mwarframe/pages_media/forums_background_dark.jpg') no-repeat fixed center top;
    background-size: 110%;
}
a:link, a:visited {
    color: #CCC;
}
a:hover {
    color: #FFF;
}
.ipsPagination:not(.ipsPagination_mini) .ipsPagination_page a, .ipsPagination:not(.ipsPagination_mini) .ipsPagination_next a, .ipsPagination:not(.ipsPagination_mini) .ipsPagination_prev a, .ipsPagination:not(.ipsPagination_mini) .ipsPagination_first a, .ipsPagination:not(.ipsPagination_mini) .ipsPagination_last a {
    background: #131313;
    color: #FFF;
}
.ipsPagination .ipsPagination_page a {
    text-shadow: none;
}
.ipsPagination .ipsPagination_page.ipsPagination_active a {
    background: #060;
}
.ipsNavBar_primary {
    background: #262626;
}
.ipsType_sectionTitle, .ipsApp .ipsButton_important, .ipsApp .ipsButton_primary, .ipsWidget.ipsWidget_vertical .ipsWidget_title {
    background: #262626;
}
html[dir] .ipsButton_split > li > .ipsButton.ipsButton_link {
    border: 1px solid #131313;
}
ul.ipsButton_split {
    background: #131313;
}
html[dir="ltr"] .ipsButton_split > li:not(:first-child) > .ipsButton {
    border-left: 1px solid #131313;
}
.ipsBreadcrumb > ul > li > a, .ipsBreadcrumb > ul > li {
    color: #FFF;
}
.ipsBreadcrumb > ul:last-child > li:last-child {
    opacity: 0.75;
}
.ipsItemStatus.ipsItemStatus_large {
    background: #060;
}
.ipsItemStatus:not(.ipsItemStatus_large) {
    color: #060;
}
.ipsCommentCount, .ipsFollow .ipsCommentCount {
    background: #262626;
    color: #FFF;
}
html[dir="ltr"] .ipsCommentCount::after, html[dir="ltr"] .ipsFollow .ipsCommentCount::after {
    border-color: transparent #262626 transparent transparent;
}
.ipsApp .ipsButton_link.ipsFollow {
    background: #131313;
    border: 0;
}
.ipsApp .ipsButton_link.ipsFollow:hover {
    background: #131313;
}
#ipsLayout_footer a, #ipsLayout_footer p {
    color: #FFF;
}
.ipsButtonBar .ipsPagination .ipsPagination_page.ipsPagination_active a {
    background: #060;
    color: #FFF;
}
#elSearchExpanded {
    background: #000;
    border-color: #000;
    top: 32px
}
#elSearchExpanded .ipsMenu_title {
    background: #262626 !important;
    border: 0;
}
span.ipsSideMenu_itemActive {
    background: #131313 !important;
}
span.ipsSideMenu_item {
    background: #000 !important;
}
span.ipsSideMenu_item:hover {
    background: #131313 !important;
}
#elSearchExpanded .ipsSideMenu_list {
    border: 0;
    background: #000;
}
#elSearchExpanded .ipsSideMenu_list:last-child {
    margin-bottom: -3px;
}
#elSearchWrapper.cSearchExpanded {
    padding: 10px 6px 10px 10px;
    margin: -10px -6px -4px -10px;
}
#elSearch {
    background: #131313;
}
#elSearchWrapper .cSearchSubmit {
    color: #FFF;
}
#ipsLayout_contentWrapper {
    background: rgba(0, 0, 0, 0.7);
}
.ipsDataList.ipsDataList_zebra .ipsDataItem:not(.ipsDataItem_selected):not(.ipsModerated):not(.ipsDataItem_new):not(.ipsDataItem_success):not(.ipsDataItem_warning):not(.ipsDataItem_error):not(.ipsDataItem_info):not(.ipsDataItem_status):nth-child(1n) {
    background: #000;
}
.ipsDataList.ipsDataList_zebra .ipsDataItem:not(.ipsDataItem_selected):not(.ipsModerated):not(.ipsDataItem_new):not(.ipsDataItem_success):not(.ipsDataItem_warning):not(.ipsDataItem_error):not(.ipsDataItem_info):not(.ipsDataItem_status):nth-child(2n) {
    background: #060606;
}
.ipsBox:not(.ipsBox_transparent):not(.ipsModerated), .ipsTabs_panel, .ipsTabs_activeItem {
    background: #060606;
}
.ipsDataList {
    background: #060606;
}
.ipsMessage_information {
    background: #030;
}
.ipsMenu {
    background: #000;
}
.ipsMenu_title {
    background: #262626;
    color: #FFF;
}
.ipsMenu_item > a:not(.ipsMenu_itemInline), .ipsMenu_item > span:not(.ipsMenu_itemInline) {
    color: #FFF;
}
ul:not(.ipsMenu_keyNav) .ipsMenu_item:not(.ipsMenu_itemClicked):not(.ipsMenu_itemDisabled) a:not(.ipsMenu_itemInline):hover, .ipsMenu_item[data-selected] a, .ipsMenu_item[data-selected] span, .ipsMenu_item.ipsMenu_hover {
    background: #131313;
}
.ipsApp .ipsButton_light {
    background: #000;
    color: #FFF;
}
.ipsReact_reactCount {
    background: #000;
    color: #FFF;
}
.ipsReact_reactCount > span > span:last-child, .ipsReact_reactCount > a > span:last-child {
    color: #FFF;
}
html[dir="ltr"] .ipsReact_blurb:not(.ipsHide) + .ipsReact_types {
    border-left: 1px solid #000;
}
.ipsQuote, .ipsSpoiler {
    background: #000;
    color: #FFF;
    border-color: #000;
    border-left: 2px solid #393939;
}
.ipsQuote_citation, .ipsSpoiler_header {
    background: #262626;
    color: #FFF;
}
.ipsSpoiler .ipsSpoiler_header a {
    color: #FFF;
}
body[data-role="internalEmbed"] {
    background: #000;
}
.ipsRichEmbed_openItem {
    position: absolute;
    top: 0px;
    right: 5px;
}
.ipsComposeArea_dummy {
    background: #000;
    color: #FFF;
}
.cke_top {
    background: #000;
}
a.cke_button_on {
    background: #262626;
}
a.cke_button_off:hover, a.cke_button_off:focus, a.cke_button_off:active, a.cke_button_disabled:hover, a.cke_button_disabled:focus, a.cke_button_disabled:active {
    background: #131313;
}
.cke_button_arrow {
    border-top: 3px solid #FFF;
}
.cke_button__bold_icon, .cke_button__italic_icon, .cke_button__underline_icon, .cke_button__strike_icon, .cke_button__ipslink_icon, .cke_button__ipsquote_icon, .cke_button__ipsspoiler_icon, .cke_button__ipsemoticon_icon, .cke_ltr .cke_button__bulletedlist_icon, .cke_rtl .cke_button__bulletedlist_icon, .cke_ltr .cke_button__numberedlist_icon, .cke_rtl .cke_button__numberedlist_icon, .cke_ltr .cke_button__outdent_icon, .cke_rtl .cke_button__outdent_icon, .cke_button__outdent_icon, .cke_ltr .cke_button__indent_icon, .cke_rtl .cke_button__indent_icon, .cke_button__indent_icon, .cke_button__justifyleft_icon, .cke_button__justifycenter_icon, .cke_button__justifyright_icon, .cke_button__justifyblock_icon, .cke_button__textcolor_icon, .cke_ltr .cke_button__outdent_icon, .cke_ltr .cke_button__cut_icon, .cke_rtl .cke_button__cut_icon, .cke_ltr .cke_button__copy_icon, .cke_rtl .cke_button__copy_icon, .cke_rtl .cke_button__paste_icon, .cke_ltr .cke_button__paste_icon, .cke_button__removeformat_icon, .cke_button__superscript_icon, .cke_button__subscript_icon, .cke_ltr .cke_button__undo_icon, .cke_rtl .cke_button__undo_icon, .cke_ltr .cke_button__redo_icon, .cke_rtl .cke_button__redo_icon, .cke_button__ipspage_icon, .cke_ltr .cke_button__ipspreview_icon, .cke_rtl .cke_button__ipspreview_icon {
    background-image: url(data:image/png;base64,) !important;
}
.cke_wysiwyg_frame, .cke_wysiwyg_div {
    background-color: #000;
    color: #FFF;
}
.cke_colorblock, .cke_colorblock a {
    background: #000;
    color: #FFF;
}
a.cke_colorauto, a.cke_colorbox, a.cke_colormore {
    border-color: #000;
}
a.cke_colorauto:focus, a.cke_colorbox:focus, a.cke_colormore:focus {
    background: #060;
    border-color: #FFF;
}
a.cke_colorauto:hover, a.cke_colorbox:hover, a.cke_colormore:hover {
    background: #131313;
    border-color: #FFF;
}
.ipsButtonRow li > a.ipsButtonRow_active, .ipsButtonRow li > button.ipsButtonRow_active {
    background: #262626;
    color: #FFF;
}
.ipsButtonRow li > a, .ipsButtonRow li > button, .ipsButtonRow li > span {
    background: #000;
    color: #FFF;
}
.ipsUserPhoto {
    background: transparent;
}
.cClubIcon {
    border: 0;
}
#elFlashMessage {
    background: rgba(0, 0, 0, 0.75);
    color: #FFF;
}
#elFlashMessage a {
    color: #FFF;
    border-bottom: 1px solid #999;
}
html[dir="ltr"] .ipsStream.ipsStream_withTimeline {
    border-left-color: #006600;
}
.ipsStreamItem_time, .ipsStream .ipsStreamItem_contentType {
    background: #009900;
    color: #FFF;
}
.ipsStream .ipsStreamItem_contentType {
    border-color: #009900;
}
html[dir] .ipsStream_withTimeline .ipsStreamItem.ipsStreamItem_actionBlock::after {
    background: #009900;
}
html[dir="ltr"] .ipsStream_withTimeline .ipsStreamItem.ipsStreamItem_contentBlock::after {
    border-color: transparent #000 transparent transparent;
}
html[dir="ltr"] .ipsStream_withTimeline .ipsStreamItem.ipsStreamItem_contentBlock::before {
    border-color: transparent #131313 transparent transparent;
}
.ipsAreaBackground_reset {
    background: #000;
}
.ipsStreamItem.ipsStreamItem_contentBlock {
    border-color: #131313;
}
html[dir="ltr"] .ipsStreamItem_bar hr.ipsHr, .ipsCommentUnreadSeperator {
    border-color: #006600;
}
.ipsTags .ipsTag, .ipsTag_prefix {
    background: #003300;
}
html[dir="ltr"] .ipsTags .ipsTag::before, html[dir="ltr"] .ipsTag_prefix::before {
    border-color: transparent transparent #003300 #003300;
}
.ipsTags .ipsTag::after, .ipsTag_prefix::after {
    background: #000;
}
.ipsTags .ipsTag:hover {
    background: #040;
}
html[dir="ltr"] .ipsTags .ipsTag:hover::before {
    border-color: transparent transparent #004400 #004400;
}
.ipsSticky {
    background: #000;
}
.ipsAreaBackground_dark {
    background: #131313;
}
a[data-mentionid] {
    background: #030;
}
.ipsTabs {
    background: #262626;
}
.ipsAreaBackground {
    background: #060606;
}
.ipsMemberCard {
    border-color: #131313;
}
}
