css:
@-moz-document url-prefix("https://xiaomi.eu/") {
html {
	background-color: #13100e !important;
	color: #E6E1E1 !important;
}
a, dd {
	color: #E6E1E1 !important;
}
.p-title-value, .block-minorHeader, .uix_categoryStrip-content, .menu-row.menu-row--highlighted, .tooltip--member .tooltip-content, .username--style2 {
	color: #E6E1E1 !important;
}
.p-nav-list .p-navEl.is-selected, .p-sectionLinks {
	background: #060505;
}
.p-sectionLinks, .uix_headerContainer .p-navSticky.is-sticky .p-sectionLinks {
	background: #060505;
	border-bottom: 1px solid #1F1F1F;
}
.has-flexbox .thNodes__nodeList .block-container .node-body {
	background-color: #000 !important;
}
.block-footer {
	background: #060505 !important; 
	border-top: 1px solid #1F1F1F;
}
.block-container, .block-separator, .menu-header {
	background-color: #000000;
	color: #E6E1E1;
	border-color: #1f1f1f;
}
.p-footer-copyrightRow {
	background: #000;
	border-top: 1px solid #1F1F1F;
}
.menu, .menu-row.menu-row--highlighted, .menu-footer {
	background: #000 !important;
}
.menu-footer {
	border-top: 1px solid #1f1f1f;
}
.menu-separator {
	border-top: 1px solid #1f1f1f;
}
.tooltip--member .tooltip-content, .memberTooltip-header, .button.button--link, a.button.button--link, .menu-content {
	background: #000;
	color: #e6e1e1;
	border: 1px solid #1f1f1f;
}
.menu-linkRow.is-selected, .menu-linkRow:hover, .menu-linkRow:focus, .structItem:hover {
	background: #080706;
}
.block-filterBar {
	color: #e6e1e1;
	background: #080706;
}
.structItem:nth-child(even) {
	background-color: #060505;
}
.structItem {
	border-top: 1px solid #1f1f1f;
}
.structItem-cell.structItem-cell--meta {
	border-left: 1px solid #1f1f1f;
	border-right: 1px solid #1f1f1f;
}

.structItem-pageJump {
	background-color: #080706;
}
.uix_extendedFooter, .p-footer-inner {
	background: #873600;
}
.block--messages .message, .block--messages .block-row, .block-row.block-row--alt {
	background: #000;
	color: #E6E1E1;
	border-color: #1f1f1f;
}
.message-cell.message-cell--user, .message-cell.message-cell--action {
	background: #060505;
	border-color: #1f1f1f;
}
.actionBar, .message-signature {
	border-color: #1f1f1f;
}
.block-header {
	color: #E6E1E1;
	border-color: #1f1f1f;
}
.bbCodeBlock-title {
	background: #000;
}
.bbCodeBlock {
	border-color: #1f1f1f;
	border-left-color: #ff6700;
	background-color: #13100E;
	color: #e6e1e1;
}
.bbCodeBlock-expandLink {
	background-image: linear-gradient(to bottom, rgba(19,16,14,0) 0%, #13100e 80%)
}
.bbCodeBlock--code .bbCodeBlock-content {
	background-color: #13100E;
	color: #e6e1e1;
}
*::selection {
	color: #fff;
	background: #A4A5A5;
}
.memberHeader-main {
	background: #080706;
	border-color: #1f1f1f;
}
.blockLink {
	background: #000;
}
.pageNav-page, .pageNav-jump {
	background: #080706;
	border: 1px solid #1f1f1f;
}
.pageNav-page:not(:first-child) {
	border-left-color: #1f1f1f;
}
.blockLink.is-selected, .blockLink:hover {
	background: #080808;
}
.block[data-widget-id] .block-minorHeader, .p-body-sideNav .block-minorHeader, .p-body-sidebar .block-minorHeader, .uix_extendedFooterRow .block-minorHeader, .block[data-widget-id] .block-header, .p-body-sideNav .block-header, .p-body-sidebar .block-header, .uix_extendedFooterRow .block-header, .block-row.block-row--separated+.block-row, .memberHeader-content, .memberHeader-separator, .message+.message, .message.message--bordered, .block-tabHeader, .overlay-title {
	border-color: #1f1f1f;
}
.message-responseRow, .formRow>dt, .memberOverviewBlock-seeMore {
	background: #060505;
	border-color: #1f1f1f;
}
.formSubmitRow-bar {
	background: #080706;
	border-color: #1f1f1f;
}
.structItem.structItem--note, .overlay-title {
	color: #e6e1e1;
	background: #080706;
}
.block-textHeader {
	color: #e6e1e1;
}
.block-filterBar .filterBar-filterToggle {
	background: #1A1818;
}
.pollResult.pollResult--showVoters:hover {
	background: #080706;
}
.fr-toolbar {
	color: #e6e1e1;
	background: #060505;
	border-color: #1f1f1f;
	border-top-color: #ff6700;
}
.fr-box.fr-basic .fr-wrapper {
	background: #060505;
	border-color: #1f1f1f;
}
.fr-box.fr-basic .fr-element{
	color: #e6e1e1;
}
.message-attachments {
	background: #080706;
	border-color: #1f1f1f;
	border-top-color: #ff6700;
}
.fr-separator {
	background: #1f1f1f;
}
.p-staffBar {
	background: #873600;
}
.p-header, .p-nav {
	background: #db5800;
}
}
