css:
@-moz-document url-prefix("https://get-atlas.xyz/dashboard") {
    .navbar-spark .navbar-nav .dropdown-menu {
        border-radius: 2px;
        margin-top: -14px;
        width:auto;
    }
    
    input.form-control.form-control-lg.command-search,
    .navbar-spark {
        background: #2b2d3399 !important
    }

    .widget > .card-heading {
        background: rgba(0, 0, 0, 0)
    }

    .widget {
        background: #2b2d33d1
    }

    .page-header {
        background: rgba(0, 0, 0, 0) !important
    }

    body {
        background-color: rgba(0, 0, 0, 0) !important;
        background-image: url('https://i.imgur.com/TsnueZX.png') !important;
        background-size: cover !important;
        background-repeat: no-repeat !important;
        background-position: center !important;
        background-attachment: fixed !important;
    }
}
