css:
@-moz-document domain("mapy.cz") {
:root {
--background-default:#222;
--background-lighter:#303030;
--background-lightest:#444;
--text-color-highlight:#fff;
--text-color-default:#eee;
--text-color-secondary:#999;
}
body{background:var(--background-default)}
.loader-big{filter: invert(0.8)}
.tweak p {color:var(--text-color-secondary)}
button, button:active, button:focus{background: none}
#language-control span{background: none;font-weight:bold}


#layout-wrapper {background:var(--background-default)}
#layout-bar, #layout-bar button {background:var(--background-lightest)}
#route-params-box {background:var(--background-lighter)}
#route-results h2{color:var(--text-color-highlight)}
#route-params-box .route-param-box{color:var(--text-color-highlight)}
#route-form .route-item.box .frame.hover, #route-form .route-item.box .frame:hover, .route-item.reorder-dragged.box .frame.hover, .route-item.reorder-dragged.box .frame:hover{background:var(--background-lightest)}
#route-form .route-item.box .frame.hover::after, #route-form .route-item.box .frame:hover::after, .route-item.reorder-dragged.box .frame.hover::after, .route-item.reorder-dragged.box .frame:hover::after{background:var(--background-lightest)}
#route-form .route-item.box .frame.hover::before, #route-form .route-item.box .frame:hover::before, .route-item.reorder-dragged.box .frame.hover::before, .route-item.reorder-dragged.box .frame:hover::before{background:var(--background-lightest)}
#route-form.simple .route-item.box .frame.hover::before, #route-form.simple .route-item.box .frame:hover::before{background:var(--background-lightest)}
#route-form .route-item.box .frame h4, .route-item.reorder-dragged.box .frame h4{color:var(--text-color-highlight)}
#route-form .route-item.box.end .frame span, #route-form .route-item.box.start .frame span, #route-form .route-item.box.waypoint .frame span, .route-item.reorder-dragged.box.end .frame span, .route-item.reorder-dragged.box.start .frame span, .route-item.reorder-dragged.box.waypoint .frame span{filter: invert(1) hue-rotate(180deg)}
#route-form .route-item .close-next, #route-form .route-item .plus, .route-item.reorder-dragged .close-next, .route-item.reorder-dragged .plus{filter:invert(1)}
#route-form.multiroute .route-item.box .btn-panel .reverse{background:none;color:var(--text-color-highlight)}
.itinerary{background:var(--background-lighter)}
.modules .module-content.itinerary.opened > div{background:var(--background-lighter)}
.itinerary ul{background:var(--background-lightest)}
.itinerary h2{color:var(--text-color-highlight)}
.itinerary ul p a{color:var(--text-color-highlight)}
.itinerary .item p{color:var(--text-color-highlight)}
.itinerary .extra p{color:var(--text-color-highlight)}
.itinerary ul{color:var(--text-color-highlight)}
.itinerary .item:hover{background:var(--background-lightest)}
.modules .module-content a.button.profile-switch, .modules .module-content button.profile-switch{color:var(--text-color-highlight)}

#search #route-results{background:var(--background-lighter)}
#search-results li h3{color:var(--text-color-highlight)}
#search-results ul > li.hover, #search-results ul > li:hover{background:var(--background-lightest)}
#search-results li .type-name{color:var(--text-color-secondary)}
#search-results .show-repeat, #search-results .show-report{color:var(--text-color-default)}
#search-results ul .opening{color:var(--text-color-highlight)}

#detail{color:var(--text-color-highlight)}
#detail .header{background:var(--background-lightest)}
#detail .content-contact em, #detail .content-review em{color:var(--text-color-highlight)}
#detail .content-opening p.title .today{color:var(--text-color-highlight)}
#detail .content-opening .note{color:var(--text-color-secondary)}

#map {filter: invert(1) hue-rotate(180deg)}
#mapset-switch{background-color:rgba(0,0,0,.7)}
.popup-window {background:var(--background-default)}
.form-cont{color:var(--text-color-highlight)}
.message > textarea{color:var(--text-color-highlight)}
.email > input[type="text"]{color:var(--text-color-highlight)}
.shbox .shitem::before, .shbox .shitem span{color:var(--text-color-default)}
.shbox .share-content.email label span:hover, .shbox .share-content.link input.complete{color:var(--text-color-highlight)}

}
