css:
@-moz-document domain("my.tablotv.com") {
  .tiles {
      background: #000;
  }
  html .menu .menu-item.aux {
      background: #222;
  }
  .component-takeover {
      background-color: #111
  }
  .overlaySwitch .list-group-item.list-group-item-tablo[data-focuszone-active] {
      color: #BCBCBC;
  }
  .panel.panel-details .panel-content {
      background-color: #111;
  }
  .fa {
      color: gold;
  }
  html .menu .menu-item.aux:hover {
      background-color: #555;
  }
  html .menu .menu-item:hover {
      background-color: #555;
  }
  .menu {
      background-color: #222222;
  }
  .menu-item-text {
      color: #cdcdcd;
  }
  .offsetter {
      background: #222222;
  }
  .settings-area {
      background: 090909;
  }
  .panel {
      background-color: #000000;
  }
  .panel-content {
      background-color: #111111;
  }
  .panel.panel-details .panel-body {
      background-color: #111111;
      color: #DDD;
  }
  .airing-perspective {
      color: #DDDDDD;
  }
  .btn-group-justified > .btn-group .btn {
      background: #000;
  }
  .is-selected {
      background: #222222;
  }
  .airings.airings-default .airing.is-selected {
      background: #222222;
  }
  .panel-content {
      background: #222222;
  }
  .airing-perspective {
      background: #444444;
  }
  .panel-heading {
      color: #cccccc !important;
      background-color: #050505 !important;
  }
  .menu-item-has-focus {
      background: #333333;
  }
  .menu .menu-item.active {
      background-color: darkblue;
  }
  html .slide-left-content {
      background: #222
  }
  button.btn.btn-bare.btn-close.btn-menu-close {
      background: #000;
  }
  html .menu .menu-item.active:hover {
      background: #444;
  }
  html .menu .menu-item.active {
      background-color: #111;
  }
  .tiles .tile.rendered {
      border-color: #333;
  }
}
