@-moz-document domain("engbook.pl"), domain("engbook.eu") {
::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(160, 22, 45, 0.55);
    background-color: rgba(160, 22, 45, 0.15);
}
::-webkit-scrollbar {
    width: 13px;
    background-color: #F5F5F5;
}
::-webkit-scrollbar-thumb {
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, .3);
    background-color: #D62929;
}
body {
    background-image: url('http://www.rybka.ct8.pl/1/gg.JPG') !important;
}
body .km_style > .content {
    background: rgba(0, 0, 0, 0.8) !important;
    padding: 20px;
    border-radius: 15px;
}
body .km_style > .header {
    background: rgba(0, 0, 0, 0.8) !important;
    border-radius: 15px;
}
body .km_style {
    color: #cccccc !important;
}
.km_style .header hr {
    background: rgb(126, 126, 126) !important;
    position: initial;
}
div#slider {
    height: 0px;
}
.km_style img {
    filter: hue-rotate(310deg);
}

/** PRZYCISKI **/
.km_style a.button {
    background: rgba(160, 22, 45, 0.55) !important;
    border-radius: 10px;
    color: white !important;
}
.km_style a.button.bigger {
    border-radius: 10px;
    color: white !important;
    padding: 9px 9px 9px 9px;
}
body > span.km_style > div.content > div.twocolumn > div.left > section.dates > form > p > input[type="submit"]:nth-child(5) {
    background: rgba(160, 22, 45, 0.55) !important;
    border-radius: 10px;
    color: white !important;
}
.km_style input[type=text],
.km_style input[type=password],
.km_style textarea,
.km_style select {
    border: 1px solid rgba(0, 0, 0, 0.7) !important;
    background: rgba(0, 0, 0, 0.3) !important;
    border-radius: 10px;
}

/** NAGŁÓWKI **/
.km_style section > h2 {
    border-radius: 15px;
    background: #a0162d !important;
}
.km_style section > h2:before {
    border-top-left-radius: 15px;
    border-bottom-left-radius: 15px;
    background: #e8667d !important;
}


/** TABELKA **/
.km_style table.regular {
    border-top: 1px solid rgba(0, 0, 0, 0.95)
}
.km_style table.regular thead tr {
    background: rgba(232, 102, 125, 0.2) !important;
}
.km_style table.regular tr.odd {
    background: rgba(232, 102, 125, 0.1) !important;
}
.km_style table.regular td,
table th {
    border-bottom: 1px solid rgba(0, 0, 0, 0.95) !important;
}

/** WYRÓŻNIONY TEKST **/
.km_style a,
.km_style a:visited {
    color: #d63939 !important;
}
.km_style .redtext {
    color: #d63939 !important;
}
.km_style .yellowtext {
    color: #d63939 !important;
}
.km_style .greentext {
    color: #d63939 !important;
}
.km_style ul.bulletlist li.bulletyellow:before,
.km_style ul.bulletlist li:before {
    color: #e8667d !important;
    margin-top: -1px;
}
.km_style ul.bulletlist li:before {
    content: "\2764";
}
.km_style .blue_stats {
    color: #d63939
}
.km_style .yellow_stats {
    color: #d63939
}
.km_style .green_stats {
    color: #d63939
}
.km_style .grey_stats {
    color: #d63939
}
.km_style a.bigcross {
    filter: hue-rotate(310deg);
}
section.remember div.icon.green {
    filter: hue-rotate(270deg);
}
section.remember div.icon.blue {
    filter: hue-rotate(160deg);
}

/** MEDAL **/
section.forteacher div.achievements div.text {
    color: #d63939 !important;
    vertical-align: 0px !important;
}

/** KALENDARZ **/
.km_style table.calendar th {
    background: rgb(232, 102, 125) !important;
}
.km_style table.calendar td.nextmonth {
    background: rgba(232, 102, 125, 0.2) !important;
    opacity: 1;
    color: #a8a8a8 !important;
}
.km_style table.calendar td.yellow {
    background: rgb(160, 22, 45) !important;
}
}

@-moz-document domain("engbook.pl"), domain("engbook.eu") {
/** STRONA LOGOWANIA **/

/** PASEK GÓRNY **/
nav#menu-top {
    background: rgba(0, 0, 0, 0.8) !important;
    padding: 20px;
    border-radius: 15px;
}
nav#menu-top a {
    color: #d63939 !important;
}
nav#menu-top .login-form {
    background: rgba(160, 22, 45, 0.55) !important;
    border-radius: 10px;
    color: white !important;
}
nav#menu-top #register.row {
    color: #cccccc !important;
}
nav#menu-top .logo {
    filter: hue-rotate(310deg);
}

/** KONTENT **/
#content {
    background: rgba(0, 0, 0, 0) !important;
    padding-right: 20px;
    border-radius: 15px;
    width: 940px;
    margin-top: -50px;
}
#about .col.info p.description {
    border-left: 20px solid #e8667d;
    background-color: #a0162d;
    color: white;
    padding: 3px;
    padding-left: 6px;
    border-radius: 15px;
}
#offer .col.student p.heading,
#offer .col.teacher p.heading {
    background-color: #a0162d;
    border-radius: 15px;
}
#footer p.copyright {
    background-color: #e8667d;
    border-radius: 15px;
    color: white;
    font-weight: 200;
    font-size: 15px;
}

/** OBRAZKI **/
p.heading::before,
#offer p.entry::before {
    filter: invert(100%)
}

/** WYRÓŻNIONY TEKST **/
p.heading {
    color: #cccccc !important;
}
}

@-moz-document domain("engbook.pl"), domain("engbook.eu") {
.old_style #container {
    background: rgba(0, 0, 0, 0.8) !important;
    padding: 20px;
    border-radius: 15px;
}
.old_style #container div.first_page {
    color: white;
}
.old_style #container div.first_page div.std_text a {
    color: #d63939;
}
.old_style .green {
    color: #d63939 !important;
}
.old_style #container #c_top,
.old_style #container #c_bottom {
    height: 0px;
}
.old_style #container div.hr {
    background: rgba(160, 22, 45, 0.6) !important;
    height: 3px;
    padding-bottom: 1px;
    margin-bottom: 10px;
}
.old_style #container div.first_page div.date {
    color: #d63939 !important;
}
.old_style #container div.book_content {
    color: white;
}
.old_style div.exercise_body table tr td {
    font-size: 13px;
}
.old_style div.header h2 {
    background: #a0162d !important;
    color: white;
    font-size: 14px;
    line-height: 25px;
    padding-right: 10px;
    border-top-right-radius: 15px;
    border-bottom-right-radius: 15px;
}
.old_style div.header div.openup,
.old_style div.button div.sheets {
    background: #e8667d !important;
    border-top-left-radius: 15px;
    border-bottom-left-radius: 15px;
    width: 10px;
}
.old_style div.header div.closeup {
    height: 0px;
}
.old_style div.button div.exercise {
    background: #e8667d !important;
    border-top-left-radius: 15px;
    border-bottom-left-radius: 15px;
    width: 10px;
}
.old_style div.button div.body {
    background: #a0162d !important;
    color: white;
    padding-left: 5px;
    font-size: 12px;
    line-height: 27px;
    padding-right: 10px;
    border-top-right-radius: 15px;
    border-bottom-right-radius: 15px;
}
.old_style div.button div.closeup {
    height: 0px;
}
.old_style div.button * {
    height: 26px;
    margin-bottom: 5px;
}
.old_style div.button div.asterix {
    background: #e8667d !important;
    border-top-left-radius: 15px;
    border-bottom-left-radius: 15px;
    width: 10px;
}
.old_style div.button div.blank_orange {
    background: #e8667d !important;
    border-top-left-radius: 15px;
    border-bottom-left-radius: 15px;
    width: 10px;
}
.old_style div.stat_box {
    background: rgba(160, 22, 45, 0) !important;
    padding: 5px;
    border-radius: 15px;
    border: 2px solid rgba(160, 22, 45, 0.7) !important;
}
#user_exercise > div.left_align > div > div {
    color: white !important;
}
.old_style div.book_content ul li,
.old_style div.first_page ul li {
    background: rgba(160, 22, 45, 0) !important;
}
.old_style div.stat_box a {
    color: #d63939 !important;
}
#user_exercise > div.body > a {
    color: #d63939 !important;
}
#container > div.book_content > div > div.exercise_body.top_15 > form > table > tbody {
    filter: hue-rotate(310deg);
}
.old_style table.register_table tr td.arrow {
    background: rgba(160, 22, 45, 0) !important;
}
#container > div.first_page > div.premium > a > div {
    border-radius: 15px;
    background: #a0162d !important;
    color: white;
}
#container > div.first_page > div.premium > a > div > div.star > img {
    filter: invert(100%);
}
.old_style #foot a,
.old_style .orange {
    color: #d63939 !important;
}
}
