css:
@-moz-document url-prefix("https://webmail.stud.uni-giessen.de/horde/"),url-prefix("http://webmail.stud.uni-giessen.de/horde/")
{
#menu, body{                                  /*Menü oben und Untergrund*/
    background-color: #181818; /*dunkelgrau*/
    color: #d7d7d7
}
#menu a:hover {
    background-color: #2d2d2d; /*grau*/
    border-bottom: hidden;
    border-right: 1px solid transparent;
    border-left: 1px solid transparent;
    border-top: 1px solid transparent;
    padding: 2px;
}
#menu a.current {
    background: #232631; /*dunkles blassblau*/
    border-bottom: hidden;
    border-right: 1px solid transparent;
    border-left: 1px solid transparent;
    border-top: 1px solid transparent;
    padding: 2px;
}

/*TopNav*/
.header, .header span {                                    
    background-color: #232631; /*dunkles blassblau*/
}

/*SideNav*/
#sidebarPanel, #t_horde_menu a, #t_horde_menu span {                             
    border-color: transparent;
    background-color: #2d2d2d; /*grau*/
    color: #d7d7d7;
}
#sidebarPanel:after{
    content: "skin by duncan wick";
    color: #181818;
    padding: 20px;
}

#menu a{                                    /*Navbar m großen Icons*/
    color: #d7d7d7
}

.mboxcontrol {                              /*Menü Leiste über Nachrichten (Löschen, Ausschlussliste, etc)*/
    background-color: #181818; /*dunkelgrau*/
}

/*Settings*/
.prefsOverview dt a {
    display: block;
    padding: 5px;
    font-weight: bold;
    border: 1px solid #000;
    border-bottom: 1px solid #446;
    background: #2d2d2d; /*grau*/
    color: white;
}
.prefsOverview dd {
    padding: 5px;
    margin-bottom: 0px;
    color: #d7d7d7;
    background: #181818 /*dunkelgrau*/;;
    border-left:hidden;
    border-right: hidden;
    border-bottom: hidden;
    font-size: 90%;
}

/*LOGO Oben links*/
#menu span a{
    content: url(https://i.imgur.com/ttv5WcB.png);
    width: 80%;
    margin: auto;
    padding: 1px;
}
#menu span a:hover {
    border-bottom: hidden;
    border-right: hidden;
    border-left: hidden;
    border-top: hidden;
    padding: 1px;
    content: url(https://i.imgur.com/mFn7IA1.png);
}

/*IN MAIL*/
#msgheaders td{
    color: black;
}
.nowrap a{
    color: black;
}

/*New Message Popup*/
.item td, tr, table, tbody{
    color: #d7d7d7;
}


/*Tabellen übersicht*/
tr, td {
    background: #181818 /*dunkelgrau*/;
    color: #d7d7d7;
    border: hidden;
}

a {color: White;}         /*Links*/
th, .item, .selected {
    background-color: #2d2d2d; /*grau*/
    color: #d7d7d7;
}
table, th, td, tr, tbody {border: hidden;}

.ohide{
    color: #d7d7d7;
}

.rightFloat a{              /*Zurück zu Posteingang*/
    color: #d7d7d7;
}
}


@-moz-document url-prefix("https://webmail.stud.uni-giessen.de/horde/login.php")
{
body {
    color: #d7d7d7;
}

#menu {
    background-color: #232631; /*dunkles blassblau*/         /*header bg*/
}
}
