css:
@-moz-document domain("uhdpixel.com") {
/* Ad Removal */
.post_top, .adsbygoogle, .addthis-smartlayers, .banner_2, .native_ad, #IL_INSEARCH, .post_bottom .embed, .IL_BASE, .desc_con > div[id^="p_root"] {
    display:none !important;   
}

body, html {
    background-color: #272a30 !important;
    font-family:"Segoe UI";
    font-size:12px;
    font-weight:normal;
    animation: fadein 0s;
    -moz-animation: fadein 0s;
    -webkit-animation: fadein 0s;
    -o-animation: fadein 0s;
}

/* Navigation */
.head_container {
    padding:0px 0 0px;
    text-align: left;
    background: rgba(237, 239, 245, 1);
}

.head_title {
    padding-left:10px;
}

.wp_count {
    margin-left:1em;
    font-weight:100;
    color:#1d5064;
}

/* Pages */

.pages {
    float: right;
    margin-right: 50px;
    margin-top: 20px;
}

.pagination {
    display: inline-block;
    padding-left: 0;
    margin: 0px;
}

.pagination > li > a, .pagination > li > span {
    position: relative;
    float: left;
    padding: 15px 22px;
    color: #fff;
    background-color: rgba(57, 60, 69, 1);
    margin-left: 1px;
}

/* Search Box */
.head_container div:nth-child(2) {
    float: right;
    padding:10px;
}

.form_wrap {
    margin-right:30px;
}

.form_wrap input {
    width: 100% !important;
    color: #3f4f61 !important;
    background: #fbfbfb !important;
    padding: 10px 15px !important;
    border: none !important;
    text-align: left !important;
    border-radius: 15px !important;
}

.form_wrap button {
    display:none;
}

/* Thumbnails */

.wall_container {
    margin: 0px 0px;
}

.thumb_con {
    transition: all .1s ease-in-out;
    position: relative;
    bottom: 0px;
    margin: -2px;
    border: 4px solid transparent;
    background: transparent;
    z-index: 1;
}

.thumb_con:hover {
    bottom: 10px;
    box-shadow: #000000a8 0px 30px 6px;
    z-index: 1000;
    border: 4px solid #ffffffe3;
}

/* Content */
.top_container {
    padding:10px 0px;
    text-align: left;
    background: hsl(220, 10%, 17%);
}

.post_content {
    width: 100%;
    background: #272a30;
}

h1 {
    color: #8d969a;
    margin: 0;
    text-transform: none;
    font-weight: 100;
    font-size: 3em;
    font-family: "Segoe UI";
    margin-left: 10px;
}

.top_container span {
    display:none;
}

.imgcon {
    border:0px;
    background: #272a30;
}

.imgcon img {
    max-width: 1280px;
}

p[itemprop="description"] {
    display:none;
}

/* Download Instructions */
.wp_dl p:nth-child(1) {
    display:none;
}

.post_bottom {
    margin-top:0px;
    background: transparent;
}

/* Tags */
.wp_tag {
    padding: 10px !important;
}

.wp_tag span:nth-child(1) {
    display:none;
}

div[class$="_tag"] a {
    color: hsl(208, 12%, 78%);
    margin: 2px;
    padding: 5px 15px;
    background: hsl(208, 11%, 28%);
    display: inline-block;
    font-size: 12px;
    font-weight: 100;
    border: 0px solid hsla(163, 41%, 50%, 1);
    border-radius: 15px;
}

div[class$="_tag"] a:hover, .pagination > li > a:hover, .pagination > li > span:hover, .pagination > li > a:focus, .pagination > li > span:focus {
    color:white;
    background-color: #7eb1c9;
}

.pagination > .active > a, .pagination > .active > span, .pagination > .active > a:hover, .pagination > .active > span:hover, .pagination > .active > a:focus, .pagination > .active > span:focus {
    background: hsla(220, 10%, 13%, 1);
    color: #505050;
}

.sub_tag {
    margin: 0px;
    float: right;
    margin-top: 17px;
    margin-bottom:10px;
    margin-left:20px;
    margin-right:20px;
}


/* Download Buttons */
.wp_dl {
    width: 100%;
    padding-right:10%;
    height: 56px;
    background: rgba(40, 42, 48, 1);
}

.wp_dl a {
    width: 150px;
    color: #e6ebef;
    background: rgba(66, 159, 133, 1);
    border-bottom: 0px;
    padding: 10px;
}

.wp_dl a:hover {
    background-color: hsla(163, 41%, 64%, 1);
}

/* Footer */
#footer {
    text-align:right;
    display:none;
}

.wp_tag {
    padding:0px;
}

.ft_link {
    padding-right:10px;
}
}
