css:
@-moz-document url-prefix("https://web.okjike.com") {
body {
    background: #000;
    color: #ffffff;
}
.btn-primary {
    border: #ffe411 1px solid;
    background: none;
}
.btn-primary.btn-disabled {
    border: #ffe411 1px solid;
    background: none;
}
.container-fluid,
.root-layout {
    background-color: #000;
}
.menu-item > a {
    color: #ddd;
}
.menu-item > a span:nth-child(2) {
    color: #ffe411;
}
.site-header,
.site-header-wrap,
.message-card {
    background: #222;
    color: #fff;
}
.header-item {
    color: #ffe411;
}
.header-item-active,
.header-item-icon {
    border: #ffe411 1px solid;
    background: none;
}
.header-item-active {
    line-height: 30px;
}
.post-creator .content-wrap {
    background: #000;
    border: #ffe411 1px solid;
}
.post-creator .content-wrap textarea {
    color: #fff;
}
.user-profile,
.menu-vertical,
.post-creator-body {
    background: #222;
}
.user-profile-main-screen-name,
.user-profile-main-verify {
    color: #fff;
}
.operation-wrap .btn {
    color: #fff;
}
.operation-wrap .btn-primary {
    color: #ffe411;
}
.operation-wrap .btn svg {
    color: #ffe411;
}
.message-link-body {
    background: #2a2a2a;
    color: #fff;
}
.message-card-body .message-link {
    border: 1px solid #4e4e4e;
}
.readable-content {
    color: #fff;
}
.message-link-right {
    background: #4e4e4e;
}
.topic-list {
    background-color: #222;
}
.topic-card {
    background: none;
}
.topic-card .topic-card-title {
    color: #aaa;
}
.topic-card .topic-card-title:hover {
    color: #ffe411;
}
.topic-list .topic-card-thumbnail {
    width: 0;
    height: 0;
    display: none;
}
.user-profile-background,
.user-profile-thumbnailUrl {
    width: 0;
    height: 0;
    display: none;
}
.search-wrap,
.search-wrap input,
.search-wrap div {
    background-color: #333;
    color: #fff;
}
.message-card-header-main .topic-extra .message-user > a,
.user-name {
    color: #ffe411;
}
.user-activity {
    background-color: #333;
}
.user-activity-repost-card,
.user-activity-comment-message {
    background-color: #444;
    border: none;
}
.user-activity-repost-card:hover,
.user-activity-comment-message:hover {
    background-color: #555;
}
.feed-root .feed-nav-container {
    padding: 10px 0 5px;
    background-color: #000;
}
.user-activity-post-link,
.user-activity-post-link:hover {
    background-color: #444;
    border: none;
}
.user-activity-post-link p {
    color: #fff;
}
.feed-root .feed-nav,
.feed-root .feed-nav-tip {
    background: #3a3a3a;
    border: none;
}
.feed-root .feed-nav-item a.active,
.feed-root .feed-nav-item a.active:after {
    color: #ffe411;
}
.comments-timeline {
    background-color: #222;
}
.comments-timeline, .comments-timeline-container {
    border-top: 1px solid #4c4c4c;
}
.comment-card-header-screenname {
    color: #ffe600c4;
}
.btn-link {
    color: #d4d4d4;
    background: transparent;
}
.comments-timeline-container-title {
    color: #ffffff;
}
.comment-card-main {
    border-bottom: .5px solid #4a4a4a;
}
.feed-root .feed-nav-item a.active:after {
    background: #ffe600;
}
.user-menu {
    background: #000;
}
.sub-comment-timeline {
    background-color: #333333;
}
/* 子页面 */
.user-card {
    background: #222;
}
.user-card-title {
    color: #fff;
}
.user-follow-default {
    color: #777;
}
.user-profile-main-stats {
    color: #aaa;
}
}
