css:
@-moz-document domain("okjike.com") {
body {background:#272c35}
.feed-root .feed-nav-container {background:#272c35}
.feed-root .feed-nav {background:none; color:#c8cacf}

/*顾大夫OJBK，有病就用春雨医生*/

/*导航*/
.header-item  {color:#ffe411}
.site-header {background:#20252c; }
.site-header {box-shadow:0 1px 4px 0 hsla(205, 28%, 2%, 0.5)}
.header-item-active {background:#ffe411;color:#272c35}
.site-header .brand-name {color:#ffe411}
.site-header .brand-beta {color:#cbbf60}
.feed-root .feed-nav-tip {border-radius:10px; border:none;}
.feed-root .feed-nav-item a.active {color:#fff}

/*卡片*/
.message-card-container {background:#272c35}
.message-card {background:#1c2129; border-radius:5px;}
.message-card-header-main .row span {color:#bbbfc6}
.message-card-header-main .topic-name {color:#ffe411}
.readable-content-collapse {color:#b0b2b4}
.message-card-topic-img {border-radius:50px;}
.message-link {background:#272c35;}
.message-link:hover {background:#2a2f39;}
.user-activity-post-link {background:#272c35}
.readable-content-collapse span {color:#fff}
.comments-timeline {background:#2f3541;}
.comments-timeline .comment-card-header span {color:#ffe411}
.comments-timeline-container-title,.comments-timeline-more is-flex middle-xs center-xs {color:#fff;}
.comment-card-main {border-bottom:#445763 1px dashed;}
.btn-link{color:#b0b2b4}
.btn-primary.btn-disabled {background:#59c036;}
.comments-timeline, .comments-timeline-container{border-top:1px solid #445763;}
.sub-comment-timeline{background:#272c35}

/*悬停卡片*/
.popover-content {box-shadow:none}
.popover-inner {background:#2b2b2b;border:1px solid }
.topic-card-thumbnail {border-radius:50px;}
.topic-subscribe-danger, .topic-subscribe-default{background:#ffe411}

/*推荐关注*/
.topic-list {background:#1c2129;}
.topic-list-title {color:#ffe411}
.topic-card {background:none;}
.topic-card-title {color:#bbbfc6}
.topic-card-title:hover {color:#ffe411!important}
.topic-list .topic-card-thumbnail { border-radius:40px; }

/*用户名*/
.message-card-header-main .topic-extra .message-user a { color:#528541 }

/*左侧边栏*/
.user-profile-main { background:#1c2129;color:#bbbfc6}
.user-profile  { background:#1c2129 }

.menu { background:#1c2129;color:#bbbfc6 }
.menu-item>a { color:#bbbfc6 }
.menu-item>a:hover { background:#528541; color:#bbbfc6 }
}
