css:
@-moz-document domain("zdfans.com")
{
/* i really want this to be global */
/*分类列表页*/

/*去除原样式*/
.column2 {
    padding-left: 0;
}
ul.excerpt {
    margin-right: 0px!important;
}

/*内容部分样式调整*/
.content {
    border: none;
    box-shadow: none;
}

/*内容列表的li样式调整*/
.excerpt li {
    padding: 30px 40px;
    border-bottom: none;
}

/*图片高度、外间距调整*/
.excerpt .pic {
    width: 100px;
    margin-right: 40px;
    border: none;
    box-shadow: none;
}

/*图片尺寸、圆角*/
.excerpt .pic img {
    width: 100px;
    height: 100px;
    border-radius: 50px;
}

/*分页部分样式调整*/
.paging,
.pagenav {
    padding: 30px 40px 30px;
}
.paging li a,
.paging li span,
.pagenav li a,
.pagenav li span {
    padding: 10px 15px;
    background: #00abc5;
    color: #fff;
    border: none;
    border-radius: 5px;
    font-size: 18px;
    float: left;
    box-shadow: none;
}

/*隐藏底部的友情链接等内容*/
.friendlink,
.copyright {
    display: none;
}

/*调整内容顶部距离*/
#hasfixed {
    margin-top: 130px;
    margin-bottom: 50px;
}

/*调整导航hover样式*/
.menu-item a {
    padding: 10px 20px;
}
.menu-item a:hover {
    background: #fff;
    color: white;
}
.menu-item:hover a {
    background-color: #00abc5;
    padding: 10px 20px;
    font-size: 16px;
    border-radius: 0;
}
.menu-item:hover a:hover {
    background: #0096ad;
    font-size: 16px;
}
.sub-menu {
    border-radius: 0 0 4px 4px;
    background-color: transparent;
    padding-bottom: 0;
    padding-left: 0;
}

.sub-menu li a {
    width: 110px;
}
.sub-menu li a:hover {
    border-left: none;
}

/*调整搜索按钮圆角*/
.search-input {
    border-radius: 20px 0 0 20px;
    width: 315px;
    text-indent: 1rem;
}

/*搜索按钮调整大小调整，便于点按*/
.search-submit {
    padding: 0 20px;
}

/*header高度、背景色调整*/
.header {
    height: 80px;
    background-color: #0096ad;
    box-shadow: 0rem 0.25rem 0.25rem rgba(0, 171, 197, 0.25), 0rem 0.5rem 0.5rem rgba(0, 171, 197, 0.25), 0rem 1rem 1rem rgba(0, 171, 197, 0.25), 0rem 2rem 2rem rgba(0, 171, 197, 0.25);
}


/*调整搜索按钮高度*/
.search-input {
    padding: 10px 8px;
}
.search-submit {
    height: 40px;
}

/*调整垂直居中*/
.search-form {
    float: right;
    margin: 20px 0 0 5px;
}

/*logo外间距调整*/
.logo {
    margin: 21px 0 0 20px;
}
.nav {
    margin: 20px 20px 0 0;
}

/*去除侧边栏边框*/
.widget_categories,
.widget_pages,
.widget_d_hotpost,
.widget_calendar,
.widget_recent_entries,
.widget_train_get {
    border: none;
}

/*调整分类目录的行高*/
.widget_bulletin li,
.widget_d_readers li,
.widget_recently_viewed li,
.widget_links li,
.widget_archive li,
.widget_categories li,
.widget_pages li,
.widget_recent_entries li {
    height: 40px;
    line-height: 40px;
}
.widget_categories {
    background: none;
    padding: 0;
}
.widget.widget_categories a {
    background: #0096ad;
    color: white;
    padding: 5px 10px;
    text-decoration: none;
}
.widget.widget_categories a:hover {
    background: #00abc5;
    color: #fff;
    padding: 5px 10px;
    text-decoration: none;
}
.sub-menu {
    border-radius: 0;
    box-shadow: 0rem 0.25rem 0.25rem rgba(0, 171, 197, 0.25), 0rem 0.5rem 0.5rem rgba(0, 171, 197, 0.25), 0rem 1rem 1rem rgba(0, 171, 197, 0.25), 0rem 2rem 2rem rgba(0, 171, 197, 0.25);
}

/*调整侧边栏标题的字体大小*/
.widget_tit {
    font-size: 18px;
    padding: 20px 0;
    border: none;
}

/*将侧边栏与主要内容分隔*/
.widget {
    margin: 0 0 15px 20px;
}

/*是指排行榜最大高度，超过则滚动显示*/
.widget.widget_views-plus ul {
    max-height: 1300px;
    overflow: scroll;
}
.widget.widget_views-plus li {
    padding: 10px 0;
}
.widget.widget_views-plus li {
    padding: 10px 30px;
    margin: 15px 0;
    background: #fafafa;
    border-radius: 5px;
}

/*修改滚动条*/
::-webkit-scrollbar {
    width: 5px;
    height: 10px;
}
::-webkit-scrollbar-thumb {
    border-radius: 10px;
    background-color: #0096ad;
}
::-webkit-scrollbar-button {
    color: #f4f4f4;
}

/*隐藏分享组件*/
.bdshare-slide-button-box.bdshare-slide-style-r6 {
    display: none;
}




/*各列表页标题调整*/
.content.column2 h1.base-tit.queryinfo {
    padding: 30px 40px;
    border: none;
    box-shadow: 0 0 20px 2px rgba(0, 0, 0, .1);
    -webkit-box-shadow: 0 0 20px 2px rgba(0, 0, 0, .1);
    -moz-box-shadow: 0 0 20px 2px rgba(0, 0, 0, .1);
    margin: 0;
}




/*详情页*/

/*详情页导航的标题文字的样式调整*/
.base-tit {
    font-size: 18px;
    padding: 30px 0;
    border-bottom: none;
}
.meta {
    padding: 20px 0;
    border: none;
}

/*详情页内容的周围间距*/
.single .content,
.page .content {
    padding: 0 40px;
}

/*详情页标题样式调整*/
.meta-tit {
    color: #fff;
    background: #0096ad;
    padding: 10px 20px;
    border-radius: 5px;
    display: inline-block;
}

/*文章信息标签的样式调整*/
.meta span {
    background: #f4f4f4;
    padding: 5px 10px;
    border-radius: 5px;
    margin-right: 1rem;
    font-size: 16px!important;
    line-height: 3rem;
}
.entry div {
    line-height: 3rem;
}

/*图片样式调整*/
.entry img {
    max-width: 800px;
    border-radius: 5px;
    margin: 1rem 0;
}

/*修补原样式造成的标签间距不同*/
span.view {
    margin-left: 0;
}

/*详情页内链接样式调整*/
.entry a {
    background: #f4f4f4;
    padding: 10px 15px;
    border-radius: 5px;
    line-height: 3rem;
}

.post-related li {
    padding: 0 20px;
    box-sizing: border-box;
}
.post-related {
    margin: 30px 0;
    border: none;
}
ul.clearfix {
    padding: 20px;
}

/*标题样式修改*/
.entry h2,
.entry h3,
.entry h4 {
    color: #111;
    border-bottom: none;
    margin: 30px 0;
    padding-left: 1rem;
    border-left: 3px solid #00abc5;
}


/*评论区样式修改*/
#SOHUCS #SOHU_MAIN .module-cmt-header .section-cbox-w .block-head-w .header-login {
    width: 80px;
    height: 40px;
    border-radius: 42px;
    color: #fff;
    background: #00abc5;
    margin-bottom: 10px;
}
#SOHUCS #SOHU_MAIN .module-cmt-list .cmt-list-type .type-lists .active {
    background: none;
}
#SOHUCS #SOHU_MAIN .module-cmt-list .cmt-list-type .cmt-list-border {
    background: none;
}
#SOHUCS #SOHU_MAIN .module-cmt-list .cmt-list-type {
    height: auto;
}
#SOHUCS #SOHU_MAIN .module-cmt-list .cmt-list-type .type-lists .active {
    border: none;
    background: #00abc5;
    color: white;
    padding: 10px 30px;
    border-radius: 60px 5px 60px 5px;
}
#SOHUCS #SOHU_MAIN .module-cmt-list .cmt-list-type .cmt-list-number .comment-number,
#SOHUCS #SOHU_MAIN .module-cmt-list .cmt-list-type .cmt-list-number .comment-number .cy-number {
    color: #00abc5;
}
#SOHUCS #SOHU_MAIN .module-cmt-box .post-wrap-w .post-wrap-border-t .post-wrap-border-t-l {
    background: none;
}
#SOHUCS #SOHU_MAIN .module-cmt-box .post-wrap-w .post-wrap-border-l {
    background: none;
}
#SOHUCS #SOHU_MAIN .module-cmt-box .post-wrap-w .post-wrap-border-t .post-wrap-border-t-r {
    border-top: none;
}
#SOHUCS #SOHU_MAIN .module-cmt-box .post-wrap-w .post-wrap-border-r {
    background: none;
}
#SOHUCS #SOHU_MAIN .module-cmt-box .post-wrap-w .post-wrap-main {
    border-bottom: none;
}
#SOHUCS #SOHU_MAIN .module-cmt-box .post-wrap-w .post-wrap-main .wrap-area-w {
    padding: 10px 10px 0 10;
    background: #0096ad;
    border-radius: 5px;
}
#SOHUCS #SOHU_MAIN .module-cmt-box .post-wrap-w .post-wrap-main .wrap-area-w .textarea-fw {
    color: #fff;
}


#SOHUCS #SOHU_MAIN .module-cmt-list .block-cont-gw {
    border: none;
    background: #f4f4f4;
    margin: 30px 0;
    padding: 20px;
    border-radius: 5px;
}
#SOHUCS #SOHU_MAIN .module-cmt-list .wrap-build-gw .build-floor-gw {
    padding: 4px 4px 6px;
    border: none;
    background-color: #fff;
    position: relative;
    z-index: 2;
}
span.user-level-gw.user-1-4-gw.user-1-gw,
span.user-level-gw.user-1-4-gw.user-2-gw,
span.user-level-gw.user-1-4-gw.user-3-gw,
span.user-level-gw.user-1-4-gw.user-4-gw,
span.user-level-gw.user-1-4-gw.user-5-gw,
span.user-level-gw.user-5-6-gw.user-6-gw,
span.user-level-gw.user-5-6-gw.user-5-gw,
span.user-address-gw,
span.prop-span,
.images-slider {
    display: none!important;
}
#SOHUCS #SOHU_MAIN .module-hot-topic .topic {
    display: block;
    float: left;
    margin: 20px 0 40px 0;
    width: 310px;
}

/*删除热门话题等*/
.topic,
#SOHUCS #SOHU_MAIN .module-cmt-footer,
.bdshare-slide-button-box.bdshare-slide-style-r6 {
    display: none!important;
}
}
