css:
@-moz-document domain("overbuff.com") {
/* Advertisements */
.vm-skin.vm-skin-left, .vm-skin.vm-skin-right, *[id^="kunkka"], li.social, .wukong {
    display:none;
}

body {
    color:rgb(237, 239, 245) !important;
    font-family: "Segoe UI" !important;
    font-size: 16px !important;
    background-color: hsl(225, 9%, 10%) !important;
    background-image: url("https://upload.wikimedia.org/wikipedia/commons/c/ca/1x1.png") !important;
    padding-top:0px !important;
}

* {
    transition: all .1s ease-in-out;
}

.container {
    background-color: hsl(225, 9%, 10%) !important;
    max-width: 100%;
}

.u-align-right-desktop {
    position: absolute;
    right: 0px;
}

/* Header */

.layout-nav {
    width:100%;
    height: 48px;
    position:absolute;
    top:0px;
    left:0px;
    background: rgba(49, 51, 59, 1);
}

.layout-nav .layout-nav-left a.layout-nav-logo {
    height: 48px;
    display: flex;
    align-items: center;
    background: rgb(233, 153, 45);
    margin-right: 0px;
    padding: 0px 20px;
    text-decoration: none;
    text-transform: uppercase;
    font-family: Big Noodle Too,Futura,Blizzard,Arial,Helvetica,sans-serif;
    font-size: 150%;
    font-style: italic;
    color: #fefefe;
}

.layout-nav .layout-nav-left a.layout-nav-logo:hover {
    background: rgb(233, 193, 93) !important;
}

/* Search Player Box */

.layout-nav .layout-nav-right .layout-nav-session .layout-nav-search {
    margin-right: 0px;
}

.layout-nav-search input[type=search] {
    background: hsl(0, 0%, 11%);
    border: 0;
    color: #fefefe;
    margin: 0;
    padding: 13px 20px;
    border-radius: 0px !important;
}

.layout-nav-search .search-form button {
    background: hsl(0, 0%, 11%);
    padding: 14px 17px;
}

/* Notifications */
.layout-notification {
    background: rgba(49, 52, 59, 0);
    color: #737c84 !important;
    border: 0px dotted #404b62;
    border-radius: 0px !important;
    box-shadow: 0 0 0px #242d44;
    font-family: "Segoe UI";
    font-size: 13px;
    text-align: center;
    padding: 20px 0px;
    color: #fefefe;
    margin-bottom: 0px;
    width: 100%;
    position: absolute;
    top: 48px;
    left: 0px;
}

.layout-notification:hover {
    background:hsla(240, 6%, 15%, 1);;
    color: white !important;
    cursor:pointer;
}

/* Header Links */
.layout-nav .layout-nav-left .layout-nav-links .link a,
.layout-nav .layout-nav-right .layout-nav-session .layout-nav-user a,
section header aside a,
article .filter .filter-group .filter-option {
    height: auto;
    color: white;
    background: rgba(65, 130, 183, 1);
    display: inline-block;
    align-items: inherit;
    padding: 14px 20px 15px 20px;
    margin: 0px;
    border-left: 1px solid hsla(207, 48%, 54%, 1);
    text-transform: none;
    text-decoration: none;
    font-size: 14px;
    outline: none;
}

.layout-nav .layout-nav-left .layout-nav-links .link a:hover,
.layout-nav .layout-nav-right .layout-nav-session .layout-nav-user a:hover,
section header aside a:hover,
article .filter .filter-group .filter-option:hover {
    color:white;
    background-color: rgb(79, 155, 216) !important;
}

a.color-brand {
    background: rgb(233, 153, 45) !important;
    color: #fefefe !important;
    border-left: 1px solid hsla(34, 81%, 65%, 1) !important;
}

.layout-nav-user {
    height:48px !important;
}

a.signed-in {
    padding:0px !important;
}

.layout-nav-user a.signed-in .player-name {
    float: left;
    padding: 14px;
}

.layout-nav-user img {
    border-radius: 0px;
    box-shadow: 0 0 0px transparent;
    border: 0px solid transparent;
    width: 48px;
    float: right;
    margin-left: 0px !important;
}

/* Content */

.layout-content {
    padding-top:106px;
    padding-bottom:80px;
    max-width:1280px;
    margin-left:auto;
    margin-right:auto;
}

/* Player Banner */
.layout-header,
section {
    background: rgba(40, 42, 48, 1);
    box-shadow: 0px 2px 0px #ffffff24;
    margin-bottom: 12px;
    border-radius: 0px;
}

.layout-header .layout-header-primary {
    padding: 0px;
    padding-right: 10px;
    padding-bottom: 0px;
    margin-bottom: -3px;
}

.image-avatar {
    border: 0px solid #242d44;
    border-radius: 0px;
    margin: 0px;
    width: 128px;
}

.layout-header .layout-header-primary .layout-header-primary-avatar {
    vertical-align: top;
    margin-right: 20px;
}

.layout-header-primary-bio h1 {
    font-size: 300%;
    padding-top: 12px;
}

/* Last Updated & Played */
.layout-header .layout-header-primary .smaller:nth-child(2) {
    font-size: 90%;
    text-transform: none;
    position: absolute;
    right: 0px;
    top: 91px;
    background: rgb(27, 27, 27);
    padding: 10px 20px;
}

.layout-header .layout-header-primary .smaller:nth-child(3) {
    font-size: 90%;
    text-transform: none;
    position:absolute;
    right: 0px;
    top: 0px;
    background: rgba(27, 27, 27, 0);
    padding: 10px 20px 10px 50px;
}

.layout-header .layout-header-primary .smaller:nth-child(3) i.fa {
    color: #c4d4e6 !important;
    padding: 12px 16px 12px 16px;
    position: absolute;
    top: 0px;
    left: 0px;
}

.layout-header .layout-header-primary .smaller:nth-child(3) i.fa:hover {
    background: rgba(79, 155, 216, 0.17);
}

.layout-header .layout-header-primary .smaller:nth-child(4), .layout-header .layout-header-primary .smaller:nth-child(5) {
    display:none;
}



/* Player Banner: On Fire & Wins */

.layout-header .row:nth-child(1) .columns:nth-child(2) {
    position: absolute;
    left: 140px;
    top: 64px;
}

.layout-header .layout-header-secondary {
    display: block;
    text-align: right;
    margin-top: 0px;
    margin-right: 0px !important;
    float:right;
}

.layout-header .layout-header-endorsement {
    position:relative;
    float:left
}

dl dd {
    color: #fefefe;
    font-size: 225%;
    font-weight: 100;
    font-family: Big Noodle Too;
}

dl dt {
    color: #9daac8;
    font-size: 10px;
    display: block;
    font-family: "Segoe UI";
    font-style: normal;
    text-transform: uppercase;
    text-align: left;
    margin-top: -5px;
}

section header {
    height: 42px;
    display: flex;
    flex-flow: row nowrap;
    align-items: center;
    color: #fefefe;
    background: rgba(57, 60, 69, 1);
    font-size: 1.4em;
    font-weight: 100;
    text-transform: none;
    padding: 7px 15px;
}

section article {
    padding: 0px;
}

section header aside a {
    padding: 19px 24px;
    margin-right: -15px !important;
}

div.column, div.six {
    width: auto !important;
}

/* Banner Links */
.layout-header .layout-header-nav {
    background: hsla(225, 29%, 92%, 1);
    border-radius:0px !important;
}

.layout-header .u-inline-list li a {
    padding: 10px 20px !important;
    font-size: 16px !important;
    font-family: "Segoe UI";
    font-weight: 600;
    color: rgba(49, 51, 59, 1) !important;
    background: rgba(237, 239, 245, 1) !important;
    border-radius: 0px !important;
    opacity: 1 !important;
    border-right: 1px solid hsla(225, 29%, 90%, 1);
}

i.fa.fa-user-secret {
    opacity:1 !important;
}

.layout-header .u-inline-list li a:hover {
    background: white !important;
}

/* Filters */

article .filter .filter-group {
    display: inline-block;
    margin: 0px;
}

article .filter .filter-group .filter-option {
    padding: 10px 20px;
    border: 0px solid black;
    font-size: 12px;
    border-left: 1px solid #518fc2
}

article .filter .filter-group .filter-option.selected, article .filter .filter-group .filter-option.selected:hover {
    color: #fefefec9 !important;
    background: hsla(207, 48%, 35%, 1) !important;
    border: 0px solid #fefefe;
}


/* Most Played Heroes */
.player-hero {
    border-bottom: 2px solid #3f3f42;
    margin: 0px;
    padding: 15px;
    vertical-align: top;
    margin-bottom: 0px;
    background-image: linear-gradient(rgba(0, 0, 0, 0.05),rgba(255, 255, 255, 0.05)) !important;
}

.player-hero img.image-hero {
    width:90px;
    height:auto;
}

.player-hero .grouping .group .name {
    font-weight: 100 !important;
    text-decoration: none;
    text-transform: none !important;
    font-family: Big Noodle Too;
}

.player-hero .grouping .group .name a {
    font-size: 2em;
}

.player-hero .grouping .group .stat {
    margin-top: 8px;
    margin-bottom:4px
}

/* Label */
.player-hero .grouping:not(.header) .group .stat .label:nth-child(3) {
   /* position: relative;
    top: -32px;*/
    color: #8a93a7;
    font-size: 12px;
}

/* Value */
.player-hero .grouping:not(.header) .group .stat .value {
    /*position: relative;
    top: 6px;*/
    color: #dbdbdb;
    font-size: 14px;
}

/* Meters */
.player-hero .grouping:not(.header) .group .stat .label:nth-child(2) {
   /* position: relative;
    top: 7px;*/
    padding-top:4px;
    padding-bottom:4px;
}

.bar {
    background: rgba(8, 8, 8, 0.35);
    cursor: default;
    font-size: 0;
    height: 6px;
    line-height: 0;
    padding: 0px;
    text-align: left;
    width: 95%;
}

.bar.bar-dots .segment, .bar .segment {
    display: inline-block;
    height: 6px;
    background: hsla(207, 48%, 69%, 1) !important;
    border-left: 0px solid hsla(207, 48%, 59%, 1);
}

.bar:hover {
    background:#000 !important;
}

.bar:hover .segment {
    background:rgb(255, 250, 178) !important;
    border-left:0px solid hsl(56, 100%, 45%) !important;
}

.stat .tooltip-inner {
    background-color: rgb(23, 23, 28);
    border-radius: 0px;
    color: rgb(237, 239, 245);
    padding: 14px;
    position: relative;
    top: 46px;
    text-align: left;
    white-space: nowrap;
}

.player-medals {
    padding-bottom:10px;
}

.chart {
    padding-top: 10px;
}

/* Player Charts */

.player-hero .grouping .group .stat .chart {
    height: 50px;
    margin: 0px;
}

rect.highcharts-point {
    fill: hsl(207, 0%, 33%);
    width: 7px;
}

rect.highcharts-point:not([fill="#242D44"]) {
    fill:rgb(233, 153, 45);
}

rect.highcharts-point.highcharts-point-hover {
    fill: rgb(79, 155, 216) !important;
}

/* Floater Text */

/*.highcharts-label.highcharts-tooltip.highcharts-color-undefined {
    opacity:1 !important;
    top:0px !important;
}*/

.highcharts-label.highcharts-tooltip.highcharts-color-undefined span {
    background: rgb(23, 23, 28);
    color:  rgb(237, 239, 245)!important;
    padding: 14px;
    width: 147px !important;
    position: absolute !important;
    top: -66px !important;
    left: -7px !important;
    font-size: 12px !important;
    font-family: "Segoe UI" !important;
    font-weight: 600;
}

g.highcharts-label.highcharts-tooltip.highcharts-color-undefined {
    /* Hide the awful backdrop they had */
    display:none !important;
}

/* Footer */

.layout-footer {
    margin: 0px !important;
    background: hsl(220, 10%, 17%);
    height: 100px;
    text-align: center;
}

.layout-footer .container {
    display:inline-block;
    flex: none !important;
    background: transparent !important;
}

.layout-footer .links {
    flex: none !important;
    position: relative;
    bottom: 0px;
}

.layout-footer .container .links ul li a {
    padding: 14px 20px;
    background: #17171c;
}

.layout-footer .container .links ul li a:hover {
    background: #393c45;
}

.layout-footer .legal {
    flex: none !important;
    position: relative;
    bottom: -15px;
}
}
