css:
@-moz-document domain("mon.compte-nickel.fr") {
/* #1c1f26; */

.replace-card {visibility: hidden; }


body {
    background-color: #1c1f26 !important;
}

.panelLastConnection {
    background-color: #1c1f26 !important;
}

.logo-account {
    background-color: #1c1f26 !important;
}

.table-cell {
    color: #aaa !important;
    background-color: #1c1f26 !important;
}

td:nth-of-type(1), td:nth-of-type(2), td:nth-of-type(3), td:nth-of-type(4), td:nth-of-type(5), td:nth-of-type(6), td:nth-of-type(7), td:nth-of-type(8) {
    color: #aaa !important;
    background-color: #252931 !important;
}

.header {
    color: #aaa !important;
    background-color: #1c1f26 !important;
}

.operation-table-row {
    color: #aaa !important;
    background-color: #1c1f26 !important;
}

.operation-table-alternate-row {
    color: #000 !important;
    background-color: #1c1f26 !important;
}

.ui-corner-all, .font-bold.uppercase, .font-orange {
    color: #aaa !important;
    background-color: #1c1f26 !important;
}

.content-bloc-rib, div:nth-of-type(2) {
    color: #aaa !important;
    background-color: #1c1f26 !important;
}


* {
    transition: all .1875s!important;
}

.moto {
    color: #aaa !important;
    background-color: #1c1f26 !important;
    font-size: .75em;
    margin-top: 20px;
}

.header .tiles .tile {
    color: #aaa !important;
    background-color: #1c1f26 !important;
    height: 22px;
}

.headerMenu {
    color: #aaa !important;
    padding: 0!important;
}

#MainContent_panelFilters div:first-child {
    color: #aaa !important;
    width: auto!important;
}

.header .tiles .tile .img,
.box_headerMenu {
    display: none;
}

.tile .text {
    margin-top: 7px;
}

.nickel-slider .slide {
    background-image: url('http://p45m.fr/stylish/nickel-blue/1.jpg')!important;
}



.title .table-cell:first-child > div {
    width: 7em;
}


.header {
    min-height: 90px;
    box-shadow: none;
}

.sms-infos ul {
    list-style-image: none;
    list-style-type: none;
}

.sms-infos ul li::before {
    content: ">";
    padding-right: 2px;
}

.page-title,
.orange-button,
.cc-activate .title,
.operation-table thead tr th div,
.cc-activate a,
.orange-big-button,
.nickel-btn-full-hbordered,
.ui-widget-header,
.nickel-bg-color,
.box-button,
.ui-dialog .ui-state-default,
.nickel-slider .slide,
.ui-dialog .ui-widget-content .ui-state-default,
.ui-dialog .ui-widget-header .ui-state-default,
#panelHeaderConnectionStatus,
.nickel-btn,
#panelHeaderConnectionStatus *,
.accordionTitle,
.nickel-box-c-est-pour-ca .nickel-box-footer,
.status-container .step.active,
.step-index,
.fc-state-highlight,
.orange-clickable-panel,
#alertes .head .table-cell,
.toggle-soft .toggle-slide.active {
    color: #aaa !important;
    background-color: #000000!important;
}

.orange-big-button {
    color: #aaa !important;
    border: 2px solid #000000;
}

.form .table-row .table-cell.kyc-top-border {
    color: #aaa !important;
    border-top: 1px solid #000000;
}

.HeaderConnectionStatus {
    border-bottom: #000000!important;
}

.nickel-btn-full-hbordered:hover,
.header .tiles .tile,
.box-button,
.ui-widget-header,
.orange-button,
.ui-dialog .ui-state-default,
.ui-dialog .ui-widget-content .ui-state-default,
.ui-dialog .ui-widget-header .ui-state-default {
    color: #aaa !important;
    border: 1px solid #000000;
}

.nickel-particulier-color,
.fc-grid .fc-day-number,
.fc-grid .fc-day-name,
.alert-info-content,
.nickel-color,
.font-orange,
.orange-text,
.ope-type-choice label.selected,
.xbbcode-orange-nickel,
.headerSubMenu a:hover,
a:active,
.header .tiles .tile:hover *,
.nickel-list.li-color,
.form .table-row .table-cell:first-of-type {
    color: #aaa !important;
    color: #000000!important;
}

.fc-state-highlight .fc-day-number,
.fc-state-highlight .fc-day-name,
.nickel-btn.inverted,
.status-container .step.active h2 {
    color: #aaa !important;
}

.nickel-btn-full-hbordered:hover,
.nickel-btn:hover {
    color: black;
}
}
