css:
@-moz-document url-prefix("https://2sucres.org") {

/* ==UserStyle==

@name 2Sucres Hiver 2018
@version 1.0.1
@description 2sucres sous un autre angle
@author PEUPLE

==UserStyle== */
.ms-header-primary {
    background-color: #020b36 !important;
}

.ms-navbar-primary {

    background-color: #020b36 !important;
}

.navbar-collapse {
    background-color: #020b36 !important;
}

.sb-site-container {
    background: url(https://wallpaperstudio10.com/static/wpdb/wallpapers/1920x1080/184455.jpg);
}



.md-input-container {
    background-color: #020b36!important;
}

.topic-message.mt-2.no-ml.no-pb.no-pl.no-pr.no-pt.no-mr.layout-column {

    border-color: #020b36!important;
    border-width: 5px;
}
.forum-view .topic-title,
#bot-pagi {
    height: 32px;
}

.forum-view .topic-title {
    text-align: center;
    padding: 5px 0px 5px 0px;
}
.list-topics {

    background-color: #020b36!important;
    border-width: 5px;
}

.topic-name,
.topic-user,
.topic-nb-message,
.topic-connected-count,
.topic-date {

    color: #cfc8ff!important
}


.list-topics > tbody > tr {
    background-color: #020b36;
}
.list-topics > tbody > tr.even {
    background-color: #08123e;
}



.list-topics-container .md-toolbar-tools,
div.col-xs-12.pl-1.pr-1,
._md,
#top-pagi-navig {
    background: none !important;
    color: #fff !important;
}
.list-topics > thead > tr {
    background-color: #020b36!important;
}

.md-ink-ripple {

    background-color: #020b36;
}

.topic-search-select,
.ng-pristine.ng-valid.md-input {
    background-color: #020b36;
}

#forum-container {
    background: none;
    font-family: geneva, sans-serif !important;
    font-size: small;
    font-weight: 600;
}


#form-send-new-message {

    background-color: #020b36;
    color: #fff
}

.md-toolbar-tools,
div.risibank-menu.col-xs-12.pt-1.pb-1 {



    background-color: #020b36!important;
}


.ms-footer {
    background-color: #020b36!important;
}

.forum-aside-content {
    background-color: #020b36;
}



.one-topic .topic-name a {
    color: #cfc8ff;
}

.one-topic .topic-name a:visited {
    color: rgb(115, 115, 130);
}

.one-topic:hover .topic-name a,
.one-topic:hover .topic-date a {
    color: #000;
}

.one-topic:hover .topic-name a:visited,
.one-topic:hover .topic-date a:visited {
    color: #2f0000;
}

.list-topics > tbody > tr.even:hover {
    background-color: #b3e3f4;
}

.list-topics > tbody > tr:hover {
    background-color: #7189aa;
}

textarea,
.topic-message {
    color: rgb(119, 177, 224) !important;
}
input,
blockquote,
.topic-title {
    color: rgb(119, 177, 224)!important;
    background-color: #040e3e;
}



.forum-view .topic-title,
#bot-pagi {
    color: #fff
}

#message-container {

    background-color: #020b36;
}

article.row.topic-message.mt-2.no-ml.no-mr.odd {
    box-shadow: 0px 0px 10px 2px #fff;
    background-color: #020b36;
    border: none;
}
article.row.topic-message.mt-2.no-ml.no-mr {
    background-color: #152158;
    border: none;
    box-shadow: 0px 0px 10px 2px #fff;
}

.user-pseudo-0 {
    color: rgb(255, 255, 255) !important;
}

.topic-message.message-preview.smoothy-2.mt-2.no-ml.no-mr {
    background-color: #020b36 !important;
    border-radius: 0%;
    border: 1px solid #fff;
}

.md-list-item-inner.ng-binding,
.poll-title {
    color: rgb(119, 177, 224) !important;
    background-color: #020b36!important;
    font-weight: bold;
    text-align: center;
    padding: 5px 0px 0px 10px;
}

* {
    cursor: url('https://i.postimg.cc/prXQDWbr/image.png'), default!important ;
 }
a,
a * {
    cursor: url('https://i.postimg.cc/sXZxqbYm/image.png'), pointer!important ;
 }
  
}
