css:
@-moz-document domain("ukitap.com")
{
body {
    background: #111c2b;
}

#main {
    margin-top: 20px;
    background: #565c73;
    color:#c6ced7 !important;
}

#header,
#aside-m div:nth-child(1), 
#content-um,
#mright div:nth-child(1),
#aside-m div,
#mright div:nth-child(3), 
.mrList a:hover, 
.mrList span:hover,
.yazar_kitap_listesi,
#frmYorumEkle div {
    background: #363c44 !important;
}

#frmYorumEkle div:nth-child(1),
#frmYorumEkle div:nth-child(2),
#frmYorumEkle div:last-child,
#aside-m div:nth-child(6) {
    background: transparent !important;
}

#main h1 a,
#mright div:nth-child(1) a, 
.ui-widget-content a, 
#content-m h1 a,
#content-um h1 a,
#content-um ol a, 
#content-um div p a, 
#content-um div ul li a,
#content-um .table-a a,
#mleft ul.leftMenu > li a,
.ukitip_icerik a,
.dotTable td #tumkitaplarimi_goster,
#content-um ul li[style="font-weight:bold"],
.mybg a,
#content-m #ilan a {
    color:#4aa4f1 !important;
}

.table-softblue td a {
    color: #4aa4f1 !important;
    font-weight: bold;
    font-size: 14px;
}

.kitap-listesi a > img, 
#home_istektekiler a > img, 
#home_takastakiler a > img, 
#home_satistakiler a > img {
    border-color:#b5b9bb !important;
}

.kitap-listesi a > img:hover, 
#home_istektekiler a > img:hover, 
#home_takastakiler a > img:hover, 
#home_satistakiler a > img:hover {
    border-color:#4aa4f1 !important;
}

#ilan #ul-ilan-resimleri li img:hover {
    background-color:#4aa4f1 !important;
}

#main h1 a:hover,
#mright div:nth-child(1) a:hover, 
#content-m h1 a:hover, 
#content-um ol a:hover, 
#content-um div p a:hover, 
#mleft ul.leftMenu > li a:hover, 
#content-um div ul li a:hover,
#content-um .table-a a:hover,
#content-um h1 a:hover,
.teklifList .liste li div a:hover,
.ukitip_icerik a:hover,
.dotTable td #tumkitaplarimi_goster:hover,
#content-m form#yeniKonu table.formTable td div.frmdesc,
.yazar_kitap_listesi .kitap a:hover,
#content-um .table-a td .mesaj_okunmadi a:nth-child(2):hover,
#content-um .yazar_kitap_listesi div:nth-child(19) a:hover,
.mesaj a:hover,
.mesaj .header a:hover,
.bbox a:hover,
#footer a:hover,
.mybg a:hover,
#content-m #ilan a:hover,
#content-um div:nth-child(17) a[title="Muhteşem Takasçı Üye"]:hover, 
#content-um div:nth-child(17) a[title="Muhteşem  Üye"]:hover, 
#content-um div:nth-child(17) a[title="Özel  Üye"]:hover, 
#content-um div:nth-child(17) a[title="Özel Takasçı Üye"]:hover, 
#content-um div:nth-child(17) a[title="Seçkin  Üye"]:hover,
#content-um div:nth-child(17) a[title="Bölüm Yöneticisi"]:hover,
.table-softblue td a:hover
{
    color:#e5e5e5 !important;
}

#mleft ul.leftMenu > li:hover {
    background: none;
}

#content,
#ilan #ilan-ek-bilgiler,
#ilan #uye-pc {
    border: 1px solid #565c73;
    background: #363c44 !important;
}

#ilan #uye-pc {
    width: 275px;
}

#content-m p:nth-last-child(3) {
    text-align: center;
    margin-top: 15px;
    background: #565c73 !important;
}

#content-m form#yeniKonu table.formTable {
    border: 2px solid #2d2d2d;
    background: #565c73 !important;
    
}

#content-m form#yeniKonu table.formTable td {
    
    
    border-bottom: none !important;
}

#content-um div:nth-child(17), 
#content-um div:nth-child(19) {
    background: #636775 !important;
}

#content-um .yazar_kitap_listesi div:nth-child(17), 
#content-um .yazar_kitap_listesi div:nth-child(19){
    background: #474a55 !important;
    padding: 0px !important;
}

#content-um div:nth-child(17) a[title="Yeni Üye"],
#content-um div:nth-child(19) a, 
#content-um div:nth-child(19) a[title="Yeni Üye"] {
        color: #242c38 !important;
        
}

#content-um div:nth-child(17) a[title="Özel  Üye"],
#content-um div:nth-child(17) a[title="Özel Takasçı Üye"]{
        color: #1dc62b !important;
}

#content-um div:nth-child(17) a[title="Seçkin  Üye"],
#content-um div:nth-child(17) a[title="Seçkin Takasçı Üye"]{
        color: #0fb3ea !important;
}

#content-um div:nth-child(17) a[title="Normal Üye"] {
        color: #d9dde4 !important;
}

.uye-profil .bilgiler > div:nth-child(6) span,
.uye-profil .bilgiler > div:nth-child(5) span {
        color: #36393e !important;
}

.mrList a .konu,
#content-um div:nth-child(17) a[title="Yeni Üye"]:hover,
#content-um div:nth-child(19) a:hover {
    color:#fff !important;
}

.ukitip_icerik,
#content-um p,
#content-um #formdanEkle div:first-child {
    background: #565c73 !important;
    color:#a9b2c0 !important;
}

#content-um #formdanEkle div {
    background: none !important;
    color:#a9b2c0 !important;
}
#content-m, 
.ui-dialog .ui-dialog-content, 
.aside-box {
    background: #363c44;
    color:#a9b2c0 !important;
}

#content-m .table-a td, 
#content-m .table-a td span {
    background: #565c73;
    color:#a9b2c0 !important;
}

#content-m .table-a td, 
.table-a {
    border: 1px solid #2d2d2d;
}

.aside-box a,
a:hover,
.teklifList .liste li div a,
.y_mesaj .tarihsaat,
.dotTable a:hover, 
#content a:hover,
#footer a:active, 
#footer a,
#ilan #uye-pc .konum,
.table-softblue .zaman {
    color:#a9b2c0 !important;
}

#kitapdetay p.uk-azhep-goster.detay a:hover {
    color:#2d2d2d !important;
}

.uye-profil .bilgiler div {
    border-bottom: 1px solid #7a8595;
}

.logo a, 
.logo a:visited {
    background: #363c44;
    color:#a9b2c0 !important;
}

.uye-profil {
    background: #363c44;
    color:#a9b2c0 !important;
    border: 1px solid #a9b2c0;
}

.table-softblue td {
    text-align: center;
}

.table-softblue td:first-child {
    max-width: 250px;
}

.teklifList .liste li div,
.table-softblue td{
    background: #363c44;
    color:#a9b2c0 !important;
    border: 1px solid #353842;
    margin-top: 3px;
    margin-bottom: 5px;
}

.teklifList span {
    background: #6e7580 !important;
    padding-left: 10px;
}

.teklifList .liste li div:hover {
    border: 1px solid #a9b2c0;
}

#content-um ol#sss li:hover {
    border: 1px solid #a9b2c0;
    padding: 4px;
}

.teklifList span,  
.dotTable td div,
.bbox, 
#ul-ilan-kategoriler
{
    background: #565c73 !important;
    color:#a9b2c0 !important;
}

.dotTable a, 
.mrList a .yazan, 
.bbox .bbox-icon, 
#mesajTable a, 
.lightGray a, 
#mesajlarDiv a, 
#content a,
#top_navigation span, 
#top_navigation .had > ul > li > a, 
#top_navigation .had > ul > li > a:visited, 
.uye-profil a,
#aside-m a, 
.mesaj a {
    color:#4aa4f1 !important;
}

#mesajlarDiv {
    background: #565c73;
    border: 2px solid #353842;
}

#ilan #ilan-detay {
    margin: 10px;
    background: #474a55 !important;
    border: 1px solid #2d2d2d;
    color: #c6ced7 !important;
}

#ilan #ul-ilan-resimleri {
    margin-top: 10px;
}

.y_mesaj {
    border-bottom: 5px solid #353842;
}

button.ukButton, 
input.ukButton,
button.udButton, 
input.udButton{
    background: #4aa4f1;
    color: #fff;
    text-shadow: 0 -1px 0 #2770af;
    border-bottom: 2px solid #3b89cc;
}

button.ukButton:hover, 
input.ukButton:hover,
button.udButton:hover, 
input.udButton:hover{
    background: #a9b2c0;
    color: #fff;
    text-shadow: 0 -1px 0 #768088;
    border-bottom: 2px solid #85898d;
}

.top-nav li a,
#sss li, 
.lightGray{
    background:#565c73 !important;
}

.top-nav li.selected a, 
.top-nav li a:hover {
    background:#4aa4f1 !important;
    color: #2d2d2d !important;
}

.ac_over, 
.sayfalar li.bu_sayfa {
    background:#4aa4f1 !important;
    color: #fff !important;
    border: none;
}

.sayfalar li.bu_sayfa,
.sayfalar li a:hover {
    background:#4aa4f1 !important;
    color: #fff !important;
    border: 1px solid #fff
}

.dotTable, 
#content  {
    border: 1px solid #565c73;
    background: #565c73;
}

.dotTable th {
    background: #a9b2c0 !important;
    color:#2d2d2d !important;
}

.dotTable td,
.dotTable td #div_ts {
    background: #474a55 !important;
    border-bottom: none;
}

.dotTable .notum {
    background: #565c73 !important;
    color: #c6ced7 !important;
}

.dotTable .ukitip_tasiyici_alt {
    background: none !important;
    color:#2d2d2d !important;
}

.dotTable .ukitip span {
    background: none !important;
    color:#ec4d4d !important;
}

.dotTable .ukitip span:hover {
    background: none !important;
    color:#fa9527 !important;
}

#divSatisci .dotTable th:nth-child(2) {
    background: #19ae07 !important;
    color:#fff !important;
}

#divSatisci .dotTable th:nth-child(3) {
    background: #3b78e7 !important;
    color:#fff !important;
}

.ui-widget-header {
    background: #3b78e7 !important;
    color:#fff !important;
    border: none;
}

#divSatisci .dotTable th:nth-child(4) {
    background: #fa9527 !important;
    color:#fff !important;
}

.bilgi-kutusu, .mesaj_uyari {
    background: #a9b2c0 !important;
    color:#2d2d2d !important;
    margin-bottom: 5px;
    border: none;
}

.bilgi-kutusu a {
    color:#3b78e7 !important;
    font-weight: bold;
}
#sss li span,
.bilgi-kutusu a:hover,
#content-um .table-a tr.mesaj_okunmadi td a:hover {
    color:#2d2d2d !important;
    font-weight: bold;
}

#content-um .table-a td {
    background: #565c73;
    border: 1px solid #2d2d2d;
}

#content-um .table-a tr.mesaj_okunmadi td, 
#content-um .table-a tr.mesaj_okunmadi td a {
    background:#8b91a4 !important;
    color: #e5e5e5 !important;
    font-weight: bold;
}

.mesaj
 {
    background: #474a55 !important;
    border: 2px solid #2d2d2d;
    color: #c6ced7 !important;
}

#kitapdetay .detay {
    background: #474a55 !important;
    border: 1px solid #2d2d2d;
    color: #c6ced7 !important;
}

#top-mem-nav a.pointer:hover,
.yazar_kitap_listesi .kitap {
    background: #474a55 !important;
    color: #c6ced7 !important;
}

.yazar_kitap_listesi .kitap {
    background: #474a55 !important;
    border: 1px solid #a9b2c0;
}

.yazar_kitap_listesi div.kitap:hover {
    background: #474a55 !important;
    border: 1px solid #e9ecee;
}

.yazar_kitap_listesi .kitap a,
#content-um .yazar_kitap_listesi div:nth-child(19) a {
    color: #42a0e8 !important;
    font-weight: bold;
}

.kitap {
    height: 111.5px;
}

#kitap_listesi {
    margin-top: 1px;
    padding: 15px;
    color:#c6ced7 !important;
}

#kitap_listesi span {

    color:#c6ced7 !important;
}

.top-nav {
    border-bottom: none;
}

.avatar img {
    box-shadow: 0 0 5px 2px #219550 !important;}


#qform, 
#q, 
#qyazar, 
#quye, 
#qara_tip, 
#qara_tip:hover, 
#qbutton {
    background: #2d2d2d !important;
    color:#a9b2c0 !important;
}

#qform input {
    padding-left: 7px;
}

#top_navigation {
    padding-top: 5px;
}

#top_navigation, 
.mybg, 
.usoftblue, 
#top-mem-nav a.pointer {
    background: #565c73 !important;
    border-bottom: 2px solid #7a8595;
    box-shadow: none;
}

.mybg {
    margin-top: 5px;
}

 h1, .mrList a {
    border-bottom: 2px solid #7a8595;
    box-shadow: none;
}

.mrList a {
    border-bottom: 1px solid #7a8595;
}

#top_navigation ul li span:hover, 
#top_navigation ul .selected span, 
#top_navigation .had > ul > li a:hover, 
#sss li {
    background: #8b91a4 !important;
}

#top_navigation .had > ul {
    padding-left: 30px;
}

.acilanMenu a:hover,
#top_navigation ul #menu-bana-ozel span {
    background: #8b91a4 !important;
    color: #e2e2e2 !important;
}


#top_navigation .had > ul > li a:hover,
#aside-m a:hover, 
#top_navigation .had ul li a:hover, 
#top_navigation .had ul li#menu-kitap-listeleri span:hover {
    color: #e2e2e2 !important;
}

#sss li div, 
select, 
input[type="text"], 
input[type="password"], 
textarea {
    background-color: #2d2d2d !important;
    color: #e2e2e2 !important;
}

#content-m .table-a td a, 
.bbox a,
#ul-ilan-kategoriler a,
#kitapdetay a {
    color: #4aa4f1 !important;
    font-weight: bold;
}

#kitapdetay,
.ui-dialog .ui-dialog-content #frmUyeNotu input {
    margin-top: 10px;
}

#kitapdetay p.uk-azhep-goster.detay {
    margin-top: 15px;
}

#content-m .table-a td a:hover, 
#content-m .table-a td a:visited,
#ul-ilan-kategoriler a:hover, 
#ul-ilan-kategoriler a:visited,
#kitapdetay a:hover {
    color: #e2e2e2 !important;
    font-weight: bold;
}

hr,
#home_satistakiler strong.fiyat,
.kitap-listesi strong.fiyat,
.formTable th {
    background: #4aa4f1;
}

.fiyat {
    border: 1px solid #565c73;
    margin-left: -7px;
}

.acilanMenu ul {
    background: #e2e2e2 !important;
    border: 3px solid #565c73;
}

.acilanMenu ul a,
a.tepeye-git {
    color: #565c73 !important;
}   

.myMenu > li > a:hover, 
.myMenu > li:hover > span, 
.myMenu > li.selected > span,
.ukitip_icerik span,
.ukitip_icerik .detay {
    background: #8b91a4 !important;
    color: #e2e2e2 !important;
} 
.myMenu > li > span, .myMenu > li > a {
    color: #a9b2c0 !important;
}  

.kitap-listesi, 
#home_istektekiler, 
#home_takastakiler, 
#home_satistakiler,
#content-um .kitap-listesi div:nth-child(17), 
#content-um .kitap-listesi div:nth-child(19){
    padding-top: 2px;
    background-color: #636775 !important;
}

.karakter_say {
    height: 220px !important;
    margin-top: 20px !important;
    margin-bottom: 20px !important;
}}
