css:
@-moz-document domain("chat.tma.com.vn") {
.sidebar {
    background-color: #4C4A48;
}
.sidebar-item:hover {
    background-color: #767676;
}
.messages-container-main,
.message {
    background-color: #333;
    color: white;
}
.color-primary-font-color {
    color: white;
}
.room-leader.message:hover {
    background-color: #444;
}
.room-leader a.chat-now {
    background-color: #0088cc;
    color: white;
}
.room-leader a.chat-now:hover {
    background-color: #01aaff;
    color: white;
}
.color-info-font-color {
    color: #d1cbcb;
}
.message a {
    color: #d1cbcb;
}
.message a:hover {
    color: #e6e6e6;
}
.message.new-day::before {
    background-color: #7c7878;
}
.content-background-color {
    background-color: #444;
    color: white;
}
.rc-message-box {
    background-color: #444;
    color: white;
}
input,
select,
textarea {
    color: white;
}
.sidebar-item--active {
    background-color: #444;
}
.flex-tab .content,
.flex-tab .user-view,
.flex-tab .list-view {
    background-color: #444;
}
.flex-tab .message.new-day::before {
    background-color: #7c7878;
}
.rc-message-box__container.editing {
    ;
    background-color: #333;
}
.message.editing {
    background-color: #555;
}
.background-transparent-dark-hover:hover {
    background-color: rgba(255, 255, 255, 0.1);
}
.flex-tab-bar .tab-button:hover {
    background-color: #a8a8a9;
}
.rooms-list__toolbar-search {
    background-color: #2f343d;
}
}
