css:
@-moz-document url-prefix("https://mail.google.com/mail/") {
/* compose button */
 .z0 {
    padding-left: 25px;
}
.z0 > .L3 {
    box-shadow: none;
    height: 44px;
    background-color: #eee;
    background-color: rgba(90, 90, 100, 0.085);
    border-radius: 5px;
    width: calc(100% - 85px);
    margin-right: 15px;
    padding-left: 8px;
    padding-right: 30px;
    padding: 0 20px;
    transition: all 0.15s ease-out !important;
}
/* compose button in collapsed mainmenu */
 .bhZ:not(.bym) .z0 > .L3 {
    margin-left: 8px;
}
.bhZ:not(.bym) .z0 {
    padding-left: 0;
}
/* compose button on hover */
 .z0 > .L3:hover {
    box-shadow: none;
    background: #5a5a64;
    color: white;
    transition: all 0.15s ease-out;
}
/* compose mail - panel header */
 .Hy .m {
    background: #5a5a64;
}
/* tabs */

/* remove top border */
 .G-atb::before {
    -moz-box-shadow: none;
    -webkit-box-shadow: none;
    box-shadow: none;
}
/* rounded tabs */
 .aKh {
    border-radius: 5px 0 0 0;
}
/* tab bar */
 .aAA.J-KU-Jg-K9 {
    background: #f5f5f5;
    box-shadow: inset 0 -2px 0 0 rgba(90, 90, 100, 0.075);
}
/* reduce tab height */
 .aKh {
    height: 50px;
}
.aKj, .J-KU-Jg-K9 .J-KU .aAy, .J-KU-Jg-K9 .J-KU .J-KU-KO .aAy {
    height: 49px;
}
/* rounded tabs and smooth transition on hover */
 .aRz {
    border-radius: 5px 5px 0 0;
    transition: background 0.2s ease-out;
}
.aAy {
    transition: background 0.2s ease-out;
    border-radius: 5px 5px 0 0 !important;
}
/* active tab */
 .J-KU-KO.aAy {
    border-radius: 10px 10px 0 0;
    background: orange;
}
/* colored border on active tab */
 .J-KU-KO.aAy::before {
    border-top-left-radius: 0;
    border-top-right-radius: 0;
    height: 3px;
    margin: 0 0;
}
/* tab with new mail notification */
 .aPb .J-KU-KO.aE2 .aKx, .aE2 .aKx {
    top: 4px;
}
.aKw > .aKs {
    letter-spacing: 0;
    line-height: 12px;
}
.aKx > .aDG {
    font-size: 11px;
    letter-spacing: 0;
}
/* email list subjectline */
 .zA > .a4W {
    letter-spacing: 0.1px;
}
/* email list sender */
 .yW {
    letter-spacing: 0;
    letter-spacing: 0.1px;
}
/* email list snippet */
 .y2 {
    color: #888;
}
/* email list message count in conversation*/
 .bx0 {
    letter-spacing: .3px;
    color: #888;
    margin-left: 6px;
}
/* email list label */
 .av {
    /*-moz-border-radius: 2px;
    border-radius: 2px;*/
    padding: 0 6px 0 6px;
}
.ar {
    font-size: 11px;
    letter-spacing: 0;
    border-radius: 2.5px;
    margin-right: 10px;
    line-height: 19px;
}
.at, .au {
    line-height: 17px;
}
/* main menu item */
 .TK .TO, .n6 .ah9, .CL {
    width: calc(100% - 5px);
    -moz-border-radius: 0 5px 5px 0;
    border-radius: 0 5px 5px 0;
}
/* main menu active item */
 .wT > .n3 .byl:first-child .aim:first-child .nZ {
    background-color: #eee;
    background-color: rgba(90, 90, 100, 0.1);
}
.TK .TO:active, .n6 .ah9.aiu:active, .CL:active, .TO.nZ, .TO.ol {
    background-color: #ddd;
}
.TK .TO {
    transition: background 0.2s ease-out;
}
/* main menu hover */
 .TK .TO.NQ {
    background: #ddd;
    background: rgba(0, 0, 0, 0.05);
}
/* email list */
 .zA {
    transition: background 0.2s ease-out;
}
.y6, .yW {
    font-size: 0.875rem;
}
.zA:hover {
    background: #eee;
    background: rgba(90, 90, 100, 0.075);
}
.zE {
    color: #000;
    color: rgba(60, 60, 66, 1);
    font-weight: normal;
}
/* selected email in list */
 .aps {
    color: #000;
    color: rgba(60, 60, 66, 1);
    background: #aaa;
    background: rgba(90, 90, 100, 0.25);
}
.zA > .PE.PF::before {
    background-color: #444;
    background-color: rgba(90, 90, 100, 1);
}
/* dropdown menus */
 .J-M {
    -moz-border-radius: 5px;
	-webkit-border-radius: 5px;
    border-radius: 5px;
}
/* system message */
 .Tm .ya {
    margin-right: 4px;
    background-color: #ddd;
    background-color: rgba(0, 0, 0, 0.035);
}
}
