css:
@-moz-document domain('boards.4chan.org') {
    .party-hat {
        width: 80px;
        margin-top: -40px;
    }

    .small .party-hat,
    .extended-small .party-hat {
        width: 50px;
        margin-top: -30px;
        left: 0px;
    }

    /* 4chan X catalog */
    :root.hats-enabled .catalog-small > .catalog-thread::after {
        top: -24px;
        width: 50px;
        height: 50px;
    }

    :root.hats-enabled .catalog-large > .catalog-thread::after {
        top: -58px;
        width: 80px;
        height: 80px;
    }
}
