css:
@-moz-document domain("netacad.com") {
.aui {
    background-color: #202020;
    color: white;
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
    line-height: 20px;
    margin: 0;
}

.course-holder .course a {
    color: white;
}

h2.course-title.ellipsis {
    color: #fff !important;
}

#right-side table.summary {
   color: black;
}

.course-holder .course .main .captions-container .title {
    -ms-word-break: break-all;
    word-break: break-all;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    color: white;
    margin-bottom: 4px;
    font-size: 16px;
}

#breadcrumbs>ul>li+li:last-of-type a {
    color: #049fd9;
}

.recent-activity-header {
    margin: 0;
    border-bottom: none;
    line-height: 1.2;
    padding-bottom: 12px;
    font-size: 1.5em;
}

.ic-notification__content {
    -webkit-flex: 1;
    flex: 1;
    padding: 12px;
    box-sizing: border-box;
    background: #202020;
}

.menu-item__text {
    color: #fff !important;
}

.ic-app-header__menu-list-item.ic-app-header__menu-list-item--active .ic-app-header__menu-list-link {
    background: #202020;
}

.context_module {
    border-top: 1px dotted #aaa;
    border-bottom: 1px dotted #aaa;
    background:none;
    min-width: 20px;
    margin-bottom: 15px;
}
/* Rows fix */
.course-list-table .course-list-table-row:hover {
    background-color: #161616;
}

.ic-Table.ic-Table--striped tbody tr:nth-child(odd) {
    background-color: #1f1f1f;
}

.quiz-submission, .question_editing {
    background: #181818;
}

.question .text {
    background-color: #161616;
}

.question .text {
    background-color: #161616;
}

.ic-Table.ic-Table--hover-row tbody tr:hover, .ic-Table.ic-Table--hover-row tbody tr.ic-Table__row--bg-neutral:hover, .ic-Table.ic-Table--hover-row tbody tr.ic-Table__row--bg-success:hover, .ic-Table.ic-Table--hover-row tbody tr.ic-Table__row--bg-alert:hover, .ic-Table.ic-Table--hover-row tbody tr.ic-Table__row--bg-danger:hover {
    background-color: #202020;
}

/* Scroll bar*/

::-webkit-scrollbar {
  width: 9px;
  height: 9px;
}
::-webkit-scrollbar-button {
  width: 0px;
  height: 0px;
}
::-webkit-scrollbar-thumb {
  background: #e1e1e1;
  border: 0px none #ffffff;
  border-radius: 50px;
}
::-webkit-scrollbar-thumb:hover {
  background: #ffffff;
}
::-webkit-scrollbar-thumb:active {
  background: #000000;
}
::-webkit-scrollbar-track {
  background: #666666;
  border: 0px none #ffffff;
  border-radius: 50px;
}
::-webkit-scrollbar-track:hover {
  background: #666666;
}
::-webkit-scrollbar-track:active {
  background: #333333;
}
::-webkit-scrollbar-corner {
  background: transparent;
}

/* Questions fix by RHIN0X */
.quiz_comment {
    background-color: rgba(0,0,0);
    background-image: linear-gradient(to bottom, #505050, #000000);
    border: 1px solid #000;
    -webkit-filter: none;
    filter: none;
    border-radius: 25px;
    box-shadow: 0 1px 2px rgba(0,0,0,0.2);
    font-size: 16px;
    font-size: 1rem;
    min-width: 100px;
    padding: 14px;
    position: relative;
    margin: 16px 30px;
    text-align: left;
    display: inline-block;
}
.question {
    border: 1px solid #AAA;
    background-color: #161616;
    position: relative;
    min-height: 50px;
    margin: 0.7em auto 30px auto;
}
#questions.assessment_results .question .header {
    padding: 8px 20px;
    background-color: #161616;
    overflow: auto;
}
.question .header {
    font-size: 1.2em;
    font-weight: bold;
    border-bottom: 1px solid #AAA;
    background-color: #161616;
    padding: 8px 20px;
    margin: 0;
}
#questions.assessing {
    background: #161616;
    padding: 16px;
    min-height: 225px;
}
/* Module Footer Fix by RHIN0X */
.module-sequence-footer-content:not(.no-animation) {
    -webkit-animation: module-sequence-footer-animation 0.5s ease-out;
    animation: module-sequence-footer-animation 0.5s ease-out;
    background-color: #161616;
}
/* Table Summary Fix by RHIN0X */
#right-side table.summary {
    color: black;
    background-color: #161616;
}
/* People roster fix by RHIN0X */
#group_categories_tabs>.roster-tab {
    padding-left: 1em;
    padding-right: 1em;
    background-color: #161616;
}
/* Banner Fix by RHIN0X */
#blue-header {
    background-color: #161616;
    height: 140px;
    background-size: contain;
    background-position: calc(100% - 20px);
    background-repeat: no-repeat;
    line-height: 140px;
    font-size: 60px;
    padding: 0;
    font-weight: 100;
}
/* Main page font color fix by RHIN0X */
.aui body.netacad-atlantic {
    margin: 0;
    overflow: auto;
    color: #f2f2f2;
}
/* Main Page tabs (at the bottom) color fix by RHIN0X */
#resourcesContainer a.resource {
    width: calc(33.3% - 15px);
    float: left;
    margin-right: 20px;
    margin-top: 20px;
    background-color: #161616;
    padding: 20px;
    border-radius: 5px;
    display: block;
    color: #58585b;
    font-size: 16px;
    line-height: 20px;
}
/* Main Page - last logged in color fix by RHIN0X */
.login-status {
    position: absolute;
    right: 20px;
    margin: 0;
    border-radius: 0 0 15px 15px;
    border: 1px solid #000;
    border-top: 0;
    background-color: #161616
}

/* Landing page fix by RHIN0X */
.cui.template--cdc header, .cui.template--cdc main {
    background-color: #161616;
    
}
/* Remainder of landing page fix by RHIN0X */
.cui .panel--ltgray {
    color: #58585b;
    position: relative;
    background: #161616;
    -webkit-box-shadow: none;
    box-shadow: none;
    border: 1px solid transparent;
}
/* 3 tabs fix by RHIN0X */
.cui .panel--dkgray {
    color: #fff;
    position: relative;
    background: #161616;
    -webkit-box-shadow: none;
    box-shadow: none;
    border: 1px solid transparent;
}

/* Footer fix by RHIN0X */
.cui.template--cdc footer {
    background-color: #161616;
}

/* Search background fix by RHIN0X */
#search-btn {
    background-color: #161616;
}
}

@-moz-document domain("netacad.com") {
/* Course */
header#header {
    box-shadow: 0px 0px 4px 0px #000 !important;
}

.ic-app-header__logomark-container {
    width: 100%;
    background-color: #181818;
    box-sizing: border-box;
    padding: 0;
}

.ic-app-header__menu-list-link:hover, .ic-app-header__menu-list-link:focus {
    background-color: #d8dde7;
    text-decoration: none;
    outline: none;
}

.ic-app-header {
    box-sizing: border-box;
    position: fixed;
    top: 0;
    left: 0;
    height: 100%;
    display: -webkit-flex;
    display: flex;
    -webkit-flex-direction: column;
    flex-direction: column;
    width: 54px;
    z-index: 100;
    background-color: #181818;
    color:white;
}

body {
    background-image: url(https://www.toptal.com/designers/subtlepatterns/patterns/zwartevilt.png) !important;
    background-repeat: repeat !important;
    background-repeat: repeat-x;
    background-repeat: repeat-y;
    background-color: #181818 !important;
    color: #fff;
}

.list-view>ul>li>a, .list-view>nav>ul>li>a {
    display: block;
    text-decoration: none;
    color: #fff;
    font-weight: 300;
    border-radius: 3px;
    padding: 6px 12px;
}

.list-view>ul>li>a:hover, .list-view>ul>li>a:focus, .list-view>nav>ul>li>a:hover, .list-view>nav>ul>li>a:focus {
    background: #f2f2f2;
    background-image: initial;
    background-position-x: initial;
    background-position-y: initial;
    background-size: initial;
    background-repeat: repeat-x;
    background-repeat: repeat-y;
    background-attachment: initial;
    background-origin: initial;
    background-clip: initial;
    background-color: rgb(62,62,62);
}

.header-bar {
    border-bottom: none;
    background-color: rgba(255, 255, 255, 0);;
    min-height: 33px;
    padding-bottom: 12px;
}

.item-group-container {
    background: #202020;
    padding-bottom: 72px;
    border-radius: 10px 10px 10px 10px;
	-moz-border-radius: 10px 10px 10px 10px;
	-webkit-border-radius: 10px 10px 10px 10px;
	border: 0px solid #000000;
}

.context_module .header {
    background-color: transparent;
    border-bottom: none;
    padding: 0px;
    color: #fff;
}

.ig-header .name {
    font-size: 15px;
    font-size: .9375rem;
    font-weight: bold;
    text-shadow:none;
    color:#fff;
}

#breadcrumbs {
    background-color: transparent;
}

.ic-app-header__menu-list-link:hover, .ic-app-header__menu-list-link:focus {
    background-color: #202020;
    text-decoration: none;
    outline: none;
}

.item-group-condensed .ig-row {
    border-top-width: 0;
    border-radius: 0;
    background: #191919;
    border-bottom: 1px solid #C7CDD1;
    color:#fff;
}

.ig-list .ig-row:hover {
    background-color: #202020;
}

.ig-list .ig-row a.ig-title {
    color: #05bbff;
    text-decoration: none;
}

.btn, .Button, .ui-button {
    background: #434343;
    color: #fff;
}
}

@-moz-document url-prefix("https://static-course-assets.s3.amazonaws.com") {
/* Header fix by RHIN0X */
#page-header, #page-footer {
    background-color: rgba(16, 16, 16, 1);
    background: -webkit-linear-gradient(to right, rgba(16, 16, 16, 1) 1%, rgba(50, 50, 50, 1) 100%);
    background: -moz-linear-gradient(to right, rgba(16, 16, 16, 1) 1%, rgba(50, 50, 50, 1) 100%);
    background: -ms-linear-gradient(to right, rgba(16, 16, 16, 1) 1%, rgba(50, 50, 50, 1) 100%);
    background: -o-linear-gradient(to right, rgba(16, 16, 16, 1) 1%, rgba(50, 50, 50, 1) 100%);
    background: linear-gradient(to right, rgba(16, 16, 16, 1) 1%, rgba(50, 50, 50, 1) 100%);
    height: 54px;
    position: absolute;
    right: 0;
    left: 0;
}

/* Side bar fix by RHIN0X */
#text-container {
    background-color: rgba(25, 25, 25, 1);
    z-index: 1;
}

/* Title fix by RHIN0X */
h1 {
    color: #f2f2f2;
    border-bottom: 1px solid #CCCCCC;
    font-family: "CiscoSansTTThin";
    font-weight: normal;
    font-size: 20px;
    padding: 15px 0;
    margin: 0 0 15px 0;
    line-height: 1;
}

/* Body fix by RHIN0X */
body {
    background-color: #252525;
    color: #f2f2f2;
    font-family: "CiscoSansTTLight", Helvetica, Arial, sans-serif;
    font-size: 12px;
    line-height: 1.5;
}

/* Buttons1Fix by RHIN0X */
#page-slide-buttons ol {
    display: inline-block;
    list-style: none;
    padding: 0;
    margin: 7px 0 0 7px;
    border-bottom: 3px solid #f2f2f2;
    height: 19px;
}

/* Buttons2Fix by RHIN0X */
#page-slide-buttons li {
    background: rgb(255,255,255);
    background: -webkit-linear-gradient(top, rgba(255,255,255,1) 0%, rgba(187,213,227,1) 100%);
    background: -moz-linear-gradient(top, rgba(255,255,255,1) 0%, rgba(187,213,227,1) 100%);
    background: -ms-linear-gradient(top, rgba(255,255,255,1) 0%, rgba(187,213,227,1) 100%);
    background: -o-linear-gradient(top, rgba(255,255,255,1) 0%, rgba(187,213,227,1) 100%);
    background: linear-gradient(to bottom, rgba(16, 16, 16, 1) 0%, rgba(50, 50, 50, 1) 100%);
    border: 3px solid #f2f2f2;
    border-radius: 50%;
    display: inline-block;
    margin: 0 14px 0 0;
    overflow: hidden;
}

/* Buttons3Fix by RHIN0X */
#page-slide-buttons legend {
    position: absolute;
    left: 100%;
    top: 50%;
    line-height: 1;
    white-space: nowrap;
    margin: -6px 0 0 0;
    padding: 0 0 0 8px;
    border-left: 2px solid #f2f2f2;
}
/* ----Netacad Test---- */
#questions{
    background-color:#202020;
}
}

@-moz-document url-prefix("https://assessment.netacad.net") {
/* TEST by RHIN0X */
#questions {
    background-color:#252525;
}
.ai-option {
    font-size: 12px;
    color: #cecece;
    vertical-align: top;
    margin-bottom: 0.5em;
    padding: 5px;
}
.ai-option:hover {
    color: #252525;
}
.surveyHeader {
    font-weight: normal;
    color: #cecece;
    font-size: 18px;
}
body {
    margin: 0;
    padding: 0;
    font-family: sans-serif;
    background-color: #252525;
}
h1 {
    color: #cecece;
    font-family: "CiscolightRegular";
    font-size: 22px;
    font-weight: normal;
    margin: 1em 0;
}
hr {
    background-color: #cecece;
/*  background-image: url(images/stripe.png); */
    background-position: top left;
    background-repeat: no-repeat;
    background-size: 100% 5px;
    border: 0;
    height: 5px;
    margin: 0;
}
.qNum {
    display: table-cell;
    position: relative;
    color: #cecece;
    vertical-align: top;
    width: auto;
}
#navbarhover {
    background-color: #181818;
    color: #202020;
}
#navbar {
    background-color: #252525;
}
td.answered {
    background-color: #181818;
    border: 1px solid #202020;
}
td.current {
    background-color: #303030;
    border: 1px solid #202020;
}
td.default {
    background-color: #252525;
    border: 1px solid #202020;
}
.ui-widget-content {
    border-top: 1px solid #ededed;
    background: #252525;
    color: #707070;
    padding: 5px;
}
#breadcrumbs ul {
    margin: 0;
    padding: 0;
    list-style: none;
    white-space: nowrap;
    background-color: #202020;
}
}

@-moz-document domain("herokuapp.com") {
/*Chestnut*/
.panel-body {
    background-color: #202020;
    color:#fff;
}

html {
    font-family: sans-serif;
    -webkit-text-size-adjust: 100%;
    -ms-text-size-adjust: 100%;
    background: #202020;
}

.panel-default {
    border:none;
}
}

@-moz-document url-prefix("https://static-course-assets.s3.amazonaws.com") {
/* Text for boxes by RHIN0X */
a, a:link, a:visited {
    color: #cecece !important;
}

/* Boxes that are selected by RHIN0X */
#map a.selected {
    background-color: hsla(198, 0%, 71%, .5);
    border-radius: 8px;
}

/* Hover oves boxes by RHIN0X */
a:hover {
    background-color: hsla(198, 0%, 0%, .5) !important;
}

/* Boxes by RHIN0X*/
ul.items a {
    background-color: rgba(68, 68, 68, .7);
    color: white;
    border: 2px solid white;
    display: block;
    padding: 8px;
    margin: 0;
    cursor: pointer;
    text-decoration: none;
    outline: 0;
}
}
