css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("srad.jp") {
.synd,
#site_menu,
#firehose-sections,
#article-bottom,
#firehose-message-tray,
.ind,
.my_toggle_share,
#comments .current {
    display: none;
}
.ads-leaderbord,
#ads-article-left,
#gpt-sd_index_rec,
#ads-index-text {
    min-height: 0px !important;
    margin: 0;
    padding: 0;
    height: auto;
}
#slashboxes,
#firehose {
    padding-top: 14px !important;
}
#commentlisting {
    padding-left: 0;
}
article {
    padding: 0;
}
ul[id^="commtree_"] {
    padding: 0 0 0 5px;
}
#commentlisting li.comment.full {
    padding: 0 0 6px 0;
}
#commentlisting li.comment.full > .comment-item {
    padding: 0 0;
}
#commentlisting li.comment.full > .comment-item .commentBody,
article .body {
    padding: 0 10px 0;
}
body.console #firehose > *, body.users2 #firehose > *, body.firehose #firehose > * {
    margin-right: 10px;
}
#commentlisting li.comment.full > .cw {
    padding: 0;
}
}
