css:
@-moz-document domain("www.baidu.com") {
body {
    background-color: #0E0E0E
}

.soutu-btn,
.ipt_rec {
    visibility: hidden
}

.cr-offset,
#rs,
.qrcode-img,
.qrcode-text,
.bdsug,
.c-icon-bear-p,
.c-icon-bear-pn,
.index-logo-srcnew {
    visibility: hidden
}

.bg {
    background-color: #0E0E0E;
    border-radius: 4px;
}

.t {
    visibility: visible
}

.s_btn {
    background-color: rgb(51, 133, 255);
    margin-left: 1px;
    width: 84px;
}

em,
.c-showurl,
.op_dict3_highlight {
    color: rgb(51, 133, 255);
}

em,
a {
    text-decoration: none;
    font-weight: bold;
}

span,
.op_dict_text2,
.op_dict3_else_items {
    color: #FFFFFF;
}

a {
    color: #FFFFFF
}

.toindex {
    text-decoration: none
}

.c-container,
.s_down {
    border: 1px solid #A0A0A0;
    padding: 8px;
    border-radius: 4px;
    background-color: #303030;
}

.op_dict3_font24,
.op_dict3_marginRight,
.op_dict3_font16,
.op_dict3_gap_small,
.op_dict_text2,
.op_dict3_inlineblock,
.op_dict3_linetwo_result,
.op_dict3_else_items {
    color: #FFFFFF
}

.c-abstract,
.c-span18 {
    color: #FFFFFF
}

.pc {
    background-color: #303030
}

.s_tab {
    background-color: #0E0E0E;
}

.s_tab_inner,
.head_wrapper,
#s_tab {
    background-color: #0E0E0E;
}

.s_ipt {
    color: #FFFFFF
}

.n {
    color: #000000
}

strong {
    visibility: hidden
}

.c-border {
    border: none;
}
}
