css:
@-moz-document domain("okoun.cz") {
textarea {
    /* padding: 2px; */
    /* min-width: 15em; */
    width: 100%;
    height: 7em;
}

input[type=checkbox] {
  transform: scale(2);
}

.nobr {
    white-space: unset;
}

.expbutton .hd {
    font-size: 20pt;
    font-family: sans-serif;
    padding: 0;
}
.pager ul li {
    font-size: 18pt;
}

.item .meta a {
    visibility: hidden;
}
.yui-button button {
    font-size: 25pt !important;
    line-height: 1.5em !important;
 }

.pager-top {
    right: unset;
    top: 16px;
}
input.text {
    font-size: 25pt;
      width: 180pt !important;
}

body {
    padding: 0;
    min-width: auto;
}
#body {
    padding: 0;
    margin: 0;
}
.head .user {
    font-size: 22pt;
}
  /* reklamy fuc */
  #adwrapper-lb, #adwrapper-bs {    visibility: hidden; display:none !important}

  /* rozdeleni sloupcu */
.main .ctx {
    visibility: hidden;
    height: 0;
    display: none;
}
.main .main {
    width: 100%;
}

  /* ramecek prispevku */
.listing .item {
     padding-bottom: 0; 
}

.listing {     padding-top: 16px; 
}
/*pryc kategorie*/
    
    h3 {
        position: absolute;
        z-index: -11;
        visibility: hidden ;
        display: none;
    }

h2 {
     font-family: sans-serif; 
     font-size: 2.5em;
}
.welcome {
    height: 0;
    visibility: hidden;
    display: none;
}
div.content {
    font-size: 25pt;
}

/* popisek audirka*/
.descr {
    position: absolute;
    right: 3%;
    display: none;
     visibility: hidden;
   }

/* button s oblidenosti klubu*/
.yui-button.top {
    float: inherit;
    margin-bottom: 0.5em;
}
.content, .item .meta, body.userlist .main .main, body.boardlist .main .main {
    font-size: 25pt;
        overflow-wrap: break-word;
    word-wrap: break-word;
}
.head .menu, .board .main .settings {
    font-size: 25pt;
}
.head .menu {
    margin-left: 0em;
}

a {
    overflow-wrap: break-word;
    word-wrap: break-word;
}
}
