css:
@-moz-document domain(1993s.top)
{
body {
	background-color: rgb(13, 13, 13);
	font-family: 雅黑;
	color:white;
}
.stui-header__logo .logo,
.stui-pannel__head .more,
.visible-xs,
.bdsharebuttonbox {
	display: none;
}

.stui-header__menu li a {
	font-family: 雅黑;
	font-size: 18px;
	color: white;
}
.stui-header__top {
	background-color: rgb(44, 40, 40);
}
.stui-pannel-bg {
	background-color: rgb(29, 27, 27);
}
.stui-vodlist__detail .title a {
	color:white;
}
.title a,.text-muted,.detail-more,.stui-pannel__head h3{
	color:white;
}
p{
	color:white;
	
	
}

input#ff-wd {
    background-color: rgb(0, 0, 0);
}

.form-control {
    border: 1px solid rgb(155, 139, 139);
	color:white;
}

.stui-content__playlist li.active a,.stui-content__playlist li a ,.page-item,.badge-third{
    border: 1px solid rgb(241, 238, 233);
    background-color: rgb(233, 229, 223);
    color: rgb(11, 11, 11);
}

.stui-content__playlist li a:hover, .stui-content__playlist li a:hover,.page-item{
    border: 1px solid rgb(75, 74, 73);
    background-color: rgb(61, 61, 59);
    color: rgb(251, 248, 248);
}
.bottom-line:after {
    border-bottom: 1px solid rgb(5, 5, 5);
}
.form-control:active{
	border: 1px solid rgb(241, 238, 233);
}
page li.disabled a {
    background-color: rgb(75, 74, 73);
    color: rgb(255, 255, 255);
    border: 1px solid rgb(243, 240, 235);
}
.stui-screen__list li a,.stui-content__detail .title,ul.stui-vodlist__rank.col-pd.clearfix li a {
	color:white;
}
.stui-pannel_bd{
	display:block
}

.detail-content p,.detail-content p span{
	background-color: rgb(29, 27, 27);
    color: rgb(245, 242, 242);
}
.badge {
    color: rgb(0, 0, 0);
    background-color: rgb(238, 238, 238);
}
.stui-foot .text-center{
	background-color: rgb(44, 40, 40);
	width:100%;
	height:80px;
	line-height:80px;
}
.stui-vodlist__screen:last-child,.stui-link__text,img.qrcode,.col-md-wide-25 .text-center font-12,.stui-pannel__head .title img,.sideslip .col-pd{
	display:none;
}


}
