css:
@-moz-document domain("youtube.com") {
ytd-grid-video-renderer #video-title.yt-simple-endpoint.ytd-grid-video-renderer {
    display: -webkit-box;
    height: 4.7rem;
    max-height: 5rem;
    -webkit-box-orient: vertical;
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: normal;
    -webkit-line-clamp: 3;
    font-size: 1.4rem;
    font-weight: 500;
    line-height: 1.6rem;
}

#items.ytd-grid-renderer > ytd-grid-video-renderer.ytd-grid-renderer, #items.ytd-grid-renderer > ytd-grid-channel-renderer.ytd-grid-renderer, #items.ytd-grid-renderer > ytd-grid-playlist-renderer.ytd-grid-renderer, #items.ytd-grid-renderer > ytd-grid-movie-playlist-renderer.ytd-grid-renderer, #items.ytd-grid-renderer > ytd-grid-movie-renderer.ytd-grid-renderer, #items.ytd-grid-renderer > ytd-grid-show-renderer.ytd-grid-renderer, #items.ytd-grid-renderer > ytd-game-card-renderer.ytd-grid-renderer {
    margin-right: 4px;
    display: inline-block;
    width: 210px;
    margin-bottom: 6px;
}
}
