css:
@-moz-document regexp("^https?\\:\\/\\/.*") {
[role='presentation']::before,
[role='none']::before{
    content: "role=\"" attr(role) "\" name=\"" attr(aria-label)attr(aria-labelledby) "\" id=\"" attr(id) "\"" !important;
    color:#FFF;
    background-color: #007bc0 !Important;
    outline: 1px solid #007bc0 !important;
    display: block !important;
    position:absolute;
    z-index:1111;
}

div[role='presentation']::before,
div[role='none']::before{
    content: "DIV role=\"" attr(role) "\" name=\"" attr(aria-label)attr(aria-labelledby) "\" id=\"" attr(id) "\"" !important;
}
header[role='presentation']::before,
header[role='none']::before{
    content: "HEADER role=\"" attr(role) "\" name=\"" attr(aria-label)attr(aria-labelledby) "\" id=\"" attr(id) "\"" !important;
}
footer[role='presentation']::before,
footer[role='none']::before{
    content: "FOOTER role=\"" attr(role) "\" name=\"" attr(aria-label)attr(aria-labelledby) "\" id=\"" attr(id) "\"" !important;
}
section[role='presentation']::before,
section[role='none']::before {
    content: "SECTION role=\"" attr(role) "\" name=\"" attr(aria-label)attr(aria-labelledby) "\" id=\"" attr(id) "\"" !important;
}
aside[role='presentation']::before,
aside[role='none']::before{
    content: "ASIDE role=\"" attr(role) "\" name=\"" attr(aria-label)attr(aria-labelledby) "\" id=\"" attr(id) "\"" !important;
}
span[role='presentation']::before,
span[role='none']::before{
    content: "SPAN role=\"" attr(role) "\" name=\"" attr(aria-label)attr(aria-labelledby) "\" id=\"" attr(id) "\"" !important;
}
nav[role='presentation']::before,
nav[role='none']::before{
    content: "NAV role=\"" attr(role) "\" name=\"" attr(aria-label)attr(aria-labelledby) "\" id=\"" attr(id) "\"" !important;
}


[role='presentation'] {
        outline: 2px solid #007bc0 !important;
}
}
