css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.gtaforums.com")
{
  .postcolor img[alt="user posted image"], .row1 img[alt="user posted image"]
  {
     max-height: 700px !important;
     max-width: 625px !important;
     background: url(data:image/png;base64,%3D) !important;
     background-repeat: no-repeat !important;
     background-position: 0 0 !important;
     padding-right: 5px !important;
     padding-bottom: 5px !important;
  }
  img[alt="user posted image"]:hover
  {
     max-height: 100% !important;
     max-width: 100% !important;
     padding-right: 0px !important;
     padding-bottom: 0px !important;  
  }
 .textinput, .forminput, div.row1 div.postcolor
  {
     font-size: 12px !important;
  }
  .textinput[id^="ta"]
  {
     height: 50px !important;
     overflow: auto !important;
  }  
  #CODE, #QUOTE
  {
     display: block !important;
     overflow: auto !important;     
     margin-left: 0 !important;
     clear: both !important;
  }
  #CODE
  {
     max-height: 350px !important;
  }
  #QUOTE
  {
     max-height: 30em !important;
     margin-bottom: 0 !important;
  }
  #QUOTE #QUOTE
  {
     overflow: visible !important;
     display: table !important;
     margin-left: 0px !important;
  }
}

@-moz-document url-prefix(http://www.gtaforums.com/index.php?&act=modcp)
{
  span[class="highlight"]
  {
     font-size: 20px !important;
     font-family: Trebuchet MS, Arial, Verdana !important;
     color: #FFFF00 !important;
     background-color: #000000 !important;
     padding: 3px 3px 3px 3px !important;
     border: 1px solid #C0C0C0 !important;
  }
}
