css:
@-moz-document regexp("^https?\\:\\/\\/.*") {
img {
    border-width: 15px !important;
   border-image: url('data:image/png;base64,') 15 2 repeat !important;  
    border-style: solid;
    padding: 15px !important; 
}


img[alt] {
    border: 3px solid rgb(0, 220, 33) !important;
    outline: 1px solid #fff;
    border-image:none;
    padding: 0 !important;
}

img[alt=""] {    
    border-width: 16px !important;
    padding: 16px !important;
    border-image: url('data:image/png;base64,') 22 2 repeat !important;
}

img[alt*="ackground"],
img[alt*="ecorative"],
img[alt*="raphic"],
img[alt*="humbnail"],
img[alt*="hoto"],
img[alt*="\["],
img[alt*="\]"],
img[alt*="?"],
img[alt*="%"],
img[alt*="#"],
img[alt*="^"],
img[alt*="<br"],
img[alt*="image"],
img[alt*="Image"],
img[alt*="icture"],
img[alt*="raphic"],
img[alt*="icon"],
img[alt*="Icon"],
img[alt*="llustration"],
img[alt*="background"],
img[alt*="no data"],
img[alt*="none"],
img[alt*="null"],
img[alt*="photo"],
img[alt*="creenshot"],
img[alt*="tbd"],
img[alt*="rawing"],
img[alt*="bmp"],
img[alt*="img"],
img[alt*="png"],
img[alt*="jpg"],
img[alt*="jpeg"],
img[alt*="gif"],
img[alt*="svg"]{
    outline: 1px solid #FFF !important;
    position: relative !important;
    border-width:15px !important;    
    border-image:  url('data:image/png;base64,') 22 2 repeat !important;
}


a img[alt*="icon"],
a img[alt*="Icon"],
a img[alt*="logo"],
a img[alt*="Logo"]{
    outline: 1px solid #FFF !important;
    position: relative !important;
    border-width:15px !important;    
    border-image: url('data:image/png;base64,') 22 2 repeat !important;
}
}
