css:
/************************************************************************
*                            GTAF-Enhanced                              *
*                     A userstyle for GTAForums.com                     *
*   Release Topic: http://www.gtaforums.com/index.php?showtopic=404839  *
************************************************************************/


@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.gtaforums.com")
{


  /********* Image Resizer **********/ 
  
  /* NOTE: If you change the maximum image width/height, the red border may not be visible. */
  
  .postcolor img[alt="user posted image"], .row1 img[alt="user posted image"]
  {
     max-height: 700px !important; /* Maximum Height */
     max-width: 625px !important;  /* Maximum Width */
     background: url(data:image/png;base64,%3D) !important;
     background-repeat: no-repeat !important;
     background-position: 0 0 !important;
     padding-right: 5px !important;
     padding-bottom: 5px !important;
  }
  img[alt="user posted image"]:hover
  {
     max-height: 100% !important;
     max-width: 100% !important;
     background: none !important;
  }
  /***********************************/
  
  
  /********* Larger Text Input **********/  
 .textinput, .forminput, div.row1 div.postcolor, td#ucpcontent  p
  {
     font-size: 12px !important; /* Font Size */
  }
  .textinput[id^="ta"]
  {
     height: 50px !important;
     overflow: auto !important;
  }  
  /***********************************/
  
  
  /********* Tiny Font Size Fix **********/  
  .postcolor span[style^="font-size: 0pt;"]:hover, .postcolor span[style^="font-size: 1pt;"]:hover, .postcolor span[style^="font-size: 2pt;"]:hover, .postcolor span[style^="font-size: 3pt;"]:hover, .postcolor span[style^="font-size: 4pt;"]:hover, .postcolor span[style^="font-size: 5pt;"]:hover, .postcolor span[style^="font-size: 6pt;"]:hover, .postcolor span[style^="font-size: 7pt;"]:hover
  {
    font-size: 100% !important;
  }
  /***********************************/
  
  
  /********* Code & Quote Cropping **********/
  #CODE, #QUOTE
  {
     display: block !important;
     overflow: auto !important;     
     margin-left: 0 !important;
     clear: both !important;
  }
  #CODE
  {
     max-height: 350px !important; /* Height */
  }
  #QUOTE
  {
     max-height: 30em !important; /* Height */
  }
  /***********************************/
  
  
  /********* Quote Train Fix **********/
  #QUOTE #QUOTE
  {
     overflow: visible !important;
     display: table !important;
     margin-left: 0px !important;
  }
  /***********************************/
  
  
  /********* Edit legend in Posts **********/
  .edit
  {
     opacity: 0.8 !important;
  }
  .edit:hover
  {
     opacity: 1 !important;
  }
  /***********************************/
  
  
  /********* Logout Link Alert **********/
  a[href$="gtaforums.com/index.php?act=Login&CODE=03"][target="_blank"]
  {
     color: red !important;
     text-decoration: blink !important;
     border: 1px red inset !important;
  }
  /***********************************/
  
  
  /********* Medals **********/
  #medal_box
  {
     opacity: 1 !important;
     padding: 3px 3px 3px 3px !important;
  }
  #medal_box:hover
  {
     display: inline !important;
  }
  /***********************************/
  
  
  /********* Fast Reply **********/
  #qr_open
  {
  	display: inline !important;
  }
  /***********************************/
  
  
}



@-moz-document url-prefix(http://www.gtaforums.com/index.php?&act=modcp)
{
  
  
  /********* Reported Posts **********/
  center span[class="highlight"]
  {
     font-size: 20px !important;
     font-family: Trebuchet MS, Arial, Verdana !important;
     color: #FFFF00 !important;
     background-color: #000000 !important;
     padding: 3px 3px 3px 3px !important;
     border: 1px solid #C0C0C0 !important;
  }
  /***********************************/
  
  
}
