@-moz-document regexp("^https?\\:\\/\\/.*") {
[class^=c-heading-]:after,
[class^=c-heading-]:before {
    display: inline-block !important;
    font-weight: normal !important;
}

body h1.visually-hidden,
body h1.definition,
body h1.pmg-minimal-header,
body h1.NarratorSpan,
body h1.x-screen-reader,
body h1.screen-reader-text,
body h2.sr-only,
body h2.visually-hidden,
body h2.definition,
body h2.pmg-minimal-header,
body h2.NarratorSpan,
body h2.x-screen-reader,
body h2.screen-reader-text,
body h2.sr-only,
body h3.visually-hidden,
body h3.definition,
body h3.pmg-minimal-header,
body h3.NarratorSpan,
body h3.x-screen-reader,
body h3.screen-reader-text,
body h3.sr-only,
body h4.visually-hidden,
body h4.definition,
body h4.pmg-minimal-header,
body h4.NarratorSpan,
body h4.x-screen-reader,
body h4.screen-reader-text,
body h4.sr-only,
body h5.visually-hidden,
body h5.definition,
body h5.pmg-minimal-header,
body h5.NarratorSpan,
body h5.x-screen-reader,
body h5.screen-reader-text,
body h5.sr-only,
h6.visually-hidden,
body h6.definition,
body h6.pmg-minimal-header,
body h6.NarratorSpan,
body h6.x-screen-reader,
body h6.screen-reader-text,
body h6.sr-only {
    display: block !important;
    clip: inherit !important;
    position: relative !important;
    width: auto !important;
    height: auto !important;
    overflow: visible !important;
}

[role="heading"]::before,
body [role=heading][aria-level]:after,
body h1:before,
body h1[class^=c-heading-]:before,
body [role=heading][level]:before,
body h2:before,
body h2[class^=c-heading-]:before,
body h3:before,
body h3[class^=c-heading-]:before,
body h4:before,
body body h4[class^=c-heading-]:before,
body h5:before,
body body h5[class^=c-heading-]:before,
body h6:before,
body body h6[class^=c-heading-]:before,
body h1:after,
body h1:before,
body h2:after,
body h2:before,
body h3:after,
body h3:before,
body h4:after,
body h4:before,
body h5:after,
body h5:before,
body h6:after,
body h6:before {
    background: rgba(255, 255, 108, 1) !important;
    color: #000 !important;
    font-size: 14px !important;
    font-family: Consolas, monospace !important;
    font-weight: normal !important;
    display: inline-block !important;
    border: 0 !important;
}

[role="heading"][aria-level]::before {
    content: " <div role=\"" attr(role) "\" aria-level=\"" attr(aria-level) "\">" !important;
}

[role="heading"]::after {
    content: "</div>" !important;
}

body h1::before {
    content: "<h1>" !important;
}

body h1:after {
    content: "</h1>" !important;
}

body h2:before {
    content: "<h2>" !important;
}

body h2:after {
    content: "</h2>" !important;
}

body h3:before {
    content: "<h3>" !important;
}

body h3:after {
    content: "</h3>" !important;
}

body h4:before {
    content: "<h4>" !important;
}

body h4:after {
    content: "</h4>" !important;
}

body h5:before {
    content: "<h5>" !important;
}

body h5:after {
    content: "</h5>" !important;
}

body h6:before {
    content: "<h6>" !important;
}

body h6:after {
    content: "</h6>" !important;
}

