/* ==UserStyle==
@name           Darker Exia's Moodle
@namespace      github.com/openstyles/stylus
@version        1.2
@description    A dark theme for Exia's Moodle installation. 
@author         Rockfire
@preprocessor   uso
@var color banner-color "Couleur des banni√®res" #26619C
==/UserStyle== */
@-moz-document domain("moodle-exia.cesi.fr"),
domain("moodle-ei.cesi.fr"),
domain("moodle-a2p2.cesi.fr") {
     :root {
    --darker: #232323;
    --dark: #262626;
    --lessdark: #323232;
    --border-light: 2px solid #666;
    --text-color: #FFF;
    --banner-color: /*[[banner-color]]*/;
    --logo: url('data:image/png;base64,') no-repeat;
}
* {
    box-shadow: 0px 0px 0px #00000000 !important;
    text-shadow: 0px 0px 0px #00000000 !important;
}


/** Logo **/
h1.logo {
    width: 200px
}
img#logo {
    background: var(--logo);
    display: block;
    box-sizing: border-box;
    width: 200px;
    height: 75px;
    padding-left: 200px;
}


/* Base Elements */
html,
body,
.wrapper {
    background: var(--darker);
    color: var(--text-color);
}
#page-header .top-bar,
.main-nav {
    background-color: var(--banner-color);
    color: var(--text-color);
}
.usermenu .moodle-actionmenu .toggle-display .userbutton .usertext {
    color: var(--text-color);
}
#page-footer {
    border-top: var(--border-light);
}
.breadcrumb-nav a {
    color: var(--text-color);
    font-style: italic;
}
#page .breadcrumb-nav ul.breadcrumb li:last-child a {
    color: var(--text-color);
}
#page #region-main .region-main-inner,
#page #region-main .course-content .section.main,
#page .block-region .block,
#page #region-main .coursebox,
#page #region-main .loginbox {
    background-color: #323232;
    border: 2px solid #3C3C3C;
}
#page #region-main .course-content .section.main {
    border-width: 0px 0px 0px 5px;
    border-color: #CCC
}
#page #region-main .course-content .section.main.current {
    background-color: var(--dark);
    border-left: 5px solid #26619C;
}
#page #region-main .course-content .section.main.current a {
    color: #26619C
}
.box,
.box.generalbox {
    background: #444;
    border: var(--border-light);
}
#page .block-region .block .header .title h2 {
    color: var(--text-color);
    border-top: 2px solid #26619C
}
p,
div {
    color: var(--text-color);
    text-shadow: 0px 0px 0px;
}
.label-info {
    background-color: #661111;
}
#page #region-main .course-content .section.main a,
a {
    color: #26619C;
}


/* EXAMENS */
#page #region-main table {
    background: #000;
}
#page #region-main table td,
#page #region-main table th {
    border: 1px solid #3C3C3C;
}
table.quizreviewsummary th.cell,
table.quizreviewsummary td.cell {
    background: #323232
}
table#explaincaps tbody > tr:nth-child(2n + 1) > td,
table#defineroletable tbody > tr:nth-child(2n + 1) > td,
table.grading-report tbody > tr:nth-child(2n + 1) > td,
table#listdirectories tbody > tr:nth-child(2n + 1) > td,
table.rolecaps tbody > tr:nth-child(2n + 1) > td,
table.userenrolment tbody > tr:nth-child(2n + 1) > td,
table#form tbody > tr:nth-child(2n + 1) > td,
form#movecourses table tbody > tr:nth-child(2n + 1) > td,
#page-admin-course-index .editcourse tbody > tr:nth-child(2n + 1) > td,
.forumheaderlist tbody > tr:nth-child(2n + 1) > td,
table.flexible tbody > tr:nth-child(2n + 1) > td,
.generaltable tbody > tr:nth-child(2n + 1) > td,
table#explaincaps tbody > tr:nth-child(2n + 1) > th,
table#defineroletable tbody > tr:nth-child(2n + 1) > th,
table.grading-report tbody > tr:nth-child(2n + 1) > th,
table#listdirectories tbody > tr:nth-child(2n + 1) > th,
table.rolecaps tbody > tr:nth-child(2n + 1) > th,
table.userenrolment tbody > tr:nth-child(2n + 1) > th,
table#form tbody > tr:nth-child(2n + 1) > th,
form#movecourses table tbody > tr:nth-child(2n + 1) > th,
#page-admin-course-index .editcourse tbody > tr:nth-child(2n + 1) > th,
.forumheaderlist tbody > tr:nth-child(2n + 1) > th,
table.flexible tbody > tr:nth-child(2n + 1) > th,
.generaltable tbody > tr:nth-child(2n + 1) > th {
    background: var(--darker);
}
.user-enroller-panel .uep-search-results .users tbody tr:hover > td,
.user-enroller-panel .uep-search-results .cohorts tbody tr:hover > td,
table.grading-report tbody tr:hover > td,
.forumheaderlist tbody tr:hover > td,
.generaltable tbody tr:hover > td,
table.flexible tbody tr:hover > td,
.category_subcategories tbody tr:hover > td,
table#modules tbody tr:hover > td,
table#permissions tbody tr:hover > td,
.user-enroller-panel .uep-search-results .users tbody tr:hover > th,
.user-enroller-panel .uep-search-results .cohorts tbody tr:hover > th,
table.grading-report tbody tr:hover > th,
.forumheaderlist tbody tr:hover > th,
.generaltable tbody tr:hover > th,
table.flexible tbody tr:hover > th,
.category_subcategories tbody tr:hover > th,
table#modules tbody tr:hover > th,
table#permissions tbody tr:hover > th {
    background: #444
}
.que .formulation,
.que .outcome,
.que .info {
    background-color: var(--dark);
    border-color: #323232;
}
.formulation .incorrect {
    background-color: #442222
}
.formulation .correct {
    background-color: #224422
}
.moodle-dialogue-base .moodle-dialogue-wrap,
table#form td.submit,
.form-buttons,
.path-admin .buttons,
#fitem_id_submitbutton,
.fp-content-center form + div,
div.backup-section + form,
#fgroup_id_buttonar,
.moodle-dialogue-base .moodle-dialogue-wrap .moodle-dialogue-hd,
.moodle-dialogue-base .moodle-dialogue-wrap .moodle-dialogue-hd.yui3-widget-hd {
    background: #323232;
    color: #fff;
    border-color: #000;
}
.form-item .form-description,
.felement.fstatic {
    color: #fff
}



    /*
     * 5th year project pages
    */
#racine > div:first-child {
    background-color: var(--dark);
    border: var(--border-light);
}

#racine h1, #racine h3.obj_ti {
    border: 3px solid #AB0132;
    background-color: #AB0132;
    color: #FFF;
}

#racine .obj {
    border-top: 3px solid #AB0132;
}

#racine p {
    color: #FFF
}

.dureeAppr,
.modSpat,
.modTemp,
.modCollab,
.typeActivite {
    color: #fff;
    background-color: #006484;
    padding: 0;
}


    .activite .modeTitre {
        color: #fff;
        background-color: #26619C;
        display: block
    }

    .modeContenu,
    .modeTitre,
    .infoTitre,
    .infoContenu,
    .typeActivite {
        padding: 4px 8px;
        color: #fff;
    }

    .modeContenu,
    .infoContenu {
        background-color: var(--lessdark);
    }
    
    #header > #title,
    #outCat {
        background-color: var(--dark);
    }

    #header > #title {
        background-image: var(--logo);
    }

    #outSec {
        background-color: var(--darker);
        border: var(--border-light);
    }

    .default.module nav.menu ul li a {
        color: #FFF
    }

    .outCat_sel_yes .outCat_i {
        color: #9cbbff !important;
        border-top: 6px solid #9cbbff;
    }

    #outSec ul li .sel_yes {
        color: #9cbbff;
        border-bottom: 6px solid #9cbbff;
    }

    .default.module section.part:nth-of-type(2n + 1) {
        background-color: var(--lessdark);
    }
    
    .txt_ili > p, .txt_oli > p, article.obj.noCbk {
        color: #FFF
    }
    
    .default.module article.collapsed > a,
    .default.module section.part > .part_ti, 
    .default #header #title h1 {
        color: #9cbbff
    }
    
    .txt_tb th {
        background-color: #111
    }
.op_txt_ul, .dwnLnk, .txt_ul {
    color: #b7ecff;
    text-decoration: underline;
}

.op_txt_ul:before, .dwnLnk:before, .txt_ul:before {
    content: "";
    display: block;
    width: 16px;
    height: 16px;
    float: left;
    margin-right: 6px;
    background:  url('data:image/png;base64,')
}
.dwnLnk:hover::after, .infoAnc:hover::after {
    font-size: 100%;
}
}
