css:
@-moz-document domain(genbeta.com){
/*
     * Name:    Genbeta Dark
     * Author:  Urban_0
     * Version: v1.0.0
     */
/*Parrafos y texto simple/Paragraphs and simple text*/
p{
 color: #bdbcbc;   
}
.aside-box .item-box a, .aside-box-videos .item-box a{
    color: white;
}
.abstract-article, .abstract-longform, .abstract-ecommerce, .abstract-respuestas, .abstract{
color:#000;
background-color:#2a2a2a !important;
}
h1, h2, h3, h4{
    color:#eee;
}
li{
    color:#ff9018;
}
/*Fondos/Background*/
.page-container{
    background-color: #1c1c1c;
    border-color: #303030 !important;
    border-radius: 10px 10px 10px 10px;
-moz-border-radius: 10px 10px 10px 10px;
-webkit-border-radius: 10px 10px 10px 10px;
border: 0px solid #000000;
}
.aside-box, .aside-box-videos{
    background-color:#1c1c1c;
    border-color: #303030 !important;
}
.abstract-article, .abstract-longform, .abstract-ecommerce, .abstract-respuestas, .abstract{
    border-radius: 10px 10px 10px 10px;
-moz-border-radius: 10px 10px 10px 10px;
-webkit-border-radius: 10px 10px 10px 10px;
border: 1px solid #000000;
}
.menu-categories, .foot-menu-categories, .foot{
    background-color:#2a2a2a;
}
/*noticia-fondo/news-background*/
.article-content-inner{
    background-color:#1c1c1c;
}
.aside-title{
    border-color: #bdbcbc;
    color:#eee;
}
.article {
    background: #2f2f2f;
}
/*Titulos/Titles*/
.section-header-recent{
    color: white;
    text-emphasis: bold;
}
.abstract-title a{
 color:white !important;   
}
/*Comentario/Commentary*/
.comment-write-form{
    background-color: #222;
}
.comment-item{
    background-color: #222;
    
} 
.comment-section{
    background-color: #111;
}
.comment-write-input textarea{
    background-color: #282828;
    border-color: black;
}
.comment-write-input textarea:focus{
    background-color:#393939;
}
.comment-author a{
    color: #eee;
}
.blob .desvio .desvio-title, .blob .desvio .desvio-title a{
    color: #eee;
}
}
