css:
/*******************************/
/* Barca skin                  */
/* Date: 28/03/2009            */
/* Made by: rei85              */
/*******************************/



@-moz-document domain("hattrick.org") {

#top
{
	height: 0px !important;
	position: relative !important;
	z-index: 50 !important;
        background: #000000 !important;
        /*background: url(page/top.png) bottom left no-repeat !important;*/
}

#footer a
{
        color: white !important; 
}

#hattrick,#hattrickNoSupporter 
{
        margin:auto!important; 
        position:relative;
        left:-5px;width:993px!important;
}

#ticker
{
	position: absolute !important;
	top: 20px !important;
	left: 360px !important;
}

#ticker div a
{
	padding-bottom: 2px !important;
	color: #ffffff !important;
	text-decoration: none !important;
	line-height: 1.1em !important;
}

#shortcuts
{
	margin-top: -10px !important;
	margin-left:70px !important;
}

#teamLinks
{
        color: #ffffff !important;
        margin-top: 10px !important;
	margin-left: 0px !important;
}

div#teamLinks a
{
        color: #ffffff !important;
}


/*Users online - week - date - time*/

div[id="online"] 
{
        color: white !important; 
        float:right!important;
        margin-right:0pt!important;
        margin-top:20pt!important;
}

div[id="time"]
{
        color: white !important;
        float:right!important;
        margin-right:0pt!important;
        margin-top:20pt!important;
}

/*Page background*/

body
{  
  background: #001e47 !important;
}

/*centre title*/

h1 
{
        color: #013173 !important;
        border-bottom: solid thin darkgrey !important;
        background: #eeeeee !important;
}



/*all boxHead*/

h2 
{
        /*color: white !important;*/
        /*border-bottom: solid thin #eeeeee !important;*/
        /*background: #c70000 !important;*/
}


/*centre news*/

.boxBody h2 

{ 
        color: #013173 !important;
        border-bottom: solid 1px #780500 !important;
}


/*-----------boxHeads-----------*/



/*Left menu*/

.subMenuBox .boxHead
{
	background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/futbolmenu.png) !important;
}


.subMenuBox .boxHead .boxLeft
{
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/futbolmenu.png) !important;      
}



/*Centre box*/

.main .boxHead h2 
{
	background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/futbolmenu.png) !important;  
}


/*Right menu*/

.sidebarBox h2 
{
	background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/futbolmenu.png) !important;
}


/* Press, GB, Team events */

.main .mainBox h2 
{
	border-bottom: solid 1px #780500 !important;
	background-color: #fff !important;
	color: #013173 !important;
	font-size: 14px !important;
	font-weight: bold !important;
	padding: 1px !important;
}

/*Central header paths*/

h2 a 
{
        color: #ffffff !important;
}

h2 a:hover 

{
        color:#013173 !important;
}


/*News and team events date*/

.date
{
	color: #780500 !important;
	padding-right: 8px;
	font-weight: 400;
}


/*Left Menu titles*/

h3 
{
        color: #441a66 !important;
        border-bottom: solid thin #808080 !important;
        background: #eeeeee !important;
}

/*General Links*/

a
{
        color: #013173 !important;
	text-decoration: none !important;
}


/*----subMenu and sidebar links----*/

.subMenuBox a, .subMenuBox a, .sidebarBox a 
{
	text-decoration: none !important;
	color: black !important;
}



/*Bot teams link*/

div a[class="shy"] 
{
        color: gray !important;
}

/*Hide Hattrick logo*/

#logo
{
        background: none !important;
}

#stageLogo {
        background: none !important;
}

/*Hide ticker's stars*/

#ticker div
{
        /*background: none !important;*/
}


/* central page background */

#content
{
        background-image: none !important;
        background: #87baff !important;
}

/*Image header (banner)*/

#header 
{
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/new_barca_banner.jpg) !important;
	height: 115px !important;
	padding-top: 50px !important;
}



/*--------Top menu settings--------*/

#menu
{
	height: 28px !important;
        width: 975px !important;	
        margin-top: 5px !important;
        margin-left: -14px !important;
        bottom: 0px !important;
        border: solid 0px #ffffff !important;
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/futbolmenu.png) !important;
}


#menu a
{
	line-height: 28px !important;  
        color: #ffffff !important;
        /*-moz-border-radius: 2% !important;*/        
        /*background: #808080 !important;*/
}

div#menu a:hover 

{
        color:#FFFFFF !important; background: #013173 !important;
}

a:hover
{
	color: #780500 !important;
}


/*bottom white line*/

div[id="bottom"]
{
        background: #87baff !important;
}


/*---------Forum---------*/

.cfHeader
{
        background:  #780500 !important;
}

#cfPollHeader
{
	background: #780500 !important;
	padding: 5px 10px;
	font-weight: bold;
	color: #ffffff;
}


.cfWrapper
{
	border: solid 1px #013173 !important;
	margin: 0 0 12px 0;
}


.cfHeader a
{
	color: #ffffff !important;
	font-weight: bold;
	text-decoration: none;
}


.cfWrapper a
{
	text-decoration: none;
        /*color: white !important;*/
}

.cfWrapper div.signature a, div.signature-trunc a, div.signature-expand a {
    color: #999999 !important;
    text-decoration: underline !important;
}

.cfUserInfo a
{
        /*color: #de4747 !important;*/
}

.cfMessage a
{
	/*color: #de4747 !important;*/
}

.folderHeader a:link 
{
	color: #013173 !important;
	font-weight: bold !important;
}

.cfFooter a
{ 
        color: black !important;
}


.folderItem div.ignore
{
        cursor: pointer;
        width: 10px;
        height: 10px;
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/cross_small.png) no-repeat !important;
}


.folderItem div.unignore
{
        cursor: pointer;
        width: 10px;
        height: 10px;
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/tick_small.png) !important;
}


.folderItem div.unignore:hover
{
        background: url(http://i253.photobucket.com/albums/hh75/reiben85/tickGreen_small.png) !important;
}


/*Ads section*/

div[class="insertHeader"]
{
        background: #780500 !important;
}


div[class="insertFooter"]
{
        background: #ffffff !important;
}

/*shortcuts*/

img.scNewMail 
{
	background: transparent url(http://i253.photobucket.com/albums/hh75/reiben85/hattrickelements/Newmail.png) no-repeat !important;
}

/* MatchLineup */

.name a
{
	color: #277f31 !important;
}


/*Foxtrick pop-up menu*/
span.myht1:hover div.myht2 {
display: inline;
position: absolute;
left: 20px;
background-color: #fff !important;
border: solid 1px #780500 !important;
padding: 0px;
z-index: 999
}

div.playerInfo {
overflow: visible !important;
}


span.myht1 table>tr>td>a {
font-weight: normal !important;
text-decoration: underline !important;
color: #780500 !important;
}

span.myht1 table>tr>td:hover {
background-color:#ccc !important;
}

div[class*="ad_"]
{
        display:none !important;
}

}
